import {
  forwardRef,
  Fragment,
  FunctionComponent,
  PropsWithoutRef,
  Ref,
  useEffect,
  useRef,
  useState,
  MouseEvent
} from 'react';
import styled, { css, DefaultTheme } from 'styled-components';
import { rgba } from 'polished';

import { useElement, useConsolidatedRef, useDirection } from '../../hooks';
import { defaultThemeProp } from '../../theme';
import { BaseProps, ForwardProps, NoChildrenProp } from '../../types';
import { debounce, tryCatch } from '../../utils';
import Button, { StyledButton } from '../Button';
import Flex from '../Flex';
import Link, { StyledLink } from '../Link';
import Icon, { StyledIcon, registerIcon } from '../Icon';
import * as caretLeftIcon from '../Icon/icons/caret-left.icon';
import * as caretRightIcon from '../Icon/icons/caret-right.icon';
import MenuButton from '../MenuButton';
import Text, { StyledText } from '../Text';
import { MenuItemProps } from '../Menu';

registerIcon(caretLeftIcon, caretRightIcon);

export interface BreadcrumbsProps extends BaseProps, NoChildrenProp {
  /** A set of actions representing the chronological hierarchy of pages or locations leading to the current view. */
  path: MenuItemProps[];
  /** Ref forwarded to the wrapping element. */
  ref?: Ref<HTMLDivElement>;
}

const StyledSeparator: typeof Icon = styled(Icon)(({ theme }) => {
  const color = tryCatch(() =>
    rgba(theme.base.palette['foreground-color'], theme.base.transparency['transparent-2'])
  );

  return css`
    color: ${color};
  `;
});

export const StyledBreadcrumbs = styled.div<{ oneItemRemaining: boolean }>(
  ({ theme, oneItemRemaining }) => css`
    white-space: nowrap;
    overflow: visible;
    visibility: hidden;

    ${StyledIcon} {
      width: 1em;
      height: 1em;
      flex-shrink: 0;
    }

    ${StyledLink},
    ${StyledButton},
    ${StyledText} {
      max-width: ${theme.base['content-width'].md};
    }

    ${oneItemRemaining &&
    css`
      & > :last-child {
        text-overflow: ellipsis;
        overflow: hidden;
      }
    `}
  `
);

StyledBreadcrumbs.defaultProps = defaultThemeProp;

const getEllipsisStyles = ({ theme }: { theme: DefaultTheme }) => {
  return css`
    min-width: ${theme.base['content-width'].xs};
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  `;
};

const StyledEllipsizedLink = styled(Link)(getEllipsisStyles);

StyledEllipsizedLink.defaultProps = defaultThemeProp;

const StyledEllipsizedButton = styled(Button)(getEllipsisStyles);

StyledEllipsizedButton.defaultProps = defaultThemeProp;

const StyledEllipsizedText = styled(Text)(getEllipsisStyles);

StyledEllipsizedText.defaultProps = defaultThemeProp;

const Breadcrumbs: FunctionComponent<BreadcrumbsProps & ForwardProps> = forwardRef(
  ({ path, ...restProps }: PropsWithoutRef<BreadcrumbsProps>, ref: BreadcrumbsProps['ref']) => {
    const [breadcrumbsEl, setBreadcrumbsEl] = useElement<HTMLDivElement>();
    const [numMenuItems, setNumMenuItems] = useState(0);
    const minInlineWidth = useRef(0);

    const breadcrumbRef = useConsolidatedRef(setBreadcrumbsEl, ref);

    const debouncedResize = debounce((entries: ResizeObserverEntry[]) => {
      if (entries[0].contentRect.width > minInlineWidth.current) {
        setNumMenuItems(curr => {
          if (curr === 0) return curr;
          return curr - 1;
        });
      }
    }, 100);

    const resizeObserver = new ResizeObserver(debouncedResize);

    const intersectionObserver = new IntersectionObserver(
      entries => {
        if (entries[0].intersectionRatio < 1 && numMenuItems < path.length - 1) {
          if (entries[0].rootBounds) minInlineWidth.current = entries[0].rootBounds.width + 1;
          setNumMenuItems(curr => {
            if (curr === path.length - 1) return curr;
            return curr + 1;
          });
        } else if (breadcrumbsEl) breadcrumbsEl.style.visibility = 'visible';
      },
      { root: breadcrumbsEl, threshold: 1 }
    );

    useEffect(() => {
      if (breadcrumbsEl && breadcrumbsEl.lastChild) {
        intersectionObserver.observe(breadcrumbsEl.lastChild as Element);
        resizeObserver.observe(breadcrumbsEl);

        return () => {
          intersectionObserver.disconnect();
          resizeObserver.disconnect();
        };
      }
    }, [breadcrumbsEl, numMenuItems]);

    const { end } = useDirection();

    return (
      <Flex
        {...restProps}
        container={{ gap: 0.5, alignItems: 'center' }}
        as={StyledBreadcrumbs}
        oneItemRemaining={numMenuItems === path.length - 1}
        ref={breadcrumbRef}
      >
        {numMenuItems > 0 && (
          <>
            <MenuButton
              text='Links'
              iconOnly
              variant='link'
              icon='folder-hierarchy-solid'
              menu={{ items: path.slice(0, numMenuItems).reverse() }}
            />
            <StyledSeparator name={`caret-${end}`} />
          </>
        )}
        {path.slice(numMenuItems).map((crumb, index) => {
          const { id, primary, visual, href, onClick, ...restCrumbProps } = crumb;
          let Comp;
          if (href)
            Comp = (
              <Link
                key={id}
                href={href}
                as={primary.length > 60 ? StyledEllipsizedLink : undefined}
                onClick={(e: MouseEvent<HTMLAnchorElement>) => {
                  onClick?.(id, e);
                }}
                {...restCrumbProps}
              >
                {visual} {primary}
              </Link>
            );
          else if (onClick)
            Comp = (
              <Button
                key={id}
                variant='link'
                as={primary.length > 60 ? StyledEllipsizedButton : undefined}
                onClick={(e: MouseEvent<HTMLButtonElement>) => {
                  onClick(id, e);
                }}
                {...restCrumbProps}
              >
                {visual} {primary}
              </Button>
            );
          else
            Comp = (
              <Text
                key={id}
                as={primary.length > 60 ? StyledEllipsizedText : undefined}
                {...restCrumbProps}
              >
                {visual} {primary}
              </Text>
            );

          if (index === path.slice(numMenuItems).length - 1) return Comp;

          return (
            <Fragment key={id}>
              {Comp}
              <StyledSeparator name={`caret-${end}`} />
            </Fragment>
          );
        })}
      </Flex>
    );
  }
);

export default Breadcrumbs;
