import { forwardRef, useState, FunctionComponent, PropsWithoutRef, Ref, ReactNode } from 'react';
import styled, { css } from 'styled-components';

import Text from '../Text';
import Button from '../Button';
import ExpandCollapse from '../ExpandCollapse';
import Flex from '../Flex';
import Grid from '../Grid';
import HTML from '../HTML';
import Icon, { StyledIcon, registerIcon } from '../Icon';
import * as caretRightIcon from '../Icon/icons/caret-right.icon';
import Actions, { ActionsProps } from '../Actions';
import { defaultThemeProp } from '../../theme';
import { BaseProps, ForwardProps } from '../../types';
import { useDirection, useI18n, useUID } from '../../hooks';
import { calculateFontSize, FontSize, omitProps } from '../../styles';
import AdditionalInfo, { AdditionalInfoProps } from '../AdditionalInfo';

registerIcon(caretRightIcon);

export interface FieldGroupProps extends BaseProps {
  /** The content of the Field Group. */
  children: ReactNode;
  /** A description of the field group to be displayed above the controls. */
  description?: string;
  /**
   * If true, the Field Group will be able to collapse the content of its children by clicking on the name.
   * @default false
   */
  collapsible?: boolean;
  /**
   * If true, the Field Group will be collapsed on the first render.
   * @default false
   */
  defaultCollapsed?: boolean;
  /** Select the heading tag for header */
  headingTag?: 'h1' | 'h2' | 'h3' | 'h4' | 'h5' | 'h6';
  /** The name associated with the Field Group. */
  name?: string;
  /** Provides additional info relevant to the field group. */
  additionalInfo?: {
    heading: AdditionalInfoProps['heading'];
    content: AdditionalInfoProps['children'];
  };
  /** An array of actions to add to the header. */
  actions?: ActionsProps['items'];
  /** A reference to the wrapping element. */
  ref?: Ref<HTMLFieldSetElement>;
}

interface FieldGroupLegendProps {
  open: boolean;
  actions?: ActionsProps['items'];
  children?: ReactNode;
  additionalInfo?: FieldGroupProps['additionalInfo'];
}

const StyledExpandButton = styled(Button)(
  ({
    theme: {
      base: { animation }
    }
  }) => {
    const { rtl } = useDirection();
    return css`
      ${StyledIcon} {
        transition: transform ${animation.speed} ${animation.timing.ease};
      }
      &[aria-expanded='true'] ${StyledIcon} {
        transform: rotate(90deg);
      }
      &[aria-expanded='false'] ${StyledIcon} {
        transform: rotate(${rtl ? 180 : 0}deg);
      }
    `;
  }
);

StyledExpandButton.defaultProps = defaultThemeProp;

export const StyledFieldGroupName = styled.span``;

export const StyledFieldGroupLegend = styled.legend.withConfig<{ open: boolean }>(
  omitProps('open')
)(
  ({
    open,
    theme: {
      base: {
        spacing,
        animation,
        'hit-area': { 'mouse-min': hitAreaMouse, 'finger-min': hitAreaFinger }
      }
    }
  }) => {
    return css`
      width: 100%;
      margin-bottom: 0;
      ${open &&
      css`
        margin-block-end: calc(1.5 * ${spacing});
      `}
      transition: margin-block-end ${animation.speed} ${animation.timing.ease};

      > ${StyledExpandButton} {
        min-height: ${hitAreaMouse};

        @media (pointer: 'coarse') {
          min-height: ${hitAreaFinger};
        }
      }
    `;
  }
);

StyledFieldGroupLegend.defaultProps = defaultThemeProp;

export const StyledFieldGroup = styled.fieldset(
  ({
    theme: {
      base: { 'font-size': fontSize, 'font-scale': fontScale },
      components: { text }
    }
  }) => {
    const fontSizes = calculateFontSize(fontSize, fontScale);

    return css`
      border: none;

      & ${StyledFieldGroupName} {
        font-size: ${fontSizes[text.h3['font-size'] as FontSize]};
        font-weight: ${text.h3['font-weight']};
      }

      & & ${StyledFieldGroupName} {
        font-size: ${fontSizes[text.h4['font-size'] as FontSize]};
        font-weight: ${text.h4['font-weight']};
      }

      & & & ${StyledFieldGroupName} {
        font-size: ${fontSizes[text.h5['font-size'] as FontSize]};
        font-weight: ${text.h5['font-weight']};
      }

      & & & & ${StyledFieldGroupName} {
        font-size: ${fontSizes[text.h6['font-size'] as FontSize]};
        font-weight: ${text.h6['font-weight']};
      }
    `;
  }
);

StyledFieldGroup.defaultProps = defaultThemeProp;

const FieldGroupLegend: FunctionComponent<FieldGroupLegendProps> = ({
  children,
  open,
  actions,
  additionalInfo,
  ...restProps
}) => {
  return (
    <StyledFieldGroupLegend open={open} {...restProps}>
      <Flex container={{ alignItems: 'center', justify: 'between', gap: 0.5 }}>
        <Flex container={{ alignItems: 'center', gap: 0.5 }}>
          {children}
          {additionalInfo && (
            <AdditionalInfo heading={additionalInfo.heading}>
              {additionalInfo.content}
            </AdditionalInfo>
          )}
        </Flex>
        {actions && <Actions items={actions} />}
      </Flex>
    </StyledFieldGroupLegend>
  );
};

const FieldGroup: FunctionComponent<FieldGroupProps & ForwardProps> = forwardRef(
  (
    {
      children,
      description,
      name,
      additionalInfo,
      actions,
      collapsible = false,
      defaultCollapsed = false,
      headingTag,
      ...restProps
    }: PropsWithoutRef<FieldGroupProps>,
    ref: FieldGroupProps['ref']
  ) => {
    const [open, setOpen] = useState(!defaultCollapsed);
    const t = useI18n();
    const uid = useUID();

    const descAndChildren = (
      <Grid container={{ cols: 'minmax(0, 1fr)', gap: 3 }}>
        {description && <HTML id={`${uid}-description`} as='p' content={description} />}
        <div>{children}</div>
      </Grid>
    );

    return (
      <StyledFieldGroup
        aria-label={name}
        aria-describedby={description ? `${uid}-description` : undefined}
        {...restProps}
        ref={ref}
      >
        {name && (
          <FieldGroupLegend open={open} actions={actions} additionalInfo={additionalInfo}>
            {collapsible ? (
              <StyledExpandButton
                type='button'
                variant='text'
                onClick={() => {
                  setOpen(cur => !cur);
                }}
                aria-expanded={open ? 'true' : 'false'}
                aria-label={t(open ? 'collapse_noun' : 'expand_noun', [name])}
              >
                <Flex
                  container={{ direction: 'row', alignItems: 'center', gap: 0.5 }}
                  forwardedAs='span'
                >
                  <Icon name='caret-right' />
                  {headingTag ? (
                    <Text variant={headingTag}>{name}</Text>
                  ) : (
                    <StyledFieldGroupName>{name}</StyledFieldGroupName>
                  )}
                </Flex>
              </StyledExpandButton>
            ) : (
              <>
                {headingTag ? (
                  <Text variant={headingTag}>{name}</Text>
                ) : (
                  <StyledFieldGroupName>{name}</StyledFieldGroupName>
                )}
              </>
            )}
          </FieldGroupLegend>
        )}
        {collapsible ? (
          <ExpandCollapse collapsed={!open}>{descAndChildren}</ExpandCollapse>
        ) : (
          descAndChildren
        )}
      </StyledFieldGroup>
    );
  }
);

export default FieldGroup;
