import {
  useMemo,
  FunctionComponent,
  forwardRef,
  PropsWithoutRef,
  Ref,
  useEffect,
  useRef
} from 'react';
import styled from 'styled-components';

import { defaultThemeProp } from '../../../theme';
import { ForwardProps } from '../../../types';
import { range } from '../../../utils';
import { useConfiguration, useConsolidatedRef, useFocusWithin } from '../../../hooks';
import FormField from '../../FormField';
import Select, { Option } from '../../Select';
import { StyledSelect } from '../../Select/Select';
import Flex from '../../Flex';
import { FormControlProps, StyledFormControl } from '../../FormControl';
import DateTimeProps, { DateTimeCallbackParameter } from '../DateTime.types';

import { parseToDate, getLocalizedMonths } from './utils';
import StyledDateTime from './DateTime.styles';
import { DateParts } from './DateTimeInput.types';

export interface MonthInputProps extends DateTimeProps {
  /** Pass a heading and content to show additional information on the field. */
  additionalInfo?: FormControlProps['additionalInfo'];
}

export const StyledMonthInput = styled(StyledDateTime)<MonthInputProps>`
  padding: 0;
  border: 0;

  ${StyledSelect} {
    min-width: 0;

    &:focus {
      z-index: 1;
    }

    &:first-child {
      border-start-end-radius: 0;
      border-end-end-radius: 0;
    }

    &:last-child {
      max-width: max-content;
      margin-inline-start: -0.0625rem;
      border-start-start-radius: 0;
      border-end-start-radius: 0;
    }
  }
`;

StyledMonthInput.defaultProps = defaultThemeProp;

const convertToCallbackParameter = (
  date: Date,
  { month, year }: DateParts<string | undefined>
): DateTimeCallbackParameter => {
  if ([month, year].every(Boolean)) {
    const resultDate = new Date(date);
    resultDate.setUTCFullYear(Number(year), Number(month));
    return {
      valueAsISOString: resultDate.toISOString(),
      valueAsTimestamp: resultDate.getTime()
    };
  }
  if ([year, month].every(p => !p)) {
    return {
      valueAsISOString: undefined,
      valueAsTimestamp: undefined
    };
  }
  return {
    valueAsISOString: '',
    valueAsTimestamp: NaN,
    state: 'incomplete'
  };
};

const MonthInput: FunctionComponent<MonthInputProps & ForwardProps> = forwardRef(
  (props: PropsWithoutRef<MonthInputProps>, ref: Ref<HTMLFieldSetElement>) => {
    const { locale } = useConfiguration();
    const {
      value,
      min,
      max,
      id,
      label,
      labelHidden,
      info,
      status,
      required,
      readOnly,
      disabled,
      onChange,
      onFocus,
      onBlur,
      autoFocus,
      additionalInfo,
      ...restProps
    } = props;

    const date = value ? parseToDate(value) : undefined;

    const dateToGetFullYearFrom = date ?? new Date();

    const minDate = parseToDate(
      min ??
        new Date(dateToGetFullYearFrom).setUTCFullYear(dateToGetFullYearFrom.getUTCFullYear() - 10)
    );
    const maxDate = parseToDate(
      max ??
        new Date(dateToGetFullYearFrom).setUTCFullYear(dateToGetFullYearFrom.getUTCFullYear() + 10)
    );

    minDate.setUTCMinutes(minDate.getUTCMinutes() - minDate.getTimezoneOffset());
    maxDate.setUTCMinutes(maxDate.getUTCMinutes() - maxDate.getTimezoneOffset());

    const minYear = minDate.getUTCFullYear();
    const maxYear = maxDate.getUTCFullYear();

    const years = range(minYear, maxYear);

    const months = useMemo(() => getLocalizedMonths(locale), [locale]);

    const monthSelectRef = useRef<HTMLSelectElement>(null);
    const yearSelectRef = useRef<HTMLSelectElement>(null);

    useEffect(() => {
      if (monthSelectRef.current && yearSelectRef.current) {
        monthSelectRef.current.value = date?.getUTCMonth().toString() ?? '';
        yearSelectRef.current.value = date?.getUTCFullYear().toString() ?? '';
      }
    }, [date?.toDateString()]);

    const pickParts = (): DateParts<string | undefined> => {
      return {
        month: monthSelectRef.current?.value || '',
        year: yearSelectRef.current?.value || ''
      };
    };

    const onFocusChange = (focused: boolean) => {
      const callbackParam = convertToCallbackParameter(
        date ? new Date(date) : new Date(),
        pickParts()
      );
      if (onFocus && focused) onFocus(callbackParam);
      else if (onBlur && !focused) onBlur(callbackParam);
    };

    const onSelectChange = () => {
      onChange?.(convertToCallbackParameter(date ? new Date(date) : new Date(), pickParts()));
    };

    const containerRef = useConsolidatedRef<HTMLElement>(ref);
    useFocusWithin([containerRef], onFocusChange);

    const isMonthOptionDisabled = (monthIndex: number) => {
      const year =
        yearSelectRef.current && yearSelectRef.current.value
          ? parseInt(yearSelectRef.current.value, 10)
          : new Date().getUTCFullYear();

      return (
        (year === minDate.getUTCFullYear() && monthIndex < minDate.getUTCMonth()) ||
        (year === maxDate.getUTCFullYear() && monthIndex > maxDate.getUTCMonth())
      );
    };

    const monthOptions = months.map((month, index) => (
      <Option disabled={isMonthOptionDisabled(index)} key={month} value={index.toString()}>
        {months[index]}
      </Option>
    ));
    const yearOptions = years.map(year => (
      <Option key={year.toString()} value={year.toString()}>
        {year.toString()}
      </Option>
    ));

    const displayNames = new Intl.DisplayNames(locale, { style: 'long', type: 'dateTimeField' });

    const Month = (
      <Select
        key='month'
        ref={monthSelectRef}
        aria-label={displayNames.of('month')}
        readOnly={readOnly}
        required={required}
        onChange={onSelectChange}
        status={status}
        disabled={disabled}
        autoFocus={autoFocus}
      >
        {!required && <Option key='null'> </Option>}
        {monthOptions}
      </Select>
    );

    const Year = (
      <Select
        key='year'
        ref={yearSelectRef}
        aria-label={displayNames.of('year')}
        readOnly={readOnly}
        required={required}
        onChange={onSelectChange}
        status={status}
        disabled={disabled}
      >
        {!required && <Option key='null'> </Option>}
        {yearOptions}
      </Select>
    );

    const Comp = (
      <Flex
        as={StyledMonthInput}
        forwardedAs={StyledFormControl}
        container={{ alignItems: 'center', wrap: 'nowrap' }}
        ref={containerRef}
        status={status}
        disabled={disabled}
        readOnly={readOnly}
        {...restProps}
      >
        {Month}
        {Year}
      </Flex>
    );

    return label ? (
      <FormField
        as='fieldset'
        labelAs='legend'
        {...{ label, labelHidden, id, info, status, required, disabled, additionalInfo }}
      >
        {Comp}
      </FormField>
    ) : (
      Comp
    );
  }
);

export default MonthInput;
