import {
  FunctionComponent,
  PropsWithoutRef,
  useEffect,
  useRef,
  Ref,
  useState,
  forwardRef,
  MouseEvent,
  SyntheticEvent
} from 'react';

import { ForwardProps } from '../../types';
import { cap } from '../../utils';
import Flex from '../Flex';
import MetaList from '../MetaList';
import Icon from '../Icon';
import Text from '../Text';
import {
  useConsolidatedRef,
  useDirection,
  useEvent,
  useFocusTrap,
  useI18n,
  useScrollToggle
} from '../../hooks';
import ErrorState from '../ErrorState';

import {
  StyledHeader,
  StyledCountTracker,
  StyledInfo,
  StyledActionButton,
  StyledPreviewRegion,
  StyledNavButton,
  StyledImageContainer,
  StyledImage,
  StyledLiveRegion,
  StyledLightbox
} from './Lightbox.styles';
import { LightboxProps } from './Lightbox.types';

const Lightbox: FunctionComponent<LightboxProps & ForwardProps> = forwardRef(
  (
    {
      items,
      defaultIndex = 0,
      cycle,
      onItemLoad,
      onItemError,
      onNavigate,
      onItemDownload,
      onAfterClose,
      ...restProps
    }: PropsWithoutRef<LightboxProps>,
    refArg: Ref<HTMLDivElement>
  ) => {
    const t = useI18n();
    const { start, end } = useDirection();
    const [currentIndex, setCurrentIndex] = useState(defaultIndex);
    const currentItem = items[currentIndex];
    const { disableScroll, enableScroll } = useScrollToggle();
    const lightboxRef = useConsolidatedRef(refArg);
    const headerRef = useRef<HTMLDivElement>(null);
    const closeButtonRef = useRef<HTMLButtonElement>(null);
    const prevButtonRef = useRef<HTMLButtonElement>(null);
    const nextButtonRef = useRef<HTMLButtonElement>(null);
    const [open, setOpen] = useState(true);
    const imageRef = useConsolidatedRef(currentItem.ref);
    const [error, setError] = useState(false);
    const [liveText, setLiveText] = useState('');
    const countTrackerText = items.length > 1 ? t('x_of_y', [currentIndex + 1, items.length]) : '';

    const close = () => {
      setOpen(false);
    };

    const navigate = (direction: 1 | -1) => {
      if (direction === 1) {
        if (currentIndex < items.length - 1) {
          setCurrentIndex(cur => cur + 1);
          setError(false);
        } else if (cycle) {
          setCurrentIndex(0);
          setError(false);
        }
      } else if (currentIndex > 0) {
        setCurrentIndex(cur => cur - 1);
        setError(false);
      } else if (cycle) {
        setCurrentIndex(items.length - 1);
        setError(false);
      }
    };
    const keyDownHandler = (e: KeyboardEvent) => {
      if (e.repeat) return;

      if (e.key === 'Escape') {
        close();
      } else if (e.key === `Arrow${cap(end)}`) {
        navigate(1);
      } else if (e.key === `Arrow${cap(start)}`) {
        navigate(-1);
      }
    };

    useEvent('keydown', keyDownHandler);
    useFocusTrap(lightboxRef);

    useEffect(() => {
      setLiveText(
        `${currentItem.name}. ${countTrackerText ? `${countTrackerText}.` : ''} ${
          currentItem.description
        }.`
      );
    }, [currentItem.name, countTrackerText, currentItem.description]);

    useEffect(() => {
      lightboxRef.current?.focus();
    }, []);

    return (
      <StyledLightbox
        {...restProps}
        tabIndex={-1}
        ref={lightboxRef}
        open={open}
        role='dialog'
        aria-modal='true'
        container={{ alignItems: 'start', justify: 'start', direction: 'column' }}
        alpha={0.9}
        variant='dark'
        transitionSpeed='slow'
        onBeforeTransitionIn={disableScroll}
        onAfterTransitionOut={() => {
          enableScroll();
          onAfterClose?.();
        }}
      >
        <Flex
          as={StyledHeader}
          ref={headerRef}
          container={{
            justify: 'between',
            alignItems: 'center',
            gap: 2
          }}
          onMouseDown={(e: MouseEvent<HTMLDivElement>) => {
            if (e.button !== 0) return;
            if (e.target === e.currentTarget) close();
          }}
        >
          <StyledCountTracker aria-hidden='true'>{countTrackerText}</StyledCountTracker>

          <Flex
            as={StyledInfo}
            container={{
              wrap: 'wrap',
              alignItems: 'center',
              justify: 'center',
              colGap: 2,
              rowGap: 0.5
            }}
          >
            <Text variant='primary' as='h2'>
              {currentItem.name}
            </Text>
            {currentItem.metadata && <MetaList items={currentItem.metadata} />}
          </Flex>

          <Flex container>
            {onItemDownload && (
              <StyledActionButton
                variant='simple'
                aria-label={t('download')}
                icon
                onClick={() => onItemDownload(currentItem.id)}
              >
                <Icon name='download' />
              </StyledActionButton>
            )}
            <StyledActionButton
              variant='simple'
              ref={closeButtonRef}
              aria-label={t('close')}
              icon
              onClick={close}
            >
              <Icon name='times' />
            </StyledActionButton>
          </Flex>
        </Flex>

        <Flex
          container={{ justify: 'between' }}
          as={StyledPreviewRegion}
          item={{ grow: 1, shrink: 0 }}
        >
          {items.length > 1 && (
            <StyledNavButton
              ref={prevButtonRef}
              variant='simple'
              aria-label={t('pagination_prev')}
              hidden={!cycle && currentIndex === 0}
              icon
              onClick={() => {
                navigate(-1);
              }}
            >
              <span>
                <Icon name='caret-left' />
              </span>
            </StyledNavButton>
          )}

          <Flex item={{ grow: 1 }} as={StyledImageContainer}>
            <Flex
              container={{ alignItems: 'center' }}
              onMouseDown={(e: MouseEvent<HTMLDivElement>) => {
                if (e.button !== 0) return;
                if (e.target === e.currentTarget) close();
              }}
            >
              {error ? (
                <ErrorState message={t('image_load_error')} />
              ) : (
                <StyledImage
                  ref={imageRef}
                  src={currentItem.src}
                  alt={currentItem.description}
                  onLoad={(e: SyntheticEvent<HTMLImageElement>) => {
                    onItemLoad?.(currentItem.id, e);
                  }}
                  onError={(e: SyntheticEvent<HTMLImageElement>) => {
                    onItemError?.(currentItem.id, e);
                    setError(true);
                  }}
                />
              )}
            </Flex>
          </Flex>

          {items.length > 1 && (
            <StyledNavButton
              ref={nextButtonRef}
              variant='simple'
              aria-label={t('pagination_next')}
              hidden={!cycle && currentIndex === items.length - 1}
              icon
              onClick={() => {
                navigate(1);
              }}
            >
              <span>
                <Icon name='caret-right' />
              </span>
            </StyledNavButton>
          )}
        </Flex>
        <StyledLiveRegion aria-live='polite' aria-label={liveText} />
      </StyledLightbox>
    );
  }
);

export default Lightbox;
