export const toLinearGradient = (
  gradient: LinearGradient,
  { direction = '180deg' }: { direction?: string } = {}
): string => {
  if (gradient.length === 0) return 'transparent';
  if (gradient.length === 1) return gradient[0].color;

  return `linear-gradient(${direction}, ${gradient
    .map(({ color, position }) => `${color} ${position * 100}%`)
    .join(', ')})`;
};
