import { forwardRef, FunctionComponent, PropsWithoutRef, Ref, useContext } from 'react';

import Text from '../Text';
import Link from '../Link';
import { BaseProps, ForwardProps } from '../../types';

import LocationView from './LocationView';
import MapsContext from './MapsContext';
import { LatLng } from './types';

export interface LocationDisplayProps extends BaseProps {
  /** Location address or coordinates */
  value?: string | LatLng;
  /** The text to be displayed. */
  displayText?: string;
  /**
   * Controls styling of the field.
   * @default 'text-map'
   */
  variant?: 'text-map' | 'text' | 'map';
  /**
   * Height of the map container.
   * @default '25rem'
   */
  height?: string;
  /**
   * How much the map should zoom.
   * @default 13
   */
  zoomLevel?: number;
  /** Ref for the wrapping element. */
  ref?: Ref<HTMLAnchorElement | HTMLSpanElement>;
}

const LocationDisplay: FunctionComponent<LocationDisplayProps & ForwardProps> = forwardRef(
  (
    {
      value = '',
      displayText,
      variant = 'text-map',
      height = '25rem',
      zoomLevel = 13,
      ...restProps
    }: PropsWithoutRef<LocationDisplayProps>,
    ref: LocationDisplayProps['ref']
  ) => {
    const { name } = useContext(MapsContext);

    const valueString =
      typeof value === 'string' ? value : `${value?.latitude}, ${value?.longitude}`;

    return !value ? (
      <Text ref={ref as Ref<HTMLSpanElement>} {...restProps} aria-hidden='true'>
        <span aria-hidden='true'>&ndash;&ndash;</span>
      </Text>
    ) : (
      <>
        {(variant === 'text' || variant === 'text-map') &&
          (name === 'google' ? (
            <Link
              ref={ref as Ref<HTMLAnchorElement>}
              {...restProps}
              target='_blank'
              rel='noopener noreferrer'
              href={`https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(
                typeof value === 'string' ? value : `${value?.latitude}, ${value?.longitude}`
              )}`}
            >
              {displayText || valueString}
            </Link>
          ) : (
            <Text ref={ref as Ref<HTMLSpanElement>} {...restProps}>
              {displayText || valueString}
            </Text>
          ))}
        {(variant === 'text-map' || variant === 'map') && (
          <LocationView
            {...{
              location: value,
              centerMapOnChange: true,
              height,
              zoomLevel
            }}
          />
        )}
      </>
    );
  }
);

export default LocationDisplay;
