import { FC, forwardRef, Ref, PropsWithoutRef, MouseEventHandler } from 'react';
import styled, { css } from 'styled-components';
import { mix } from 'polished';

import { defaultThemeProp } from '../../theme';
import { tryCatch } from '../../utils';
import { BaseProps, ForwardProps } from '../../types';
import BareButton from '../Button/BareButton';

export interface TagProps extends BaseProps {
  /** The content of the Badge. */
  children: string;
  /** A location to navigate to. Passing an href will render an anchor styled as a Tag. */
  href?: string;
  /** Click handler for the badge. Passing an onClick function will render a button styled as a Tag. */
  onClick?: MouseEventHandler<HTMLAnchorElement | HTMLButtonElement>;
  /** Ref forwarded to the wrapping element. */
  ref?: Ref<HTMLSpanElement | HTMLAnchorElement | HTMLButtonElement>;
}

export const StyledTag = styled.span(props => {
  const borderRadius = props.theme.base['border-radius'];
  const { spacing } = props.theme.base;
  const { foreground, background } = props.theme.components.badges.tag;

  const borderColor = tryCatch(() =>
    mix(0.85, props.theme.base.palette['primary-background'], background)
  );
  const boxShadow = props.theme.base.shadow.focus;

  return css`
    border: 0.0625rem solid ${borderColor};
    border-radius: calc(0.25 * ${borderRadius});
    color: ${foreground};
    display: inline-block;
    font-size: 0.75rem;
    padding: 0 ${spacing};

    &:hover {
      border-color: ${background};
    }

    &:focus {
      border-color: ${background};
      box-shadow: ${boxShadow};
      outline: none;
    }

    &:active {
      border: 0.0625rem solid ${borderColor};
      box-shadow: none;
    }
  `;
});

StyledTag.defaultProps = defaultThemeProp;

const Tag: FC<TagProps & ForwardProps> = forwardRef(
  ({ children, href, onClick, ...restProps }: PropsWithoutRef<TagProps>, ref: TagProps['ref']) => {
    return (
      <StyledTag
        {...restProps}
        as={onClick || href ? BareButton : undefined}
        href={href}
        onClick={onClick}
        ref={ref}
      >
        {children}
      </StyledTag>
    );
  }
);

export default Tag;
