import { forwardRef, PropsWithoutRef, Ref, ComponentType, ReactNode } from 'react';
import styled from 'styled-components';

import { BaseProps, NoChildrenProp, ForwardProps } from '../../types';

export type OldTreeNode<T extends object = Record<string, unknown>> = {
  /** The id of the Node. Used as the key for the li element wrapping the Node. */
  id: string;
  /** Child nodes. */
  nodes?: OldTreeNode<T>[];
  /** Props to apply to the li. */
  listItemProps?: ForwardProps;

  [key: string]: unknown;
} & T;

export type TreeNode = {
  /** The id of the Node. Used as the key for the li element wrapping the Node. */
  id: string;
  /** Child nodes. */
  nodes?: TreeNode[];
  /** Props to apply to the li. */
  listItemProps?: ForwardProps;

  [key: string]: unknown;
};

export interface TreeProps<T extends TreeNode = TreeNode> extends BaseProps, NoChildrenProp {
  /** An array of ParentNodes and Nodes that compose the layout of the Tree. */
  nodes: T[];
  /** A function that allows custom styling and behavior for all Nodes. */
  nodeRenderer?: ComponentType<
    {
      depth: number;
      subTree?: ReactNode;
      hasParentSibling: TreeItemProps['hasParentSibling'];
    } & Exclude<T, 'listItemProps'>
  >;
  /** Ref for the wrapping element. */
  ref?: Ref<HTMLUListElement>;
}

export const StyledTreeListItem = styled.li``;

export const StyledTreeList = styled.ul`
  &,
  & & {
    list-style: none;
    position: relative;
  }

  ${StyledTreeListItem} {
    position: relative;
  }
`;

const DefaultNodeRenderer: TreeProps['nodeRenderer'] = ({ data, subTree }) => {
  return (
    <>
      {data}
      {subTree}
    </>
  );
};

interface TreeItemProps extends TreeNode {
  nodeRenderer: NonNullable<TreeProps['nodeRenderer']>;
  depth: number;
  hasParentSibling: boolean;
}

interface TreeListProps extends Pick<TreeProps, 'nodes' | 'ref'> {
  nodeRenderer: NonNullable<TreeProps['nodeRenderer']>;
  depth: number;
}

let TreeList: ComponentType<TreeListProps>;

const TreeItem = ({
  listItemProps,
  depth,
  nodes,
  nodeRenderer: NodeRenderer,
  ...restProps
}: TreeItemProps) => {
  return (
    <StyledTreeListItem {...listItemProps}>
      <NodeRenderer
        {...restProps}
        depth={depth}
        nodes={nodes}
        subTree={nodes && <TreeList depth={depth + 1} nodes={nodes} nodeRenderer={NodeRenderer} />}
      />
    </StyledTreeListItem>
  );
};

TreeList = forwardRef(
  (
    { nodes, depth, nodeRenderer, ...restProps }: PropsWithoutRef<TreeListProps>,
    ref?: TreeProps['ref']
  ) => {
    const hasParentSibling = nodes.some(node => !!node.nodes);
    return nodes.length > 0 ? (
      <StyledTreeList {...restProps} ref={ref} role={depth === 0 ? 'tree' : 'group'}>
        {nodes.map(node => (
          <TreeItem
            {...node}
            key={node.id}
            depth={depth}
            nodeRenderer={nodeRenderer}
            hasParentSibling={hasParentSibling}
          />
        ))}
      </StyledTreeList>
    ) : null;
  }
);

const Tree = forwardRef(
  (
    { nodeRenderer = DefaultNodeRenderer, ...restProps }: PropsWithoutRef<TreeProps>,
    ref: TreeProps['ref']
  ) => {
    return <TreeList {...restProps} ref={ref} depth={0} nodeRenderer={nodeRenderer} />;
  }
) as <T extends TreeNode = TreeNode>(props: TreeProps<T> & ForwardProps) => JSX.Element | null;

export default Tree;
