import { forwardRef, PropsWithoutRef, FC, HTMLAttributes } from 'react';

import { useConsolidatedRef } from '../../hooks';

import { StyledDialog } from './Dialog.styles';
import { DialogProps } from './Dialog.types';

const Dialog: FC<DialogProps & HTMLAttributes<HTMLDivElement>> = forwardRef(
  (
    {
      arrow = true,
      target,
      labelledbyId,
      ariaLabel,
      portal,
      strategy,
      placement,
      children,
      progress,
      ...restProps
    }: PropsWithoutRef<DialogProps>,
    ref: DialogProps['ref']
  ) => {
    const dialogRef = useConsolidatedRef(ref);

    return (
      <StyledDialog
        {...restProps}
        role='dialog'
        arrow={arrow}
        aria-modal='false'
        aria-labelledby={labelledbyId}
        aria-label={ariaLabel}
        aria-busy={progress ? 'true' : 'false'}
        ref={dialogRef}
        target={target}
        portal={portal}
        strategy={strategy}
        placement={placement}
      >
        {children}
      </StyledDialog>
    );
  }
);

export default Dialog;
