import {
  FunctionComponent,
  forwardRef,
  useState,
  useCallback,
  useRef,
  ChangeEvent,
  useEffect,
  PropsWithoutRef,
  Ref
} from 'react';

import { ForwardProps } from '../../types';
import ComboBox, { ComboBoxProps } from '../ComboBox';
import icons from '../Icon/iconNames';
import Icon from '../Icon';
import { menuHelpers, MenuProps, MenuItemProps } from '../Menu';
import { useI18n } from '../../hooks';
import { createStringMatcher } from '../../utils';

export interface IconPickerProps
  extends Pick<
      ComboBoxProps,
      | 'id'
      | 'label'
      | 'labelHidden'
      | 'required'
      | 'disabled'
      | 'readOnly'
      | 'name'
      | 'status'
      | 'info'
      | 'placeholder'
      | 'autoFocus'
    >,
    Pick<MenuProps, 'scrollAt'> {
  /** Default selection for the input. */
  defaultSelection: string;
  /** Callback fired when user chooses icon from the list of icons. */
  onSelect?: (iconName: string) => void;
}

const getFilteredItems = (iconList: MenuItemProps[], filterValue: string) => {
  if (!filterValue) return iconList;

  const regExp = createStringMatcher(filterValue);
  return iconList.filter(({ primary }) => regExp.test(primary));
};

const iconList: MenuItemProps[] = icons.map(icon => {
  return {
    id: icon,
    primary: icon,
    visual: <Icon name={icon} />,
    selected: false
  };
});

const pageSize = 20;

const IconPicker: FunctionComponent<IconPickerProps & ForwardProps> = forwardRef(
  (props: PropsWithoutRef<IconPickerProps>, ref: Ref<HTMLInputElement>) => {
    const { defaultSelection, scrollAt, onSelect, ...restProps } = props;

    const [items, setItems] = useState<MenuProps['items']>(() => iconList.slice(0, pageSize));

    const [isLoading, setIsLoading] = useState(false);
    const [hasMore, setHasMore] = useState(true);
    const [selectedIcon, setSelectedIcon] = useState(defaultSelection ?? '');
    const [filterValue, setFilterValue] = useState('');
    const t = useI18n();
    const loadMoreTimeoutId = useRef<ReturnType<typeof setTimeout>>();

    const resetItems = useCallback(() => {
      const filteredItems = getFilteredItems(iconList, filterValue);
      setItems(
        menuHelpers.selectItem(filteredItems.slice(0, pageSize), selectedIcon, 'single-select')
      );
      setHasMore(filteredItems.length > pageSize);
    }, [filterValue, selectedIcon]);

    useEffect(() => {
      resetItems();
    }, [filterValue, selectedIcon]);

    useEffect(() => {
      return () => {
        if (loadMoreTimeoutId.current) clearTimeout(loadMoreTimeoutId.current);
      };
    }, []);

    const loadMore = useCallback(() => {
      if (!isLoading) {
        setIsLoading(true);
        if (loadMoreTimeoutId.current) clearTimeout(loadMoreTimeoutId.current);
        loadMoreTimeoutId.current = setTimeout(() => {
          setIsLoading(false);
          setItems(prevItems => {
            const newItems = menuHelpers.mapTree(
              getFilteredItems(iconList, filterValue).slice(
                prevItems.length,
                prevItems.length + pageSize
              ),
              item => ({
                ...item,
                selected: item.items ? undefined : selectedIcon === item.id
              })
            );

            if (newItems.length === 0) setHasMore(false);

            return menuHelpers.appendTo(prevItems, newItems);
          });
        }, 500);
      }
    }, [filterValue, isLoading]);

    const selectItem = useCallback(
      (iconName: string) => {
        setSelectedIcon(iconName);
        setFilterValue('');
        onSelect?.(iconName);
      },
      [onSelect]
    );

    const clearSelection = useCallback(() => {
      setSelectedIcon('');
      setItems(cur =>
        menuHelpers.mapTree(cur, item => {
          return {
            ...item,
            selected: false
          };
        })
      );
    }, []);

    return (
      <ComboBox
        {...restProps}
        ref={ref}
        selected={
          selectedIcon
            ? {
                items: {
                  id: selectedIcon,
                  text: selectedIcon
                }
              }
            : undefined
        }
        value={filterValue}
        onChange={(e: ChangeEvent<HTMLInputElement>) => {
          if (!e.target.value) clearSelection();
          setFilterValue(e.target.value);
        }}
        onBlur={() => {
          resetItems();
          setFilterValue('');
        }}
        menu={{
          items,
          onItemClick: selectItem,
          scrollAt,
          accent: filterValue ? createStringMatcher(filterValue) : undefined,
          emptyText: t('no_items'),
          loading: isLoading,
          loadMore: hasMore ? loadMore : undefined
        }}
        visual={selectedIcon ? <Icon name={selectedIcon} /> : undefined}
      />
    );
  }
);

export default IconPicker;
