import { FunctionComponent } from 'react';
import styled, { css } from 'styled-components';

import { defaultThemeProp } from '../../theme';
import { ForwardProps } from '../../types';

export const StyledMark = styled.mark(({ theme }) => {
  return css`
    color: ${theme.base.colors.black};
    background-color: ${theme.components.mark['background-color']};
    font-weight: ${theme.components.mark['font-weight']};
  `;
});

StyledMark.defaultProps = defaultThemeProp;

const Mark: FunctionComponent<ForwardProps> = props => {
  return <StyledMark {...props} />;
};

export default Mark;
