import { forwardRef, FC, PropsWithoutRef } from 'react';

import { ForwardProps, OmitStrict } from '../../types';
import { useUID } from '../../hooks';
import MultiStepForm from '../MultiStepForm';
import { type BaseFormProps } from '../MultiStepForm/MultiStepForm.types';

export interface FormProps extends OmitStrict<BaseFormProps, 'content'> {
  /** The Form content. Recommended composing form elements within a layout template such as Grid or Flex as the only child. */
  children: BaseFormProps['content'];
}

const Form: FC<FormProps & ForwardProps> = forwardRef(
  (
    { children, actions, heading, description, banners, ...restProps }: PropsWithoutRef<FormProps>,
    ref: FormProps['ref']
  ) => {
    const stepId = useUID();

    return (
      <MultiStepForm
        {...restProps}
        heading={heading}
        steps={[
          {
            id: stepId,
            name: '',
            actions,
            content: children,
            description,
            banners
          }
        ]}
        currentStepId={stepId}
        ref={ref}
        autoComplete='off'
      />
    );
  }
);

export default Form;
