import { FC, MouseEvent, forwardRef, PropsWithoutRef, Ref, KeyboardEvent } from 'react';
import styled, { css } from 'styled-components';
import { mix, transparentize } from 'polished';

import { ForwardProps } from '../../types';
import { tryCatch } from '../../utils';
import BareButton, { StyledBareButton } from '../Button/BareButton';
import Icon, { registerIcon, StyledIcon } from '../Icon';
import * as timesIcon from '../Icon/icons/times.icon';
import { useConsolidatedRef, useI18n } from '../../hooks';
import { defaultThemeProp } from '../../theme';

registerIcon(timesIcon);

export interface SelectableProps {
  /** An id that is used by the onSelect and onRemove functions if they are provided. */
  id: string;
  /** The content of the Badge. */
  children: string;
  /** Takes an ID that represents the Badge. This function is called when the Badge is clicked. */
  onSelect?: (id: string) => void;
  /** Takes an ID that represents the Badge. This function is called when the Badge is closed by clicking the X. */
  onRemove?: (id: string) => void;
  /** Event handler called when the Badge is clicked. */
  onClick?: (event: MouseEvent) => void;
  /** Event handler fired on every keyup event. */
  onKeyUp?: (event: KeyboardEvent) => void;
}

export const StyledSelectable = styled.button(
  ({
    theme: {
      base: { spacing, palette, 'border-radius': borderRadius },
      components: {
        badges: { selectable }
      }
    }
  }) => {
    const { foreground, background } = selectable.base;
    const { dark, 'primary-background': primaryBackground, interactive } = palette;

    const displayBackground = tryCatch(() => mix(0.15, background, primaryBackground));
    const hoverBorder = tryCatch(() => mix(0.3, background, primaryBackground));
    const boxShadowColor = tryCatch(() => transparentize(0.45, dark));

    const height = 1.125;
    return css`
      background: ${displayBackground};
      border: 0 solid transparent;
      border-radius: calc(${height} * ${borderRadius});
      color: ${foreground};
      cursor: default;
      display: inline-flex;
      font-size: 0.75rem;
      height: ${height}rem;
      padding: 0 ${spacing};
      max-width: 100%;
      line-height: 1;

      span {
        white-space: nowrap;
        text-overflow: ellipsis;
        overflow: hidden;
        line-height: normal;
      }

      &:hover {
        color: ${interactive};
        box-shadow: inset 0 0 0 0.0625rem ${hoverBorder};

        ${StyledBareButton} {
          color: ${interactive};
        }
      }

      &:active {
        box-shadow: inset 0 0 0 0.0625rem ${background};
      }

      &:focus {
        color: ${interactive};
        box-shadow: 0 0 0.5rem -0.125rem ${boxShadowColor}, inset 0 0 0 0.125rem ${background};
        outline: none;

        ${StyledBareButton} {
          color: ${interactive};
        }
      }

      ${StyledIcon} {
        margin-inline-start: ${spacing};
        height: 1em;
        width: 1em;
      }
    `;
  }
);

StyledSelectable.defaultProps = defaultThemeProp;

const Selectable: FC<SelectableProps & ForwardProps> = forwardRef(
  (
    {
      id,
      children,
      onSelect,
      onRemove,
      onClick,
      onKeyUp,
      ...restProps
    }: PropsWithoutRef<SelectableProps>,
    ref: Ref<HTMLButtonElement>
  ) => {
    const consolidatedRef = useConsolidatedRef(ref);
    const t = useI18n();

    const handleClick = (e: MouseEvent<HTMLButtonElement>) => {
      onClick?.(e);
      onSelect?.(id);
    };

    const handleCloseClick = (e: MouseEvent) => {
      e.preventDefault();
      e.stopPropagation();
      onRemove?.(id);
    };

    const handleKeyUp = (e: KeyboardEvent) => {
      onKeyUp?.(e);
      if (e.key === 'Enter') {
        onSelect?.(id);
      }

      if (e.key === 'Backspace' || e.key === 'Delete') {
        onRemove?.(id);
      }
    };

    return (
      <StyledSelectable
        ref={consolidatedRef}
        onClick={handleClick}
        onKeyUp={handleKeyUp}
        {...restProps}
      >
        <span>{children}</span>
        {onRemove && (
          <BareButton tabIndex='-1' onClick={handleCloseClick} aria-label={t('remove')}>
            <Icon name='times' />
          </BareButton>
        )}
      </StyledSelectable>
    );
  }
);

export default Selectable;
