import {
  forwardRef,
  ReactNode,
  FunctionComponent,
  Ref,
  PropsWithoutRef,
  useState,
  useRef,
  KeyboardEvent,
  useContext,
  useEffect
} from 'react';
import styled, { css } from 'styled-components';
import { readableColor } from 'polished';

import Button, { ButtonProps } from '../Button';
import Popover, { PopoverProps } from '../Popover';
import BareButton from '../Button/BareButton';
import { BaseProps, ForwardProps } from '../../types';
import { defaultThemeProp } from '../../theme';
import {
  useBreakpoint,
  useConsolidatedRef,
  useI18n,
  useUID,
  useModalContext,
  useOS
} from '../../hooks';
import { documentIsAvailable, sameOrigin, tryCatch } from '../../utils';
import { calculateFontSize } from '../../styles';
import AppShellContext from '../AppShell/AppShellContext';
import { Keyboard } from '../Badges';
import Text, { StyledText } from '../Text';
import { StyledKeyboard } from '../Badges/Keyboard';
import Flex from '../Flex';
import Icon, { registerIcon } from '../Icon';
import * as openIcon from '../Icon/icons/open.icon';
import VisuallyHiddenText from '../VisuallyHiddenText';

export interface LinkProps extends BaseProps {
  /** Text or content for the Link. */
  children: ReactNode;
  /** URL or DOM id to navigate to. */
  href: string;
  /**
   * Controls the styling of the Link.
   * @default 'link'
   */
  variant?: ButtonProps['variant'];
  /**
   * Set the Icon prop to `true` if you're using just an Icon in your Link. Make sure to pass an Icon as children if `true`.
   * @default false
   */
  icon?: ButtonProps['icon'];
  /** Determines if the Link should render a popover when focused or hovered over. */
  previewable?: boolean;
  /** Callback function that is run when the preview popover is interacted with. */
  onPreview?: (e: { href: string }) => void;
  /** Specifies where to open the link href. */
  target?: string;
  /** Ref for the wrapping element. */
  ref?: Ref<HTMLAnchorElement>;
}

export const StyledLinkPopover = styled.div<{ preview: boolean }>(
  ({ theme, preview }) => css`
    background-color: ${theme.components.tooltip['background-color']};
    z-index: ${theme.base['z-index'].tooltip};

    ${preview &&
    css`
      padding: ${theme.base.spacing};
    `}
  `
);

StyledLinkPopover.defaultProps = defaultThemeProp;

export const StyledLinkPopoverBtn = styled(BareButton)(({ theme }) => {
  const contrastColor = tryCatch(() => readableColor(theme.base.palette.interactive));
  const fontSize = calculateFontSize(theme.base['font-size'], theme.base['font-scale']);

  return css`
    position: relative;
    display: inline-flex;
    align-items: center;
    z-index: 1;
    padding: 0 ${theme.base.spacing};
    min-height: 1.5rem;
    line-height: 1;
    color: ${contrastColor};
    font-size: ${fontSize.xs};
    text-decoration: none;

    /* FIXME: Button selector specificity... */
    & + & {
      margin: 0;
    }

    & + &::before {
      content: '';
      position: absolute;
      inset-inline-start: 0;
      inset-block: 0;
      width: 1px; /* stylelint-disable-line unit-allowed-list */
      background-color: ${contrastColor};
      opacity: ${theme.base.transparency['transparent-3']};
    }

    &:hover,
    &:active {
      text-decoration: underline;
    }

    &:focus {
      box-shadow: inset 0 0 0 0.0625rem ${theme.base.palette.light},
        0 0 0.125rem 0.0625rem ${theme.base.palette.interactive};
    }
  `;
});

StyledLinkPopoverBtn.defaultProps = defaultThemeProp;

export const StyledLinkPreviewPopoverBtn = styled(StyledLinkPopoverBtn)(({ theme }) => {
  return css`
    width: 100%;
    padding: unset;

    &:hover,
    &:active {
      text-decoration: none;
      ${StyledText} span {
        text-decoration: underline;
      }
    }

    ${StyledText} {
      display: flex;
      justify-content: space-between;
      align-items: center;
      width: 100%;
    }

    ${StyledKeyboard} {
      color: ${theme.base.palette.light};
      background-color: ${theme.base.colors.gray.dark};
      border-color: ${theme.base.colors.gray.medium};
      inset-block-start: unset;

      &:first-of-type {
        margin-inline: ${theme.base.spacing};
      }
    }
  `;
});

StyledLinkPreviewPopoverBtn.defaultProps = defaultThemeProp;

export const StyledLink = styled(Button)``;

StyledLink.defaultProps = defaultThemeProp;

registerIcon(openIcon);

const Link: FunctionComponent<LinkProps & ForwardProps> = forwardRef(
  (
    {
      href,
      variant = 'link',
      icon = false,
      previewable,
      onPreview,
      target: targetProp,
      children,
      ...restProps
    }: PropsWithoutRef<LinkProps>,
    ref: LinkProps['ref']
  ) => {
    const { initialized: inModal } = useModalContext();
    const { previewTriggerRef } = useContext(AppShellContext);
    const uid = useUID();
    const linkRef = useConsolidatedRef(ref);
    const [popover, setPopover] = useState(false);
    const [popoverHideDelay, setPopoverHideDelay] = useState<PopoverProps['hideDelay']>('short');
    const [previewPopover, setPreviewPopover] = useState(false);
    const previewBtnRef = useRef<HTMLButtonElement>(null);
    const isSmallOrAbove = useBreakpoint('sm');
    const t = useI18n();
    const { macintosh } = useOS();
    const contextMenuOpened = useRef(false);

    const url = new URL(href, document.location.href);
    const crossOrigin = documentIsAvailable
      ? /^https?:$/.test(url.protocol) && !sameOrigin(document.location, url)
      : false;
    const target = crossOrigin ? '_blank' : targetProp;

    const showPopover = () => {
      if (isSmallOrAbove) {
        setPopover(true);
        setPreviewPopover(false);
      }
    };

    const showPreviewPopover = () => {
      if (isSmallOrAbove && !popover && !contextMenuOpened.current) setPreviewPopover(true);
    };

    const hidePopover = () => {
      setPopover(false);
    };

    const hidePreviewPopover = () => {
      setPreviewPopover(false);
    };

    const onEnterLink = () => {
      if (previewable) showPopover();
    };

    const onFocusLink = () => {
      if (previewable) showPreviewPopover();
    };

    const onBlurLink = () => {
      contextMenuOpened.current = false;
      hidePreviewPopover();
    };

    const onPreviewClick = () => {
      previewTriggerRef.current = linkRef.current;
      onPreview?.({ href });
    };

    const onKeyUp = (e: KeyboardEvent) => {
      if (e.getModifierState('Alt') && e.code === 'KeyP') {
        onPreviewClick();
      }
    };

    useEffect(() => {
      if (popoverHideDelay === 'none' && !popover && !previewPopover) {
        setPopoverHideDelay('short');
      }
    }, [popoverHideDelay, popover, previewPopover]);

    return (
      <>
        <StyledLink
          href={href}
          variant={variant}
          ref={linkRef as any}
          target={target}
          {...restProps}
          onMouseEnter={onEnterLink}
          onMouseLeave={hidePopover}
          onFocus={onFocusLink}
          onBlur={onBlurLink}
          onKeyUp={onKeyUp}
          onContextMenu={() => {
            contextMenuOpened.current = true;

            setPopoverHideDelay('none');
            setPreviewPopover(false);
            setPopover(false);
          }}
        >
          {children}
          {target === '_blank' && (
            <VisuallyHiddenText>{t('opens_in_a_new_tab')}</VisuallyHiddenText>
          )}
          {!icon && crossOrigin && <Icon name='open' />}
        </StyledLink>

        {!inModal && (
          <Popover
            show={previewPopover}
            groupId='link_preview'
            showDelay='short'
            hideDelay={popoverHideDelay}
            placement='bottom'
            target={linkRef.current}
            onMouseEnter={showPreviewPopover}
            onMouseLeave={hidePreviewPopover}
            as={StyledLinkPopover}
            arrow
            preview
            portal
            onHide={hidePreviewPopover}
          >
            <StyledLinkPreviewPopoverBtn
              preview
              type='button'
              ref={previewBtnRef}
              onClick={onPreviewClick}
              tabIndex='-1'
            >
              <Text>
                <span>{t('preview')}</span>
                <Flex container={{ inline: true, justify: 'end' }}>
                  <Keyboard keyName='Alt' />
                  <Keyboard keyName='P' />
                </Flex>
              </Text>
            </StyledLinkPreviewPopoverBtn>
          </Popover>
        )}

        <Popover
          show={popover}
          groupId='link_preview'
          showDelay='short'
          hideDelay={popoverHideDelay}
          placement='bottom'
          target={linkRef.current}
          onMouseEnter={showPopover}
          onMouseLeave={hidePopover}
          as={StyledLinkPopover}
          arrow
          portal
        >
          {!inModal && (
            <StyledLinkPopoverBtn
              preview
              type='button'
              ref={previewBtnRef}
              onClick={onPreviewClick}
              tabIndex='-1'
            >
              {t('preview')}
            </StyledLinkPopoverBtn>
          )}
          <StyledLinkPopoverBtn
            forwardedAs='a'
            href={href}
            target='_blank'
            rel='noreferrer'
            tabIndex='-1'
          >
            {t('link_open_in_tab_text')}
          </StyledLinkPopoverBtn>
        </Popover>

        {previewable && (
          <span id={`${uid}-preview-instructions`} hidden>
            {t('preview_link_instruction', [macintosh ? 'option' : 'alt'])}
          </span>
        )}
      </>
    );
  }
);

export default Link;
