import { FC, memo } from 'react';
import styled, { css } from 'styled-components';
import { hideVisually } from 'polished';
import { Shortcut } from 'shortcuts';

import { defaultThemeProp } from '../../theme';
import { focusHeadingOrContainer } from '../../utils';
import Button from '../Button';
import Popover from '../Popover';
import Flex from '../Flex';
import { Keyboard } from '../Badges';
import Text from '../Text';
import Grid from '../Grid';
import { useI18n, useShortcutManager, useUID } from '../../hooks';
import { Translation } from '../../i18n';
import { StyledButtonLink } from '../Button/Button';

interface SkipLink {
  label: string;
  target: { ariaLabel: string; selector: string };
}

export interface SkipLinksProps {
  items: SkipLink[];
}

const StyledSkipLinksItems = styled.div`
  & > ${StyledButtonLink} {
    display: block;
    margin-inline-start: 0;
  }
`;

export const StyledSkipLinks = styled(Popover)(props => {
  const {
    theme: {
      base: { 'z-index': zIndices }
    }
  } = props;

  const z = Math.max(...Object.values(zIndices)) + 1;

  return css`
    position: fixed;
    z-index: ${z};

    &:not(:focus-within) {
      z-index: -1;
      ${hideVisually}
    }
  `;
});

StyledSkipLinks.defaultProps = defaultThemeProp;

const StyledShortcuts = styled.div(({ theme }) => {
  return css`
    border-block-start: 0.0625rem solid ${theme.base.palette['border-line']};

    [role='listitem'] {
      display: contents;
    }
  `;
});

StyledShortcuts.defaultProps = defaultThemeProp;

const SkipLinks: FC<SkipLinksProps> = memo(({ items }: SkipLinksProps) => {
  const { bindings } = useShortcutManager();
  const t = useI18n();
  const shortcutHeadingId = useUID();

  return (
    <Flex
      as={StyledSkipLinks}
      container={{ direction: 'column' }}
      placement='bottom-end'
      target={{
        getBoundingClientRect: () => ({
          height: 0,
          width: 0,
          x: 24,
          y: 24,
          top: 24,
          bottom: 24,
          left: 24,
          right: 24
        })
      }}
      modifiers={[
        {
          name: 'placeAndContain',
          enabled: false
        }
      ]}
      data-app-region
      aria-label={t('skip_navigation_menu_label')}
    >
      <Flex container={{ direction: 'column', gap: 0.5, pad: 1 }} as={StyledSkipLinksItems}>
        {items.map(item => (
          <Button
            variant='link'
            key={item.label}
            onClick={() =>
              focusHeadingOrContainer(
                document.querySelector(item.target.selector) ?? document,
                item.target.ariaLabel
              )
            }
          >
            {item.label}
          </Button>
        ))}
      </Flex>

      {Object.entries(bindings).length > 0 && (
        <Flex container={{ direction: 'column', gap: 1, pad: 1 }} as={StyledShortcuts}>
          <Text variant='h2' as='span' id={shortcutHeadingId}>
            {t('shortcuts')}
          </Text>
          <Grid
            container={{
              cols: 'auto minmax(0, 1fr)',
              gap: 1,
              alignItems: 'center'
            }}
            role='list'
            aria-labelledby={shortcutHeadingId}
          >
            {Object.entries(bindings).map(([id, shortcut]) => {
              return (
                <div role='listitem' key={id}>
                  <Text>{t(`shortcut_${id}` as keyof Translation)}</Text>
                  <Flex container={{ gap: 0.5 }}>
                    {Shortcut.shortcut2accelerator(shortcut)
                      .split('+')
                      .map(key => (
                        <Keyboard keyName={key} key={key} />
                      ))}
                  </Flex>
                </div>
              );
            })}
          </Grid>
        </Flex>
      )}
    </Flex>
  );
});

export default SkipLinks;
