import {
  FunctionComponent,
  useRef,
  forwardRef,
  Ref,
  PropsWithoutRef,
  useImperativeHandle,
  useEffect
} from 'react';
import styled from 'styled-components';

import { ForwardProps } from '../../../types';
import FormField from '../../FormField';
import Flex from '../../Flex';
import Select, { Option } from '../../Select';
import Button from '../../Button';
import Icon, { registerIcon } from '../../Icon';
import * as clockIcon from '../../Icon/icons/clock.icon';
import Popover from '../../Popover';
import TimePicker from '../Picker/TimePicker';
import { useConfiguration, useConsolidatedRef, useFocusWithin, useI18n } from '../../../hooks';
import { FormControlProps, StyledFormControl } from '../../FormControl';
import DateTimeProps, { DateTimeCallbackParameter } from '../DateTime.types';
import { sameWidth } from '../../Popover/modifiers';
import { hasProp } from '../../../utils';
import type { MinuteInterval } from '../Picker/DatePicker.types';

import {
  getDateFormat,
  parseToDate,
  is12HClockFormat,
  validateTime,
  ClockFormat,
  getTimeOptions
} from './utils';
import StyledDateTime, { StyledInputContainer } from './DateTime.styles';
import PartInput from './PartInput';
import usePickerButton from './usePickerButton';
import useAutoFocusNextInput from './useAutoFocusNextInput';
import { TimeParts } from './DateTimeInput.types';

registerIcon(clockIcon);

export interface TimeInputProps extends DateTimeProps {
  /**
   * Display the time with seconds.
   * @default false
   */
  withSeconds?: boolean;
  /**
   * Defines interval for time picker options (in minutes).
   * @default 30
   */
  pickerInterval?: MinuteInterval;
  /** Defines clock format that overrides locale based format */
  clockFormat?: ClockFormat;
  /** Pass a heading and content to show additional information on the field. */
  additionalInfo?: FormControlProps['additionalInfo'];
}

export const StyledTimeInput = styled(StyledDateTime)<TimeInputProps>`
  padding-inline-end: 0;
`;

function getMeridiem(hour: number | undefined) {
  return hour && hour >= 12 ? 'PM' : 'AM';
}

const convertTo12h = (hour: number) => {
  let converted: number = hour;
  if (hour > 12) converted = hour - 12;
  else if (hour === 0) converted = 12;
  return converted;
};

const convertTo24h = (hour: number, meridiem: string) => {
  let converted: number = hour;
  if (meridiem === 'PM' && hour < 12) converted = hour + 12;
  else if (meridiem === 'AM' && hour === 12) converted = 0;
  return converted;
};

const convertToCallbackParameter = (
  date: Date,
  parts: TimeParts<string | undefined>
): DateTimeCallbackParameter => {
  const withSeconds = hasProp(parts, 'second');
  const { hour, minute, second = date.getUTCSeconds().toString(), dayPeriod: meridiem } = parts;
  const toValidate = withSeconds ? [hour, minute, second] : [hour, minute];

  if (toValidate.every(Boolean)) {
    const is12h = meridiem !== undefined;

    if (!validateTime(Number(hour), Number(minute), Number(second), is12h)) {
      return {
        valueAsISOString: '',
        valueAsTimestamp: NaN,
        state: 'invalid'
      };
    }

    const resultDate = new Date(date);
    resultDate.setUTCHours(
      is12h ? convertTo24h(Number(hour), meridiem) : Number(hour),
      Number(minute),
      Number(second)
    );
    return {
      valueAsISOString: resultDate.toISOString(),
      valueAsTimestamp: resultDate.getTime()
    };
  }
  if (toValidate.every(p => !p)) {
    return {
      valueAsISOString: undefined,
      valueAsTimestamp: undefined
    };
  }
  return {
    valueAsISOString: '',
    valueAsTimestamp: NaN,
    state: 'incomplete'
  };
};

const TimeInput: FunctionComponent<TimeInputProps & ForwardProps> = forwardRef(
  (props: PropsWithoutRef<TimeInputProps>, ref: Ref<HTMLFieldSetElement>) => {
    const { locale } = useConfiguration();
    const {
      value,
      id,
      label,
      labelHidden,
      status,
      withSeconds = false,
      pickerInterval = 30,
      clockFormat,
      info = getDateFormat(locale, getTimeOptions(withSeconds), true).join(''),
      required,
      readOnly,
      disabled,
      onChange,
      onFocus,
      onBlur,
      onResolveSuggestion,
      autoFocus,
      additionalInfo,
      handle,
      ...restProps
    } = props;

    const date = value !== undefined ? parseToDate(value) : undefined;

    const is12h = clockFormat ? clockFormat === 12 : is12HClockFormat(locale);

    const [hourInputRef, minuteInputRef, secondInputRef, meridiemSelectRef] = [
      useRef<HTMLInputElement>(null),
      useRef<HTMLInputElement>(null),
      useRef<HTMLInputElement>(null),
      useRef<HTMLSelectElement>(null)
    ];

    useAutoFocusNextInput([hourInputRef, minuteInputRef, secondInputRef]);

    const pickParts = () => {
      const parts: TimeParts<string | undefined> = {
        hour: hourInputRef.current?.value || undefined,
        minute: minuteInputRef.current?.value || undefined
      };
      if (withSeconds) parts.second = secondInputRef.current?.value;
      if (is12h) parts.dayPeriod = meridiemSelectRef.current?.value;
      return parts;
    };

    const currentlySelectedTime = () => {
      const parts = pickParts();

      if (Object.values(parts).every(Boolean)) {
        const hour = Number(parts.hour);
        const minute = Number(parts.minute);
        const second = parts.minute ? Number(parts.minute) : 0;

        const current = date ?? new Date();
        return validateTime(hour, minute, second, is12h)
          ? new Date(
              current.setUTCHours(
                is12h ? convertTo24h(hour, parts.dayPeriod!) : hour,
                minute,
                second
              )
            )
          : undefined;
      }
    };

    const [open, setOpen, , setPopoverRef, buttonEl, setButtonEl] = usePickerButton();

    const inputContainerRef = useRef<HTMLDivElement>(null);

    const onFocusChange = (focused: boolean) => {
      const callbackParam = convertToCallbackParameter(
        date ? new Date(date) : new Date(),
        pickParts()
      );
      if (onFocus && focused) onFocus(callbackParam);
      if (!focused) {
        if (inputContainerRef.current) inputContainerRef.current.scrollTop = 0;
        onBlur?.(callbackParam);
      }
    };

    const onInputChange = () => {
      onChange?.(convertToCallbackParameter(date ? new Date(date) : new Date(), pickParts()));
    };

    const setMeridiem = (meridiem: string) => {
      if (meridiemSelectRef.current) {
        meridiemSelectRef.current.value = meridiem;
      }
    };

    useEffect(() => {
      if (is12h && meridiemSelectRef.current) {
        meridiemSelectRef.current.value = getMeridiem(date?.getUTCHours());
      }
    }, [is12h, date?.getUTCHours()]);

    const t = useI18n();

    const displayNames = new Intl.DisplayNames(locale, { style: 'long', type: 'dateTimeField' });

    const hour = date && is12h ? convertTo12h(date.getUTCHours()) : date?.getUTCHours();
    const HourInput = (
      <PartInput
        key='hour'
        placeholder={t('hour_placeholder')}
        aria-label={displayNames.of('hour')}
        value={hour}
        onChange={onInputChange}
        size={2}
        maxLength={2}
        readOnly={readOnly}
        min={is12h ? 1 : 0}
        max={is12h ? 12 : 23}
        ref={hourInputRef}
        autoFocus={autoFocus}
      />
    );

    const MinuteInput = (
      <PartInput
        key='minute'
        placeholder={t('minute_placeholder')}
        aria-label={displayNames.of('minute')}
        value={date?.getUTCMinutes()}
        onChange={onInputChange}
        size={2}
        maxLength={2}
        readOnly={readOnly}
        max={59}
        ref={minuteInputRef}
      />
    );

    const SecondInput = (
      <PartInput
        key='second'
        placeholder={t('second_placeholder')}
        aria-label={displayNames.of('second')}
        value={date?.getUTCSeconds()}
        onChange={onInputChange}
        size={2}
        maxLength={2}
        readOnly={readOnly}
        max={59}
        ref={secondInputRef}
      />
    );

    const Period = (
      <Select
        ref={meridiemSelectRef}
        aria-label={displayNames.of('dayPeriod')}
        key='period'
        required={required}
        disabled={disabled}
        onChange={() => {
          if (Object.values(pickParts()).every(Boolean)) onInputChange();
        }}
        readOnly={readOnly}
      >
        <Option value='AM'>{t('meridiem_value_am')}</Option>
        <Option value='PM'>{t('meridiem_value_pm')}</Option>
      </Select>
    );

    const containerRef = useConsolidatedRef(ref);
    useFocusWithin([containerRef], onFocusChange);

    useImperativeHandle(
      handle,
      () => ({
        clear: () => {
          if (hourInputRef.current) hourInputRef.current.value = '';
          if (minuteInputRef.current) minuteInputRef.current.value = '';
          if (secondInputRef.current) secondInputRef.current.value = '';
          if (meridiemSelectRef.current) meridiemSelectRef.current.value = '';
          onInputChange();
        }
      }),
      [onInputChange]
    );

    const Picker = (
      <>
        <Button
          variant='simple'
          icon
          ref={setButtonEl}
          onClick={() => setOpen(cur => !cur)}
          aria-label={t(open ? 'close_time_button_a11y' : 'open_time_button_a11y')}
          readOnly={readOnly}
          disabled={disabled}
        >
          <Icon name='clock' />
        </Button>

        <Popover
          show={open}
          ref={setPopoverRef}
          target={containerRef.current}
          placement='bottom-end'
          strategy='fixed'
          modifiers={[sameWidth]}
        >
          <TimePicker
            selected={currentlySelectedTime()}
            interval={pickerInterval}
            onSelect={selected => {
              hourInputRef.current!.value = (
                is12h ? convertTo12h(selected.getUTCHours()) : selected.getUTCHours()
              )
                .toString()
                .padStart(2, '0');
              minuteInputRef.current!.value = selected.getUTCMinutes().toString().padStart(2, '0');
              if (withSeconds)
                secondInputRef.current!.value = selected
                  .getUTCSeconds()
                  .toString()
                  .padStart(2, '0');
              if (meridiemSelectRef.current) {
                meridiemSelectRef.current.value = getMeridiem(selected.getUTCHours());
              }
              setMeridiem(getMeridiem(selected.getUTCHours()));
              setOpen(false);
              setTimeout(() => buttonEl?.focus());
              onInputChange();
            }}
            clockFormat={clockFormat}
          />
        </Popover>
      </>
    );

    const Comp = (
      <Flex
        as={StyledTimeInput}
        forwardedAs={StyledFormControl}
        hasSuggestion={status === 'pending' && !!onResolveSuggestion}
        container={{ alignItems: 'center', wrap: 'nowrap' }}
        status={status}
        readOnly={readOnly}
        disabled={disabled}
        ref={containerRef}
        onClick={(e: MouseEvent) => {
          if (e.target === containerRef.current) hourInputRef.current?.focus();
        }}
        {...restProps}
      >
        <Flex
          ref={inputContainerRef}
          as={StyledInputContainer}
          container={{ alignItems: 'center', wrap: 'wrap' }}
        >
          {getDateFormat(locale, getTimeOptions(withSeconds, is12h)).map(part => {
            switch (part) {
              case 'hh':
                return HourInput;
              case 'mm':
                return MinuteInput;
              case 'ss':
                return SecondInput;
              case 'AM':
              case 'PM':
                return Period;
              default:
                return part;
            }
          })}
        </Flex>
        {!readOnly && Picker}
      </Flex>
    );

    return label ? (
      <FormField
        as='fieldset'
        labelAs='legend'
        {...{
          label,
          labelHidden,
          id,
          info,
          status,
          required,
          disabled,
          onResolveSuggestion,
          additionalInfo
        }}
        ref={ref}
      >
        {Comp}
      </FormField>
    ) : (
      Comp
    );
  }
);

export default TimeInput;
