import { FunctionComponent, ReactNode, Ref, PropsWithoutRef, forwardRef } from 'react';
import styled, { css } from 'styled-components';

import Text from '../Text';
import Flex from '../Flex';
import { BaseProps, ForwardProps, NoChildrenProp, TestIdProp } from '../../types';
import { useTestIds } from '../../hooks';

import { getMetaListTestIds } from './MetaList.test-ids';

export interface MetaListProps extends BaseProps, NoChildrenProp, TestIdProp {
  /** Array of nodes or text to be rendered in the list. */
  items: ReactNode[];
  /**
   * If true, MetaList items that extend past the MetaList's container will wrap to a new line.
   * @default true
   */
  wrapItems?: boolean;
  /** Ref for the wrapping element. */
  ref?: Ref<HTMLUListElement>;
}

export const StyledMetaList = styled.ul<{ wrapItems: boolean }>(({ wrapItems }) => {
  return css`
    list-style: none;
    overflow: hidden;

    ${!wrapItems &&
    css`
      white-space: nowrap;
    `}
  `;
});

export const StyledMetaListItem = styled.li<{ wrapItems: boolean }>(({ wrapItems }) => {
  return css`
    min-width: 0;
    display: inline-block;

    ${wrapItems
      ? css`
          overflow-wrap: break-word;
        `
      : css`
          white-space: nowrap;
          text-overflow: ellipsis;
          overflow: hidden;
        `}

    &[role='separator'] {
      -webkit-user-select: none;
      user-select: none;
    }
  `;
});

const MetaList: FunctionComponent<MetaListProps & ForwardProps> = forwardRef(
  (
    { testId, items, wrapItems = true, ...restProps }: PropsWithoutRef<MetaListProps>,
    ref: MetaListProps['ref']
  ) => {
    const testIds = useTestIds(testId, getMetaListTestIds);

    return (
      <Flex
        data-testid={testIds.root}
        {...restProps}
        as={StyledMetaList}
        container={{ colGap: 0.5, alignItems: 'center', wrap: wrapItems ? 'wrap' : 'nowrap' }}
        wrapItems={wrapItems}
        ref={ref}
      >
        {items.flatMap((item, i, arr) =>
          item
            ? [
                <Text
                  as={StyledMetaListItem}
                  key={`${i + 0}`}
                  variant='secondary'
                  wrapItems={wrapItems}
                >
                  {item}
                </Text>
              ].concat(
                i !== arr.length - 1
                  ? [
                      <Text
                        as={StyledMetaListItem}
                        role='separator'
                        key={`${i + 0}-sep`}
                        variant='secondary'
                      >
                        â€¢
                      </Text>
                    ]
                  : []
              )
            : []
        )}
      </Flex>
    );
  }
);

export default MetaList;
