import { FC, Ref, forwardRef, PropsWithoutRef } from 'react';

import { Action, BaseProps, ForwardProps, NoChildrenProp, TestIdProp } from '../../types';
import FormField from '../FormField';
import { FormControlProps, StyledFormControl } from '../FormControl';
import { hasProp } from '../../utils';
import { useTestIds, useUID } from '../../hooks';

import StyledInput from './Input.styles';
import { getInputTestIds } from './Input.test-ids';

export interface InputProps extends FormControlProps, BaseProps, NoChildrenProp, TestIdProp {
  /**
   * Specifies the type of input to be used.
   * @default 'text'
   */
  type?:
    | 'text'
    | 'password'
    | 'number'
    | 'email'
    | 'url'
    | 'search'
    | 'tel'
    | 'date'
    | 'datetime-local'
    | 'time'
    | 'week'
    | 'month'
    | 'hidden'
    | 'color';
  /** Pass an array of Action objects to append button(s) inline with the Input. */
  actions?: Action[];
  /** Enables read only mode */
  readOnly?: FormControlProps['readOnly'];
}

const Input: FC<InputProps & ForwardProps> = forwardRef(
  (props: PropsWithoutRef<InputProps>, ref: Ref<HTMLInputElement>) => {
    const uid = useUID();

    const {
      testId,
      id = uid,
      value,
      defaultValue,
      required = false,
      disabled = false,
      readOnly = false,
      label,
      additionalInfo,
      labelHidden,
      info,
      status,
      actions,
      onResolveSuggestion,
      ...restProps
    } = props;

    const testIds = useTestIds(testId, getInputTestIds);

    const controlProp: {
      value?: string;
      defaultValue?: string;
    } = {};

    // Conditionally render component as controlled/uncontrolled
    if (hasProp(props, 'value')) {
      controlProp.value = value ?? '';
    } else if (hasProp(props, 'defaultValue')) {
      controlProp.defaultValue = defaultValue ?? '';
    }

    const Comp = (
      <StyledFormControl
        {...{
          'data-testid': testIds.control,
          ref,
          id,
          required,
          disabled,
          readOnly,
          status,
          hasSuggestion: !!onResolveSuggestion && status === 'pending',
          ...controlProp,
          ...restProps,
          as: StyledInput
        }}
      />
    );

    return label ? (
      <FormField
        {...{
          testId: testIds,
          additionalInfo,
          label,
          labelHidden,
          id,
          info,
          readOnly,
          status,
          required,
          disabled,
          actions,
          onResolveSuggestion
        }}
      >
        {Comp}
      </FormField>
    ) : (
      Comp
    );
  }
);

export default Input;
