import {
  FC,
  forwardRef,
  Ref,
  useState,
  PropsWithoutRef,
  MouseEventHandler,
  useEffect
} from 'react';
import styled, { css } from 'styled-components';
import { hideVisually } from 'polished';
import { ChromePicker, ColorChangeHandler } from 'react-color';

import {
  BaseProps,
  ForwardProps,
  NoChildrenProp,
  PropsWithDefaults,
  TestIdProp
} from '../../types';
import FormField, { FormFieldProps } from '../FormField';
import { defaultThemeProp } from '../../theme';
import { FormControlProps, StyledFormControl } from '../FormControl';
import Flex from '../Flex';
import { useConsolidatedRef, useUID, useElement, useOuterEvent, useTestIds } from '../../hooks';
import Popover from '../Popover';

import { getColorPickerTestIds } from './ColorPicker.test-ids';

export interface ColorPickerProps extends BaseProps, NoChildrenProp, TestIdProp {
  /** label for the color picker control */
  label: FormControlProps['label'];
  /**
   * Sets DOM id for the control and associates label element via 'for' attribute.
   * If an id is not passed, a random id will be generated for any render.
   */
  id?: FormControlProps['id'];
  /**
   * Color picker value in hex
   * @default '#000000'
   */
  value?: FormControlProps['value'];
  /**
   * Show color swatch without hex value display.
   * @default false
   */
  swatchOnly?: boolean;
  /**
   * Enables alpha slider.
   * @default false
   */
  alpha?: boolean;
  /** Indicate if the field is required. The browser defaults to false. */
  required?: FormControlProps['required'];
  /** Disable the control. */
  disabled?: FormControlProps['disabled'];
  /** Visually hides the label region. */
  labelHidden?: FormControlProps['labelHidden'];
  /** It is recommended to pass a simple string to offer guidance. Text will be styled based on status prop. */
  info?: FormControlProps['info'];
  /** Set visual state based on a validation state. */
  status?: FormControlProps['status'];
  /** Makes the input non editable and non clickable. The browser defaults to false. */
  readOnly?: FormControlProps['readOnly'];
  /** Sets html name attribute for the underlying control. Useful for mapping to a data field. */
  name?: FormControlProps['name'];
  /** Layout field elements inline in a row. */
  inline?: FormFieldProps['inline'];
  /** Button when clicked will show additional information on the color picker */
  additionalInfo?: FormFieldProps['additionalInfo'];
  /** onChange event handler that gets called on change of value. */
  onChange?: ColorChangeHandler;
  /** onBeforeClose of the color picker */
  onBeforeClose?: () => void;
  /** Called when the underlying input element is clicked. This should be rarely if ever used. */
  onClick?: MouseEventHandler<HTMLInputElement>;
  /** Ref placed on the input element. */
  ref?: Ref<HTMLInputElement>;
}

type ColorPickerPropsWithDefaults = PropsWithDefaults<ColorPickerProps, 'value' | 'swatchOnly'>;

const StyledColorPicker = styled.div<Pick<ColorPickerPropsWithDefaults, 'swatchOnly'>>(
  ({ theme, swatchOnly }) => {
    const size = theme.components.input.height;
    const borderWidth = theme.components['form-control']['border-width'];

    return css`
      ${StyledFormControl} {
        overflow: hidden;
        text-transform: uppercase;
        ${!swatchOnly &&
        css`
          min-width: 8rem;
        `}

        ${!swatchOnly &&
        css`
          padding-inline-end: ${theme.base.spacing};
        `}

        &:focus-within {
          box-shadow: ${theme.base.shadow.focus};
          border: none;
        }

        & > input {
          ${hideVisually}

          & + div {
            height: calc(${size} - (2 * ${borderWidth}));

            ${swatchOnly
              ? css`
                  width: calc(${size} - (2 * ${borderWidth}));
                `
              : css`
                  min-width: calc(${size} - ${borderWidth});
                  margin-inline-end: ${theme.base.spacing};
                  border-inline-end: ${borderWidth} solid
                    ${theme.components['form-control']['border-color']};
                `}
          }

          &:focus + div {
            width: ${size};
            height: ${size};
          }
        }

        & input {
          height: 2rem !important;
          min-height: 2rem;
          color: #000000 !important;
          background-color: #ffffff !important;
          font-size: 0.8125rem !important;
          font-family: ${theme.base['font-family']};

          & + label {
            font-weight: 600;
            color: rgba(0, 0, 0, 0.6) !important;
            font-size: 0.8125rem !important;
            font-family: ${theme.base['font-family']};
          }
        }
      }
    `;
  }
);

StyledColorPicker.defaultProps = defaultThemeProp;

const ColorPicker: FC<ColorPickerProps & ForwardProps> = forwardRef(
  (props: PropsWithoutRef<ColorPickerProps>, ref: ColorPickerProps['ref']) => {
    const uid = useUID();
    const {
      testId,
      id = uid,
      value = '#000000',
      label,
      info,
      disabled,
      status,
      required,
      readOnly,
      labelHidden,
      swatchOnly = false,
      alpha = false,
      inline,
      onClick,
      onChange,
      onBeforeClose,
      additionalInfo,
      ...restProps
    } = props;

    const testIds = useTestIds(testId, getColorPickerTestIds);

    const inputRef = useConsolidatedRef(ref);
    const maskedSwatchRef = useConsolidatedRef<HTMLDivElement>();
    const [popoverEl, setPopoverEl] = useElement<HTMLElement>(null);
    const [showPopOver, setShowPopOver] = useState(false);

    const hidePopover = () => {
      if (showPopOver) {
        onBeforeClose?.();
      }

      setShowPopOver(false);
    };

    const closePopover = (e: KeyboardEvent) => {
      if (e.key === 'Escape') {
        e.preventDefault();
        hidePopover();
      }
    };

    useOuterEvent('mousedown', [popoverEl], hidePopover);

    useEffect(() => {
      document.addEventListener('keydown', closePopover);
      popoverEl?.addEventListener('keydown', closePopover);
      return () => {
        document.removeEventListener('keydown', closePopover);
        popoverEl?.removeEventListener('keydown', closePopover);
      };
    }, [popoverEl]);

    useEffect(() => {
      if (showPopOver) {
        popoverEl?.focus();
      }
    }, [showPopOver]);

    return (
      <FormField
        testId={testIds}
        inline={inline}
        container={inline ? { justify: 'between' } : undefined}
        as={StyledColorPicker}
        id={id}
        label={label}
        labelHidden={labelHidden}
        swatchOnly={swatchOnly}
        info={info}
        disabled={disabled}
        status={status}
        required={required}
        additionalInfo={additionalInfo}
      >
        <Flex
          container={{ alignItems: 'center' }}
          item={swatchOnly ? { alignSelf: 'start' } : undefined}
          as={StyledFormControl}
          disabled={disabled}
          status={status}
          required={required}
          readOnly={readOnly}
          onClick={readOnly || disabled ? undefined : () => setShowPopOver(true)}
        >
          <input
            data-testid={testIds.control}
            {...restProps}
            id={id}
            type={readOnly ? undefined : 'color'}
            ref={inputRef}
            defaultValue={value}
            disabled={disabled}
            required={required}
            readOnly={readOnly}
            onClick={e => {
              e.preventDefault();
            }}
          />
          <div style={{ backgroundColor: value }} ref={maskedSwatchRef} />
          {!swatchOnly && value}
          <Popover
            placement='bottom-start'
            style={{ zIndex: '3000' }}
            show={showPopOver}
            ref={setPopoverEl}
            groupId='popover'
            target={maskedSwatchRef.current}
          >
            <ChromePicker
              disableAlpha={!alpha}
              color={value}
              onChange={(color, e) => {
                onChange?.(color, e);
              }}
            />
          </Popover>
        </Flex>
      </FormField>
    );
  }
);

export default ColorPicker;
