import { FunctionComponent, forwardRef, PropsWithoutRef, Ref } from 'react';
import styled, { css } from 'styled-components';

import { ForwardProps } from '../../types';
import Button from '../Button';
import Icon, { registerIcon } from '../Icon';
import * as plusIcon from '../Icon/icons/plus.icon';
import { useI18n } from '../../hooks';
import { defaultThemeProp } from '../../theme';

import FieldGroup, { FieldGroupProps, StyledFieldGroup } from './FieldGroup';

registerIcon(plusIcon);

export interface FieldGroupListItemProps extends FieldGroupProps {
  /** The ID of the field group list item */
  id: string;
}

export interface FieldGroupListProps {
  items: FieldGroupListItemProps[];
  onAdd?: () => void;
  onDelete?: (id: string) => void;
  ref?: Ref<HTMLDivElement>;
}

const StyledFieldGroupList = styled.div(
  ({ theme }) =>
    css`
      margin-block-start: ${theme.base.spacing};

      ${StyledFieldGroup} {
        margin-block-start: 0;
      }
    `
);

StyledFieldGroupList.defaultProps = defaultThemeProp;

export const StyledListItem = styled(FieldGroup)(
  ({ theme }) =>
    css`
      padding: ${theme.base.spacing} 0 calc(2 * ${theme.base.spacing});
      border-bottom: 0.0625rem solid ${theme.base.palette['border-line']};
      margin-block-end: calc(2 * ${theme.base.spacing});

      legend {
        margin-block-end: 0;
      }
    `
);

StyledListItem.defaultProps = defaultThemeProp;

const StyledAddButton = styled(Button)(
  ({ theme }) =>
    css`
      margin-top: ${theme.base.spacing};
    `
);

StyledAddButton.defaultProps = defaultThemeProp;

const FieldGroupList: FunctionComponent<FieldGroupListProps & ForwardProps> = forwardRef(
  (
    { items, onAdd, onDelete, ...restProps }: PropsWithoutRef<FieldGroupListProps>,
    ref: FieldGroupListProps['ref']
  ) => {
    const t = useI18n();

    return (
      <StyledFieldGroupList ref={ref} {...restProps}>
        {items.map(
          ({ id, children, name, headingTag, ...restItemProps }: FieldGroupListItemProps) => {
            return (
              <StyledListItem
                key={id}
                name={name}
                headingTag={headingTag}
                {...restItemProps}
                actions={
                  onDelete
                    ? [
                        {
                          id: 'delete',
                          text: t('delete'),
                          icon: 'trash',
                          iconOnly: true,
                          onClick: onDelete
                            ? () => {
                                onDelete(id);
                              }
                            : undefined
                        }
                      ]
                    : undefined
                }
              >
                {children}
              </StyledListItem>
            );
          }
        )}
        {onAdd && (
          <StyledAddButton variant='link' onClick={onAdd}>
            <Icon name='plus' /> {t('add')}
          </StyledAddButton>
        )}
      </StyledFieldGroupList>
    );
  }
);

export default FieldGroupList;
