import { forwardRef, FunctionComponent, PropsWithoutRef, Ref } from 'react';

import { BaseProps, ForwardProps, NoChildrenProp } from '../../types';
import Link from '../Link';

export interface EmailDisplayProps extends BaseProps, NoChildrenProp {
  /** Email address, no 'mailto:' scheme */
  value?: string;
  /** The text to be displayed. */
  displayText?: string;
  /**
   * Controls styling of the field.
   * @default 'link'
   */
  variant?: 'link' | 'text';
  /** Ref for the wrapping element. */
  ref?: Ref<HTMLAnchorElement | HTMLSpanElement>;
}

const EmailDisplay: FunctionComponent<EmailDisplayProps & ForwardProps> = forwardRef(
  (
    { value, displayText, variant = 'link', ...restProps }: PropsWithoutRef<EmailDisplayProps>,
    ref: EmailDisplayProps['ref']
  ) => {
    const displayValue = value ? (
      displayText || value
    ) : (
      <span aria-hidden='true'>&ndash;&ndash;</span>
    );
    return variant === 'text' || !value ? (
      <span ref={ref} {...restProps}>
        {displayValue}
      </span>
    ) : (
      <Link ref={ref as Ref<HTMLAnchorElement>} {...restProps} href={`mailto:${value}`}>
        {displayValue}
      </Link>
    );
  }
);

export default EmailDisplay;
