import {
  ClipboardEvent,
  FunctionComponent,
  forwardRef,
  PropsWithoutRef,
  Ref,
  useRef,
  RefObject,
  Dispatch,
  SetStateAction,
  useImperativeHandle,
  useEffect,
  ComponentType
} from 'react';
import styled from 'styled-components';

import { defaultThemeProp } from '../../../theme';
import { useConfiguration, useFocusWithin, useI18n, useOuterEvent } from '../../../hooks';
import { ForwardProps } from '../../../types';
import FormField from '../../FormField';
import Flex from '../../Flex';
import Button from '../../Button';
import Icon, { registerIcon } from '../../Icon';
import * as calendarIcon from '../../Icon/icons/calendar.icon';
import DatePicker from '../Picker/DatePicker';
import type DatePickerProps from '../Picker/DatePicker.types';
import { FormControlProps, StyledFormControl } from '../../FormControl';
import DateTimeProps, { DateTimeCallbackParameter } from '../DateTime.types';
import { cap } from '../../../utils';
import Dialog from '../../Dialog/Dialog';

import {
  getDateFormat,
  parsePastedTextToDate,
  parseToDate,
  validateDate,
  generatePickerButtonAriaLabel
} from './utils';
import StyledDateTime, {
  StyledClearNowButtonsContainer,
  StyledDialogContent,
  StyledInputContainer
} from './DateTime.styles';
import useAutoFocusNextInput from './useAutoFocusNextInput';
import PartInput from './PartInput';
import usePickerButton from './usePickerButton';
import type { DateParts } from './DateTimeInput.types';

registerIcon(calendarIcon);

export interface DateInputProps extends DateTimeProps {
  /**
   * Display additional column with week number in date picker popover.
   * @default false
   */
  showWeekNumber?: boolean;
  /** Changes default date picker */
  picker?: {
    renderer: ComponentType;
    rendererProps?: Partial<DatePickerProps> & ForwardProps;
  };
  /** Pass a heading and content to show additional information on the field. */
  additionalInfo?: FormControlProps['additionalInfo'];
}

export interface DateInputRefObject {
  readonly value: Date | undefined;
  setOpen: Dispatch<SetStateAction<boolean>>;
  buttonEl: HTMLElement | null;
}

export const StyledDateInput = styled(StyledDateTime)<DateInputProps>`
  padding-inline-end: 0;
`;

StyledDateInput.defaultProps = defaultThemeProp;

const convertToCallbackParameter = (
  date: Date,
  { year, month: monthIndex, day }: DateParts<string | undefined>
): DateTimeCallbackParameter => {
  if ([year, monthIndex, day].every(Boolean)) {
    if (!validateDate(Number(year), Number(monthIndex) + 1, Number(day))) {
      return {
        valueAsISOString: '',
        valueAsTimestamp: NaN,
        state: 'invalid'
      };
    }

    date.setUTCFullYear(Number(year), Number(monthIndex), Number(day));
    return {
      valueAsISOString: date.toISOString(),
      valueAsTimestamp: date.getTime()
    };
  }
  if ([year, monthIndex, day].every(p => !p)) {
    return {
      valueAsISOString: undefined,
      valueAsTimestamp: undefined
    };
  }
  return {
    valueAsISOString: '',
    valueAsTimestamp: NaN,
    state: 'incomplete'
  };
};

const DateInput: FunctionComponent<DateInputProps & ForwardProps> = forwardRef(
  (props: PropsWithoutRef<DateInputProps>, ref: Ref<DateInputRefObject>) => {
    const { locale } = useConfiguration();
    const {
      value,
      min,
      max,
      id,
      label,
      labelHidden,
      info = getDateFormat(locale).join(''),
      status,
      required,
      readOnly,
      disabled,
      onChange,
      onFocus,
      onBlur,
      onResolveSuggestion,
      showWeekNumber,
      picker: { renderer: PickerComp = DatePicker, rendererProps = {} } = {},
      autoFocus,
      additionalInfo,
      handle,
      ...restProps
    } = props;

    const t = useI18n();
    const containerRef = useRef<HTMLElement>(null);

    const date = value !== undefined ? parseToDate(value) : undefined;

    const [yearInputRef, monthInputRef, dayInputRef] = [
      useRef<HTMLInputElement>(null),
      useRef<HTMLInputElement>(null),
      useRef<HTMLInputElement>(null)
    ];

    const pickParts = (): DateParts<string | undefined> => {
      return {
        day: dayInputRef.current?.value || undefined,
        month: monthInputRef.current?.value
          ? (Number(monthInputRef.current.value) - 1).toString()
          : undefined,
        year: yearInputRef.current?.value || undefined
      };
    };

    const currentlySelectedDate = () => {
      const parts = pickParts();
      if (Object.values(parts).every(Boolean)) {
        const year = Number(parts.year);
        const monthIndex = Number(parts.month);
        const day = Number(parts.day);

        return validateDate(year, monthIndex + 1, day)
          ? new Date(Date.UTC(year, monthIndex, day))
          : undefined;
      }
    };

    const [open, setOpen, , setPopoverRef, buttonEl, setButtonEl] = usePickerButton();

    const inputContainerRef = useRef<HTMLDivElement>(null);

    const onFocusChange = (focused: boolean) => {
      const callbackParam = convertToCallbackParameter(
        date ? new Date(date) : new Date(),
        pickParts()
      );
      if (onFocus && focused) onFocus(callbackParam);
      if (!focused) {
        if (inputContainerRef.current) inputContainerRef.current.scrollTop = 0;
        onBlur?.(callbackParam);
      }
    };

    const onInputChange = () => {
      onChange?.(convertToCallbackParameter(date ? new Date(date) : new Date(), pickParts()));
      buttonEl?.setAttribute(
        'aria-label',
        generatePickerButtonAriaLabel(currentlySelectedDate(), 'date', locale, t, open)
      );
    };

    const setInputValue = (newValue?: Date) => {
      if (dayInputRef.current && monthInputRef.current && yearInputRef.current) {
        dayInputRef.current.value = newValue?.getUTCDate().toString().padStart(2, '0') ?? '';
        monthInputRef.current.value =
          (newValue && (newValue.getUTCMonth() + 1).toString().padStart(2, '0')) ?? '';
        yearInputRef.current.value = newValue?.getUTCFullYear().toString() ?? '';
      }
    };

    const onPaste = (e: ClipboardEvent) => {
      const text = e.clipboardData.getData('text');
      const newDate = parsePastedTextToDate(text, locale, date || new Date());
      if (newDate) {
        e.preventDefault();
        setInputValue(newDate);
        onInputChange();
      }
    };

    // autoclose picker on focusing out
    useOuterEvent('focusin', [containerRef], e => {
      if (containerRef.current?.contains(e.relatedTarget as HTMLElement)) setOpen(false);
    });

    const displayNames = new Intl.DisplayNames(locale, { style: 'long', type: 'dateTimeField' });

    const DayInput = (
      <PartInput
        key='day'
        placeholder={t('day_placeholder')}
        aria-label={displayNames.of('day')}
        value={date?.getUTCDate()}
        onChange={onInputChange}
        size={2}
        maxLength={2}
        readOnly={readOnly}
        min={1}
        max={31}
        ref={dayInputRef}
      />
    );

    const MonthInput = (
      <PartInput
        key='month'
        placeholder={t('month_placeholder')}
        aria-label={displayNames.of('month')}
        value={date ? date.getUTCMonth() + 1 : undefined}
        onChange={onInputChange}
        size={2}
        maxLength={2}
        readOnly={readOnly}
        min={1}
        max={12}
        ref={monthInputRef}
      />
    );

    const YearInput = (
      <PartInput
        key='year'
        placeholder={t('year_placeholder')}
        aria-label={displayNames.of('year')}
        value={date?.getUTCFullYear()}
        onChange={onInputChange}
        size={4}
        maxLength={4}
        padWithZeros={false}
        readOnly={readOnly}
        min={1}
        ref={yearInputRef}
      />
    );

    useFocusWithin([containerRef], onFocusChange);

    useImperativeHandle(
      handle,
      () => ({
        clear: () => {
          if (dayInputRef.current) dayInputRef.current.value = '';
          if (monthInputRef.current) monthInputRef.current.value = '';
          if (yearInputRef.current) yearInputRef.current.value = '';
          onInputChange();
        }
      }),
      [onInputChange]
    );

    useImperativeHandle(ref, () => ({
      setOpen,
      buttonEl,
      get value() {
        return currentlySelectedDate();
      }
    }));

    const PickerButton = (
      <>
        <Button
          variant='simple'
          icon
          ref={setButtonEl}
          onClick={() => setOpen(cur => !cur)}
          aria-label={generatePickerButtonAriaLabel(
            currentlySelectedDate(),
            'date',
            locale,
            t,
            open
          )}
          readOnly={readOnly}
          disabled={disabled}
        >
          <Icon name='calendar' />
        </Button>
        {open && buttonEl && (
          <Dialog
            ref={setPopoverRef}
            target={buttonEl}
            placement='bottom-end'
            ariaLabel={t('choose_date')}
          >
            <Flex container={{ direction: 'column' }} as={StyledDialogContent}>
              <PickerComp
                selected={currentlySelectedDate()}
                min={min}
                max={max}
                showWeekNumber={showWeekNumber}
                {...rendererProps}
                onChange={selected => {
                  setInputValue(selected);
                  setOpen(false);
                  setTimeout(() => buttonEl?.focus());
                  onInputChange();
                  rendererProps?.onChange?.(selected);
                }}
              />
              <Flex as={StyledClearNowButtonsContainer} container={{ justify: 'between' }}>
                <Button
                  aria-label={t('clear_current_date')}
                  onClick={() => {
                    setInputValue(undefined);
                    setOpen(false);
                    setTimeout(() => buttonEl?.focus());
                    onInputChange();
                  }}
                >
                  {t('clear')}
                </Button>
                <Button
                  aria-label={t('set_current_date_a11y')}
                  onClick={() => {
                    const now = new Date();
                    now.setUTCMinutes(now.getMinutes());
                    now.setUTCHours(now.getHours());
                    now.setUTCSeconds(now.getSeconds());
                    setInputValue(now);
                    setOpen(false);
                    setTimeout(() => buttonEl?.focus());
                    onInputChange();
                  }}
                >
                  {cap(new Intl.RelativeTimeFormat(locale, { numeric: 'auto' }).format(0, 'day'))}
                </Button>
              </Flex>
            </Flex>
          </Dialog>
        )}
      </>
    );

    const orderedRefs: RefObject<HTMLInputElement>[] = [];

    const Comp = (
      <Flex
        as={StyledDateInput}
        forwardedAs={StyledFormControl}
        hasSuggestion={status === 'pending' && !!onResolveSuggestion}
        container={{ alignItems: 'center', wrap: 'nowrap' }}
        status={status}
        readOnly={readOnly}
        disabled={disabled}
        ref={containerRef}
        onClick={(e: MouseEvent) => {
          if (e.target === containerRef.current) orderedRefs[0].current?.focus();
        }}
        {...restProps}
      >
        <Flex
          ref={inputContainerRef}
          as={StyledInputContainer}
          container={{ alignItems: 'center', wrap: 'wrap' }}
          onPaste={onPaste}
        >
          {getDateFormat(locale).map(part => {
            switch (part) {
              case 'DD':
                orderedRefs.push(dayInputRef);
                return DayInput;
              case 'MM':
                orderedRefs.push(monthInputRef);
                return MonthInput;
              case 'YYYY':
                orderedRefs.push(yearInputRef);
                return YearInput;
              default:
                return part;
            }
          })}
        </Flex>
        {!readOnly && PickerButton}
      </Flex>
    );

    useEffect(() => {
      if (autoFocus) orderedRefs[0].current?.focus();
    }, [autoFocus]);

    useAutoFocusNextInput(orderedRefs);

    return label ? (
      <FormField
        as='fieldset'
        aria-labelledby={id}
        labelAs='legend'
        {...{
          label,
          labelHidden,
          id,
          info,
          status,
          required,
          disabled,
          onResolveSuggestion,
          additionalInfo
        }}
        ref={ref}
      >
        {Comp}
      </FormField>
    ) : (
      Comp
    );
  }
);

export default DateInput;
