import {
  useRef,
  useEffect,
  forwardRef,
  FunctionComponent,
  ReactNode,
  PropsWithoutRef,
  Ref,
  MouseEvent,
  MouseEventHandler
} from 'react';
import styled, { css, DefaultTheme } from 'styled-components';
import { mix, readableColor } from 'polished';

import { BaseProps, ForwardProps, PropsWithDefaults, AsProp } from '../../types';
import { defaultThemeProp } from '../../theme';
import { tryCatch } from '../../utils';
import { getHoverColors, omitProps, readableHue } from '../../styles';
import { useElement, useConsolidatedRef } from '../../hooks';
import Tooltip from '../Tooltip';
import Progress, { StyledProgressRing } from '../Progress';
import { StyledBackdrop } from '../Backdrop';
import { StyledIcon } from '../Icon';
import { StyledPopover } from '../Popover';

export interface ButtonProps extends BaseProps, AsProp {
  /** Text or content for the Button. */
  children: ReactNode;
  /**
   * Determines if the Button will be disabled.
   * @default false
   */
  disabled?: boolean;
  /** A location to navigate to. Passing an href will render an anchor styled as a Button. */
  href?: string;
  /**
   * Controls the styling of the Button.
   * @default 'secondary'
   */
  variant?: 'primary' | 'secondary' | 'simple' | 'link' | 'text';
  /**
   * Controls the behavior of a Button within a Form.
   * @default 'button'
   */
  type?: 'button' | 'reset' | 'submit';
  /**
   * Set the Icon prop to `true` if you're using just an Icon in your Button. Make sure to pass an Icon as children if `true`.
   * @default false
   */
  icon?: boolean;
  /**
   * Used for a smaller sized button.
   * @default false
   */
  compact?: boolean;
  /** The label text will be used inside a Tooltip. */
  label?: string;
  /** The aria label text will be inserted as the aria-label on the Button. */
  'aria-label'?: string;
  /**
   * Optionally renders an indeterminate progress indicator in a button.
   * @default false
   */
  loading?: boolean;
  /** Ref forwarded to the wrapping element. */
  ref?: Ref<HTMLButtonElement>;
}

type ButtonPropsWithDefaults = PropsWithDefaults<
  ButtonProps,
  'variant' | 'type' | 'disabled' | 'icon' | 'loading'
>;

const baseStyles = (theme: DefaultTheme) => {
  const {
    base: {
      spacing,
      animation: {
        speed,
        timing: { ease }
      },
      'disabled-opacity': disabledOpacity
    },
    components: {
      button: { 'focus-shadow': focusShadow }
    }
  } = theme;

  return css`
    outline: none;
    text-decoration: none;
    transition-property: background-color, color, box-shadow;
    transition-duration: calc(0.5 * ${speed});
    transition-timing-function: ${ease};
    cursor: pointer;

    & + & {
      margin-inline-start: ${spacing};
    }

    /* Not able to combine with selector above. Stylis bug? */
    & + ${StyledPopover} + & {
      margin-inline-start: ${spacing};
    }

    &:disabled,
    &[disabled] {
      opacity: ${disabledOpacity};
      cursor: not-allowed;
      pointer-events: none;
    }

    &:enabled:focus,
    &:not([disabled]):focus {
      box-shadow: ${focusShadow};
    }
  `;
};

export const StyledButton = styled.button.withConfig<ButtonPropsWithDefaults>(omitProps('loading'))(
  ({ variant, icon, loading, compact, theme }) => {
    const {
      base: {
        'border-radius': baseBorderRadius,
        palette: { 'primary-background': backgroundColor },
        'hit-area': {
          'mouse-min': hitAreaMouse,
          'finger-min': hitAreaFinger,
          'compact-min': hitAreaCompact
        },
        animation: { speed }
      },
      components: {
        button: {
          color,
          'secondary-color': secondaryColor,
          padding,
          'border-radius': borderRadius,
          'border-width': borderWidth,
          touch: { padding: touchPadding }
        }
      }
    } = theme;
    const contrastColor = tryCatch(() => readableColor(color));

    // Base
    const base = baseStyles(theme);

    // Non-text base
    const nonTextual = css`
      position: relative;
      display: inline-flex;
      align-items: center;
      justify-content: center;
      min-height: ${hitAreaMouse};
      min-width: ${hitAreaMouse};
      line-height: 1;
      border: ${borderWidth} solid transparent;
      border-radius: calc(${baseBorderRadius} * ${borderRadius});
      -webkit-user-select: none;
      user-select: none;

      ${!icon &&
      css`
        padding: ${padding};
      `}

      ${icon &&
      css`
        border-radius: calc(${baseBorderRadius} * ${borderRadius});

        > svg {
          display: block;
        }
      `}

      ${compact &&
      css`
        min-height: ${hitAreaCompact};
        min-width: ${hitAreaCompact};
      `}

      @media (pointer: coarse) {
        border-radius: calc(${baseBorderRadius} * ${borderRadius});

        ${!compact &&
        css`
          min-height: ${hitAreaFinger};
          min-width: ${hitAreaFinger};
        `}

        ${!icon &&
        css`
          padding: ${touchPadding};
        `}
      }
    `;

    // Primary
    const hoverColors = getHoverColors(color);
    const hoverPrimaryColor = hoverColors.background;
    const hoverContrastColor = hoverColors.foreground;
    const primary = css`
      --button-background-color: ${color};
      color: ${contrastColor};
      background-color: ${color};

      @media (hover: hover) {
        &:hover {
          background-color: ${hoverPrimaryColor};
          color: ${hoverContrastColor};
          text-decoration: none;
        }
      }
    `;

    // Secondary
    const secondaryHoverBackgroundColor = tryCatch(() => mix(0.85, secondaryColor, color));
    const secondaryHoverForegroundColor = readableHue(
      color,
      secondaryHoverBackgroundColor ?? 'transparent'
    );
    const secondary = css`
      --button-background-color: ${secondaryColor};
      color: ${color};
      background-color: ${secondaryColor};
      border-color: ${color};

      @media (hover: hover) {
        &:hover {
          color: ${secondaryHoverForegroundColor};
          background-color: ${secondaryHoverBackgroundColor};
          text-decoration: none;
          border-color: ${secondaryHoverForegroundColor};
        }
      }
    `;

    // Simple
    const simple = css`
      --button-background-color: ${icon ? 'transparent' : backgroundColor};
      color: ${icon ? 'currentColor' : color};
      background-color: ${icon ? 'transparent' : backgroundColor};

      @media (hover: hover) {
        &:hover {
          ${icon &&
          css`
            ::before {
              content: '';
              position: absolute;
              top: calc(${borderWidth} * -1);
              bottom: calc(${borderWidth} * -1);
              left: calc(${borderWidth} * -1);
              right: calc(${borderWidth} * -1);
              border-radius: inherit;
              background-color: currentColor;
              opacity: 0.15;
            }
          `}

          ${!icon &&
          css`
            background-color: ${secondaryHoverBackgroundColor};
            color: ${secondaryHoverForegroundColor};
            border-color: ${secondaryHoverForegroundColor};
            text-decoration: none;
          `}
        }
      }
    `;

    return css`
      ${base}
      ${nonTextual}
      ${variant === 'primary' && primary}
      ${variant === 'secondary' && secondary}
      ${variant === 'simple' && simple}

      ${loading &&
      css`
        ${StyledBackdrop} {
          background-color: var(--button-background-color);
          border-radius: inherit;
        }

        ${StyledProgressRing} {
          width: 1em;
          height: 1em;
          circle:nth-child(2) {
            animation-duration: calc(${speed} * 2);
          }
        }
      `}
    `;
  }
);

StyledButton.defaultProps = defaultThemeProp;

export const StyledButtonLink = styled.a.withConfig<ButtonPropsWithDefaults>(omitProps('loading'))(
  ({ theme, variant }) => {
    const {
      base: {
        palette: { 'foreground-color': textColor }
      },
      components: {
        link: { color: linkColor }
      }
    } = theme;

    const base = baseStyles(theme);

    const textual = css`
      background-color: transparent;
      display: inline;
      text-align: start;
      border: none;

      @media (hover: hover) {
        &:hover {
          text-decoration: underline;
        }
      }

      > ${StyledIcon} {
        vertical-align: bottom;
      }
    `;

    const link = css`
      color: ${linkColor};
    `;

    const text = css`
      color: ${textColor};
    `;

    return css`
      ${base}
      ${textual}
      ${variant === 'link' && link}
      ${variant === 'text' && text}
    `;
  }
);

StyledButtonLink.defaultProps = defaultThemeProp;

const Button: FunctionComponent<ButtonProps & ForwardProps> = forwardRef(
  (
    {
      variant = 'secondary',
      type = 'button',
      disabled = false,
      icon = false,
      compact = false,
      href,
      as,
      forwardedAs,
      label,
      'aria-label': ariaLabel,
      loading = false,
      children,
      ...restProps
    }: PropsWithoutRef<ButtonProps> & ForwardProps,
    ref: Ref<HTMLButtonElement>
  ) => {
    const [buttonEl, setButtonEl] = useElement();
    const mouseDownEvent = useRef<MouseEvent>();
    const buttonRef = useConsolidatedRef<HTMLButtonElement>(ref, setButtonEl);
    const isTextual = ['link', 'text'].includes(variant);
    const showProgress = loading && !isTextual;
    const StyledComp: React.ElementType = isTextual ? StyledButtonLink : StyledButton;

    useEffect(() => {
      return () => {
        mouseDownEvent.current = undefined;
      };
    }, []);

    return (
      <>
        <StyledComp
          {...restProps}
          ref={buttonRef}
          as={as || (href ? 'a' : 'button')}
          forwardedAs={forwardedAs || (href ? 'a' : 'button')}
          variant={variant}
          icon={icon}
          compact={compact}
          type={href ? undefined : type}
          href={href}
          disabled={disabled && !href}
          loading={showProgress}
          aria-label={ariaLabel || label}
          onMouseDown={(e: MouseEvent<HTMLButtonElement | HTMLAnchorElement>) => {
            (restProps.onMouseDown as MouseEventHandler | undefined)?.(e);
            e.persist();
            mouseDownEvent.current = e;
          }}
          onMouseUp={(e: MouseEvent<HTMLButtonElement | HTMLAnchorElement>) => {
            const shouldFocus = mouseDownEvent.current && !mouseDownEvent.current.defaultPrevented;
            mouseDownEvent.current = undefined;

            if (shouldFocus && buttonRef.current !== document.activeElement) {
              buttonRef.current?.focus();
            }

            return (restProps.onMouseUp as MouseEventHandler | undefined)?.(e);
          }}
        >
          {showProgress && <Progress variant='ring' placement='local' />}
          {children}
        </StyledComp>

        {buttonEl && label && (
          <Tooltip target={buttonEl} showDelay='none' hideDelay='none' describeTarget={false}>
            {label}
          </Tooltip>
        )}
      </>
    );
  }
);

export default Button;
