import { forwardRef, FunctionComponent, PropsWithoutRef, Ref } from 'react';
import styled from 'styled-components';

import { useI18n } from '../../hooks';
import { defaultThemeProp } from '../../theme';
import { BaseProps, ForwardProps, NoChildrenProp } from '../../types';

export interface BooleanDisplayProps extends BaseProps, NoChildrenProp {
  /** Boolean value */
  value?: boolean;
  /** The text to be displayed when value is true.
   * @default 'Yes'
   */
  trueLabel?: string;
  /** The text to be displayed when value is false.
   * @default 'No'
   */
  falseLabel?: string;
  /** Ref for the wrapping element. */
  ref?: Ref<HTMLSpanElement>;
}

const StyledBooleanDisplay = styled.span``;

StyledBooleanDisplay.defaultProps = defaultThemeProp;

const BooleanDisplay: FunctionComponent<BooleanDisplayProps & ForwardProps> = forwardRef(
  (
    { value, trueLabel, falseLabel, ...restProps }: PropsWithoutRef<BooleanDisplayProps>,
    ref: BooleanDisplayProps['ref']
  ) => {
    const t = useI18n();

    const displayText = value
      ? trueLabel || t('boolean_display_true_label')
      : falseLabel || t('boolean_display_false_label');

    return (
      <StyledBooleanDisplay ref={ref} {...restProps}>
        {value !== undefined ? displayText : <span aria-hidden='true'>&ndash;&ndash;</span>}
      </StyledBooleanDisplay>
    );
  }
);

export default BooleanDisplay;
