import { forwardRef, FunctionComponent, PropsWithoutRef, Ref } from 'react';
import styled from 'styled-components';

import { BaseProps, ForwardProps } from '../../types';
import { useConfiguration, useI18n } from '../../hooks';

import { DateTimeFormat, DateTimeVariant } from './DateTime.types';
import { ClockFormat, parseToDate } from './Input/utils';
import { isValidDate } from './Picker/utils';
import { formatDateTime } from './utils';

export interface DateTimeDisplayProps extends BaseProps {
  /** Date as ISO8601 string, timestamp or native Date object. */
  value?: Date | string | number;
  /** Controls date time variant. */
  variant: DateTimeVariant;
  /**
   * Controls display format.
   * @default 'short'
   */
  format?: DateTimeFormat;
  /** Defines clock format that overrides locale based format */
  clockFormat?: ClockFormat;
  /** Ref for the wrapping element. */
  ref?: Ref<HTMLSpanElement>;
}

export const StyledDateTimeDisplay = styled.span``;

const DateTimeDisplay: FunctionComponent<DateTimeDisplayProps & ForwardProps> = forwardRef(
  (
    {
      value,
      variant,
      format = 'short',
      clockFormat,
      ...restProps
    }: PropsWithoutRef<DateTimeDisplayProps>,
    ref: DateTimeDisplayProps['ref']
  ) => {
    const { locale } = useConfiguration();
    const t = useI18n();

    return (
      <StyledDateTimeDisplay ref={ref} {...restProps}>
        {(value || value === 0) && isValidDate(parseToDate(value)) ? (
          formatDateTime(parseToDate(value), {
            t,
            locale,
            format,
            variant,
            clockFormat,
            timeZone: 'UTC'
          })
        ) : (
          <span aria-hidden='true'>&ndash;&ndash;</span>
        )}
      </StyledDateTimeDisplay>
    );
  }
);

export default DateTimeDisplay;
