import { forwardRef, FunctionComponent, PropsWithoutRef, Ref } from 'react';

import { BaseProps, ForwardProps, NoChildrenProp } from '../../types';
import Link from '../Link';

import FileItem, { FileItemProps } from './FileItem';

export interface FileDisplayProps
  extends Pick<FileItemProps, 'onPreview' | 'onDownload'>,
    BaseProps,
    NoChildrenProp {
  /** URL address */
  value?: string;
  /** The text to be displayed. */
  displayText?: string;
  /**
   * Display value either as file, link only or textual representation.
   * @default 'file'
   */
  variant?: 'file' | 'link' | 'text';
  /** Thumbnail image. */
  thumbnail?: string;
  /** Ref for the wrapping element. */
  ref?: Ref<HTMLDivElement | HTMLAnchorElement | HTMLSpanElement>;
}

const FileDisplay: FunctionComponent<FileDisplayProps & ForwardProps> = forwardRef(
  (
    {
      displayText,
      value,
      variant,
      thumbnail,
      onPreview,
      onDownload,
      ...restProps
    }: PropsWithoutRef<FileDisplayProps>,
    ref: FileDisplayProps['ref']
  ) => {
    if (variant === 'text' || !value) {
      return (
        <span ref={ref} {...restProps}>
          {value ? displayText || value : <span aria-hidden='true'>&ndash;&ndash;</span>}
        </span>
      );
    }

    if (variant === 'link')
      return (
        <Link ref={ref as Ref<HTMLAnchorElement>} {...restProps} href={value}>
          {displayText || value}
        </Link>
      );

    return (
      <FileItem
        {...restProps}
        ref={ref as Ref<HTMLButtonElement>}
        name={displayText || value}
        url={value}
        {...{
          thumbnail,
          onDownload,
          onPreview
        }}
      />
    );
  }
);

export default FileDisplay;
