import { FC, useContext, useEffect, useRef, useState, MouseEvent as ReactMouseEvent } from 'react';
import styled, { css } from 'styled-components';

import { useDirection, useEscape, useOuterEvent } from '../../hooks';
import Text from '../Text';
import Tooltip from '../Tooltip';
import Popover from '../Popover';
import Menu from '../Menu';
import { StyledMenu, StyledMenuListContainer, StyledMenuListWrapper } from '../Menu/Menu.styles';
import { defaultThemeProp } from '../../theme';
import { calculateFontSize } from '../../styles';

import { NavListItemWrapperProps } from './AppShell.types';
import AppShellContext from './AppShellContext';

const StyledNavigationPopover = styled.div(({ theme }) => {
  const headingFontSize = calculateFontSize(theme.base['font-size'], theme.base['font-scale']).s;
  return css`
    display: flex;
    background-color: ${theme.components['app-shell'].nav['background-color']};

    ${StyledMenu} {
      display: flex;
      flex-direction: column;
    }

    ${StyledMenuListWrapper} {
      overflow: auto;
    }

    &,
    ${StyledMenuListContainer} {
      background-color: ${theme.components['app-shell'].nav['background-color']};

      header {
        color: ${theme.base.palette.light};
        border-bottom: none;
        opacity: 0.7;

        h2 {
          font-weight: ${theme.base['font-weight']['semi-bold']};
          font-size: ${headingFontSize};
        }
      }
    }

    ${StyledMenuListContainer} li {
      color: rgba(255, 255, 255, 0.7);
      background-color: transparent;

      &:hover,
      &[data-current='true'] {
        color: #ffffff;
        background-color: rgba(255, 255, 255, 0.05);
      }
    }
  `;
});

StyledNavigationPopover.defaultProps = defaultThemeProp;

const ListItemWrapper: FC<NavListItemWrapperProps> = props => {
  const {
    children,
    childElementRef,
    label,
    tooltipLabel,
    items = [],
    content,
    onMenuToggle
  } = props;
  const { navOpen, collapsedHoverMenus, headerEl } = useContext(AppShellContext);
  const popoverRef = useRef<HTMLDivElement>(null);
  const [popoverOpen, setPopoverOpen] = useState(false);
  const { end } = useDirection();
  const mousedOverMenu = useRef(false);
  const hidePopoverTimeout = useRef<number | null>();

  const closePopover = (focusChildEl: boolean = true) => {
    if (popoverOpen) {
      setPopoverOpen(false);
      if (focusChildEl) {
        childElementRef.current?.focus();
      }
      if (hidePopoverTimeout.current) {
        clearTimeout(hidePopoverTimeout.current);
        hidePopoverTimeout.current = null;
      }
    }
  };

  const onMouseEnterContent = () => {
    if (collapsedHoverMenus) {
      mousedOverMenu.current = true;
    }
  };

  const onMouseLeaveContent = () => {
    if (mousedOverMenu.current && collapsedHoverMenus) {
      mousedOverMenu.current = false;
      closePopover();
    }
  };

  useOuterEvent('click', [popoverRef], () => closePopover());

  useEffect(() => {
    const onClick = (e: Event) => {
      if (items.length || content) {
        if (!navOpen) {
          e.stopPropagation();
          e.preventDefault();
        }
        if (!navOpen && !popoverOpen) setPopoverOpen(true);
        else {
          setTimeout(() => {
            closePopover(false);
          }, 0);
        }
      }
    };

    const onKeyDown = (e: KeyboardEvent) => {
      if (e.key === 'Tab' && !content) {
        setPopoverOpen(false);
      }
    };

    const onMouseOut = (e: MouseEvent) => {
      if (hidePopoverTimeout.current) {
        clearTimeout(hidePopoverTimeout.current);
        hidePopoverTimeout.current = null;
      }
      const tag = (e.relatedTarget as HTMLElement).tagName;

      // If the mouse is mousing out from this nav item onto another nav item
      if (['A', 'BUTTON', 'FORM'].includes(tag)) {
        closePopover(false);
      } else {
        hidePopoverTimeout.current = window.setTimeout(() => {
          if (!mousedOverMenu.current) {
            closePopover(false);
          }
        }, 1000);
      }
    };

    childElementRef.current?.addEventListener('click', onClick);
    childElementRef.current?.addEventListener('keydown', onKeyDown);
    if (collapsedHoverMenus) {
      childElementRef.current?.addEventListener('mouseenter', onClick);
      childElementRef.current?.addEventListener('mouseleave', onMouseOut);
    }
    return () => {
      childElementRef.current?.removeEventListener('click', onClick);
      childElementRef.current?.removeEventListener('keydown', onKeyDown);
      if (collapsedHoverMenus) {
        childElementRef.current?.removeEventListener('mouseenter', onClick);
        childElementRef.current?.removeEventListener('mouseleave', onMouseOut);
      }
      if (hidePopoverTimeout.current) {
        clearTimeout(hidePopoverTimeout.current);
        hidePopoverTimeout.current = null;
      }
    };
  }, [items, content, navOpen, closePopover, collapsedHoverMenus]);

  useEffect(() => {
    onMenuToggle?.(popoverOpen ? 'open' : 'close');
  }, [popoverOpen]);

  useEscape(() => closePopover(), childElementRef, [childElementRef]);

  useEscape(() => closePopover(), popoverRef, [popoverRef]);

  return (
    <>
      {children}
      {!navOpen && (
        <>
          {!popoverOpen && (
            <Tooltip
              target={childElementRef.current}
              placement={end}
              showDelay='none'
              hideDelay='none'
            >
              {tooltipLabel}
            </Tooltip>
          )}
          {popoverOpen && (items.length || content) && (
            <Popover
              target={childElementRef.current}
              placement={`${end}-start`}
              ref={popoverRef}
              as={StyledNavigationPopover}
              arrow
              modifiers={
                headerEl
                  ? [
                      {
                        name: 'preventOverflow',
                        options: {
                          padding: {
                            top: headerEl.offsetHeight + 8,
                            right: 8,
                            bottom: 8,
                            left: 8
                          }
                        }
                      }
                    ]
                  : undefined
              }
              onClick={(e: ReactMouseEvent<HTMLDivElement>) => {
                e.stopPropagation();
              }}
            >
              {content ? (
                <div onMouseEnter={onMouseEnterContent} onMouseLeave={onMouseLeaveContent}>
                  {content}
                </div>
              ) : (
                <Menu
                  mode='action'
                  variant='flyout'
                  focusControlEl={childElementRef.current ?? undefined}
                  scrollAt={99}
                  items={items.map(item => {
                    return {
                      id: (item.primary || item.text) as string,
                      primary: (item.primary || item.text) as string,
                      href: item.href,
                      onClick: (_, e) => {
                        e.stopPropagation();
                        item.onClick?.(e);
                        setPopoverOpen(false);
                      }
                    };
                  })}
                  header={<Text variant='h2'>{label}</Text>}
                  onMouseEnter={onMouseEnterContent}
                  onMouseLeave={onMouseLeaveContent}
                />
              )}
            </Popover>
          )}
        </>
      )}
    </>
  );
};

export default ListItemWrapper;
