import { forwardRef, FunctionComponent, PropsWithoutRef } from 'react';

import { ForwardProps } from '../../types';
import VisuallyHiddenText from '../VisuallyHiddenText';

import { CreditCardDisplayProps } from './CreditCard.types';
import { formatCreditCard } from './utils';

const CreditCardDisplay: FunctionComponent<CreditCardDisplayProps & ForwardProps> = forwardRef(
  (
    { value, formattingOptions = {}, ...restProps }: PropsWithoutRef<CreditCardDisplayProps>,
    ref: CreditCardDisplayProps['ref']
  ) => {
    const formatted = value ? formatCreditCard(value, formattingOptions) : undefined;

    return (
      <span ref={ref} {...restProps}>
        {formatted ? (
          <>
            <span aria-hidden='true'>{formatted}</span>
            <VisuallyHiddenText>
              {formatted
                .replace(/\s+/g, '.')
                .replace(/([0-9•]\.?)/g, digit => `${digit} `)
                .trim()}
            </VisuallyHiddenText>
          </>
        ) : (
          <span aria-hidden='true'>&ndash;&ndash;</span>
        )}
      </span>
    );
  }
);

export default CreditCardDisplay;
