import {
  forwardRef,
  useState,
  FunctionComponent,
  PropsWithoutRef,
  Ref,
  ReactNode,
  useRef,
  ReactElement,
  useContext,
  useEffect
} from 'react';
import styled, { css } from 'styled-components';
import { readableColor } from 'polished';

import { defaultThemeProp } from '../../theme';
import { BaseProps, ForwardProps, NoChildrenProp, AsProp, OmitStrict } from '../../types';
import Grid from '../Grid';
import Text from '../Text';
import SummaryItem from '../SummaryItem';
import Icon from '../Icon';
import { focusHeadingOrContainer, tryCatch } from '../../utils';
import Tabs, { TabPanel, TabsProps, Tab } from '../Tabs';
import Flex from '../Flex';
import {
  useBreakpoint,
  useElement,
  useScrollStick,
  useConsolidatedRef,
  useConfiguration
} from '../../hooks';
import type { ScrollStickOptions } from '../../hooks/useScrollStick';
import Breadcrumbs, { BreadcrumbsProps, StyledBreadcrumbs } from '../Breadcrumbs';
import AppShellContext from '../AppShell/AppShellContext';
import { StyledBanner } from '../Banner/Banner';
import { StyledTabs } from '../Tabs/Tabs';

export interface PageTemplateProps extends BaseProps, AsProp, NoChildrenProp {
  title: string;
  icon?: string;
  actions?: ReactNode;
  banners?: ReactNode;
  path?: BreadcrumbsProps['path'];
  header?: ReactNode;
  scrollContent?: boolean;
  ref?: Ref<HTMLDivElement>;
}

export interface TabbedPageTab extends Tab {
  content: ReactNode;
}

export interface TabbedPageProps extends PageTemplateProps {
  tabs: OmitStrict<TabsProps, 'tabs' | 'currentTabId'> & {
    tabs: TabbedPageTab[];
    defaultTabId?: string;
  };
}

export interface OneColumnPageProps extends PageTemplateProps {
  a: ReactNode;
}

export interface TwoColumnPageProps extends PageTemplateProps {
  a: ReactNode;
  b: ReactNode;
}

export interface ThreeColumnPageProps extends PageTemplateProps {
  a: ReactNode;
  b: ReactNode;
  c: ReactNode;
}

export interface FourColumnPageProps extends PageTemplateProps {
  a: ReactNode;
  b: ReactNode;
  c: ReactNode;
  d: ReactNode;
}

export interface WideNarrowPageProps extends PageTemplateProps {
  a: ReactNode;
  b: ReactNode;
}

export interface NarrowWidePageProps extends PageTemplateProps {
  a: ReactNode;
  b: ReactNode;
}

export interface WideWideNarrowPageProps extends PageTemplateProps {
  a: ReactNode;
  b: ReactNode;
  c: ReactNode;
}

export interface NarrowWideWidePageProps extends PageTemplateProps {
  a: ReactNode;
  b: ReactNode;
  c: ReactNode;
}

export interface NarrowWideNarrowPageProps extends PageTemplateProps {
  a: ReactNode;
  b: ReactNode;
  c: ReactNode;
}

export interface PageLayoutProps extends PageTemplateProps {
  tabs?: ReactElement<typeof Tabs>;
  regions: ReactNode[];
  regionsRef?: Ref<HTMLDivElement>;
  cols?: string;
}

export const StyledPageIcon = styled(Flex)(({ theme }) => {
  const bgColor = theme.base.palette['brand-primary'];
  const color = tryCatch(() => readableColor(bgColor));
  return css`
    height: calc(4 * ${theme.base.spacing});
    width: calc(4 * ${theme.base.spacing});
    background: ${bgColor};
    color: ${color};
    border-radius: calc(0.5 * ${theme.base['border-radius']});
  `;
});

StyledPageIcon.defaultProps = defaultThemeProp;

export const StyledPageHeader = styled.header(({ theme }) => {
  return css`
    h1:focus-visible {
      outline: transparent;
      box-shadow: ${theme.base.shadow.focus};
    }

    ${StyledBreadcrumbs} {
      margin-block-end: ${theme.base.spacing};
    }

    ${StyledBanner},
    ${StyledTabs} {
      margin-block-start: calc(2 * ${theme.base.spacing});
    }
  `;
});

StyledPageHeader.defaultProps = defaultThemeProp;

export const StyledRegion = styled.div``;
StyledRegion.defaultProps = defaultThemeProp;

export const StyledRegions = styled.div<{ pad: boolean }>(({ pad, theme }) => {
  const spacing = theme.base.spacing;

  return css`
    flex-grow: 1;
    max-width: 100%;

    ${pad &&
    css`
      padding: 0 calc(2 * ${spacing}) calc(2 * ${spacing});
    `}
  `;
});
StyledRegions.defaultProps = defaultThemeProp;

export const StyledContent = styled.div``;
StyledContent.defaultProps = defaultThemeProp;

export const StyledScrollableTabPanel: typeof TabPanel = styled(TabPanel)(() => {
  return css`
    height: 100%;
    overflow: auto;
  `;
});

StyledScrollableTabPanel.defaultProps = defaultThemeProp;

export const StyledPageLayout = styled.div<{
  scrollContent: PageLayoutProps['scrollContent'];
  minContentHeight?: number;
}>(({ theme, scrollContent, minContentHeight }) => {
  const emptyContentHeight = minContentHeight ? `${minContentHeight}px` : undefined;

  return css`
    --content-height-in-view: ${emptyContentHeight};
    position: relative;
    min-height: inherit;
    background-color: ${theme.base.palette['app-background']};

    ${scrollContent &&
    css`
      ${StyledContent} {
        position: relative;
      }

      ${StyledRegions} {
        position: absolute;
        height: 100%;
        width: 100%;
        overflow: auto;
      }
    `}
  `;
});

StyledPageLayout.defaultProps = defaultThemeProp;

const PageLayout: FunctionComponent<PageLayoutProps & ForwardProps> = forwardRef(
  (
    {
      regions,
      cols = `repeat(${regions.length}, minmax(0, 1fr))`,
      title,
      icon,
      path,
      actions,
      banners,
      tabs,
      as,
      header,
      regionsRef,
      scrollContent = false,
      ...restProps
    }: PropsWithoutRef<PageLayoutProps>,
    ref: PageLayoutProps['ref']
  ) => {
    const pageRef = useConsolidatedRef(ref);
    const [contentEl, setContentEl] = useElement<HTMLElement>();
    const isMediumOrAbove = useBreakpoint('md');
    const hasSideBySideColumns = regions.length > 1 && isMediumOrAbove;
    const { headerEl } = useContext(AppShellContext);
    const { loadedRef } = useConfiguration();

    const consolidatedRegionRef = useConsolidatedRef(regionsRef);
    const scrollStickOptions = useRef<ScrollStickOptions & { elements: HTMLElement[] }>({
      elements: []
    });

    scrollStickOptions.current.scrollContainer = scrollContent && contentEl ? contentEl : undefined;
    scrollStickOptions.current.offset = (headerEl?.offsetHeight ?? 0) + 16;

    useScrollStick(hasSideBySideColumns ? scrollStickOptions.current : undefined);

    const renderSingleRegion = regions?.length === 1;

    const [minContentHeight, setMinContentHeight] = useState<number | undefined>();

    useEffect(() => {
      if (!pageRef.current) return;

      if (loadedRef.current) {
        focusHeadingOrContainer(pageRef.current, title);
      } else {
        loadedRef.current = true;
      }
    }, [title]);

    useEffect(() => {
      if (consolidatedRegionRef.current) {
        const refElement = consolidatedRegionRef.current;
        const regionsPaddingBottomPixels = Number.parseInt(
          window.getComputedStyle(refElement).getPropertyValue('padding-bottom'),
          10
        );

        const resizeObserver = new ResizeObserver(() => {
          const headerOffset = headerEl?.offsetHeight ?? 0;
          const emptyHeight =
            document.documentElement.offsetHeight -
            (headerOffset + refElement.offsetTop) -
            regionsPaddingBottomPixels;

          setMinContentHeight(emptyHeight);
        });

        resizeObserver.observe(refElement);

        return () => {
          resizeObserver.disconnect();
        };
      }
    }, [consolidatedRegionRef, headerEl]);

    return (
      <Flex
        {...restProps}
        container={{
          direction: 'column',
          alignItems: 'stretch',
          gap: 2
        }}
        scrollContent={scrollContent}
        as={StyledPageLayout}
        forwardedAs={as}
        ref={pageRef}
        minContentHeight={minContentHeight}
      >
        <Flex
          container={{ direction: 'column', pad: [2, 2, 0] }}
          item={{ grow: 0, shrink: 0 }}
          as={StyledPageHeader}
        >
          {path && path.length > 0 && <Breadcrumbs path={...path} />}

          {header || (
            <SummaryItem
              visual={
                icon && (
                  <StyledPageIcon container={{ alignItems: 'center', justify: 'center' }}>
                    <Icon name={icon} />
                  </StyledPageIcon>
                )
              }
              primary={<Text variant='h1'>{title}</Text>}
              actions={actions}
            />
          )}

          {banners}
          {tabs}
        </Flex>

        <Flex
          container
          item={{ grow: 1 }}
          as={StyledContent}
          ref={scrollContent ? setContentEl : undefined}
        >
          {renderSingleRegion && (
            <StyledRegions ref={consolidatedRegionRef} pad>
              <Flex
                container={{
                  direction: 'column',
                  gap: 2
                }}
                as={StyledRegion}
                ref={(el: HTMLDivElement | null) => {
                  if (el) {
                    scrollStickOptions.current.elements[0] = el;
                  }
                }}
              >
                {regions[0]}
              </Flex>
            </StyledRegions>
          )}

          {!renderSingleRegion && (
            <Grid
              container={{
                cols: 'minmax(0, 1fr)',
                alignItems: 'start',
                gap: 2,
                pad: [0, 2, 2]
              }}
              md={{ container: { cols } }}
              as={StyledRegions}
              ref={consolidatedRegionRef}
            >
              {regions.map((colItems, i) => {
                return (
                  <Flex
                    container={{
                      direction: 'column',
                      gap: 2
                    }}
                    as={StyledRegion}
                    // eslint-disable-next-line react/no-array-index-key
                    key={i}
                    ref={(el: HTMLDivElement | null) => {
                      if (el) {
                        scrollStickOptions.current.elements[i] = el;
                      }
                    }}
                  >
                    {colItems}
                  </Flex>
                );
              })}
            </Grid>
          )}
        </Flex>
      </Flex>
    );
  }
);

export const TabbedPage: FunctionComponent<TabbedPageProps & ForwardProps> = forwardRef(
  ({ tabs, ...restProps }: PropsWithoutRef<TabbedPageProps>, ref: Ref<HTMLDivElement>) => {
    const [tabId, setTabId] = useState(tabs.defaultTabId || tabs.tabs[0]?.id);

    const tabPanels = tabs.tabs.map(tab => {
      return (
        <Flex
          container={{ direction: 'column', gap: 2 }}
          as={TabPanel}
          tabId={tab.id}
          currentTabId={tabId}
          key={tab.id}
        >
          {tab.content}
        </Flex>
      );
    });

    return (
      <PageLayout
        {...restProps}
        tabs={
          <Tabs
            {...tabs}
            onTabClick={(id, e) => {
              setTabId(id);
              tabs.onTabClick?.(id, e);
            }}
            currentTabId={tabId}
          />
        }
        regions={[<>{tabPanels}</>]}
        ref={ref}
      />
    );
  }
);

export const OneColumnPage: FunctionComponent<OneColumnPageProps & ForwardProps> = forwardRef(
  ({ a, ...restProps }: PropsWithoutRef<OneColumnPageProps>, ref: Ref<HTMLDivElement>) => {
    return <PageLayout regions={[a]} ref={ref} {...restProps} cols='minmax(0, 1fr)' />;
  }
);

export const TwoColumnPage: FunctionComponent<TwoColumnPageProps & ForwardProps> = forwardRef(
  ({ a, b, ...restProps }: PropsWithoutRef<TwoColumnPageProps>, ref: Ref<HTMLDivElement>) => {
    return (
      <PageLayout regions={[a, b]} ref={ref} {...restProps} cols='repeat(2, minmax(0, 1fr))' />
    );
  }
);

export const ThreeColumnPage: FunctionComponent<ThreeColumnPageProps & ForwardProps> = forwardRef(
  ({ a, b, c, ...restProps }: PropsWithoutRef<ThreeColumnPageProps>, ref: Ref<HTMLDivElement>) => {
    return (
      <PageLayout regions={[a, b, c]} ref={ref} {...restProps} cols='repeat(3, minmax(0, 1fr))' />
    );
  }
);

export const FourColumnPage: FunctionComponent<FourColumnPageProps & ForwardProps> = forwardRef(
  (
    { a, b, c, d, ...restProps }: PropsWithoutRef<FourColumnPageProps>,
    ref: Ref<HTMLDivElement>
  ) => {
    return (
      <PageLayout
        regions={[a, b, c, d]}
        ref={ref}
        {...restProps}
        cols='repeat(4, minmax(0, 1fr))'
      />
    );
  }
);

export const WideNarrowPage: FunctionComponent<WideNarrowPageProps & ForwardProps> = forwardRef(
  ({ a, b, ...restProps }: PropsWithoutRef<WideNarrowPageProps>, ref: Ref<HTMLDivElement>) => {
    return (
      <PageLayout regions={[a, b]} ref={ref} {...restProps} cols='minmax(0, 2fr) minmax(0, 1fr)' />
    );
  }
);

export const NarrowWidePage: FunctionComponent<WideNarrowPageProps & ForwardProps> = forwardRef(
  ({ a, b, ...restProps }: PropsWithoutRef<WideNarrowPageProps>, ref: Ref<HTMLDivElement>) => {
    return (
      <PageLayout regions={[a, b]} ref={ref} {...restProps} cols='minmax(0, 1fr) minmax(0, 2fr)' />
    );
  }
);

export const WideWideNarrowPage: FunctionComponent<WideWideNarrowPageProps & ForwardProps> =
