import { forwardRef, FunctionComponent, PropsWithoutRef, Ref } from 'react';
import styled, { css } from 'styled-components';

import Flex from '../Flex';
import { useI18n } from '../../hooks';
import { defaultThemeProp } from '../../theme';
import { BaseProps, ForwardProps, NoChildrenProp } from '../../types';
import Icon, { StyledIcon } from '../Icon';

export interface SentimentProps extends BaseProps, NoChildrenProp {
  /** Ref to the element */
  ref?: Ref<HTMLDivElement>;
  /** Variant of the sentiment */
  variant: 'positive' | 'neutral' | 'negative';
  /** Flag that indicates if label needs to be hidden
   * @default false
   */
  labelHidden?: boolean;
  /** Labels for different sentiment variants. If not supplied default labels will be shown */
  labels?: {
    [variant in SentimentProps['variant']]?: string;
  };
  /** Icons for different sentiment variants. If not supplied default icons will be shown */
  icons?: {
    [variant in SentimentProps['variant']]?: string;
  };
  /** Aria label for screen readers. */
  'aria-label'?: string;
}

const defaultIcons: SentimentProps['icons'] = {
  negative: 'face-sad',
  positive: 'face-happy',
  neutral: 'face-blank'
};

export const StyledSentiment = styled.div<SentimentProps>(props => {
  const { theme, variant } = props;
  return css`
    > ${StyledIcon} {
      color: ${theme.components.sentiment[variant].color};
    }
  `;
});

StyledSentiment.defaultProps = defaultThemeProp;

const Sentiment: FunctionComponent<SentimentProps & ForwardProps> = forwardRef(
  (props: PropsWithoutRef<SentimentProps>, ref: SentimentProps['ref']) => {
    const t = useI18n();

    const defaultLabels: SentimentProps['labels'] = {
      negative: t('sentiment_negative'),
      positive: t('sentiment_positive'),
      neutral: t('sentiment_neutral')
    };

    const {
      'aria-label': ariaLabel,
      variant,
      labelHidden = false,
      labels = {},
      icons = {},
      ...restProps
    } = props;

    return (
      <Flex
        aria-label={ariaLabel}
        ref={ref}
        variant={variant}
        role='img'
        {...restProps}
        as={StyledSentiment}
        container={{ gap: 1 }}
      >
        <Icon aria-hidden='true' name={icons[variant] || defaultIcons[variant]!} />
        {!labelHidden && <span>{labels[variant] || defaultLabels[variant]}</span>}
      </Flex>
    );
  }
);

export default Sentiment;
