/** @returns Throttled function that can be called at most once in given waitTime(ms). */
const throttle = <F extends (...args: any[]) => any>(
  fn: F,
  waitTime: number
): ((...args: Parameters<F>) => void) => {
  let paused = false;

  return function throttled(this: any, ...args) {
    if (paused) return;

    paused = true;

    window.setTimeout(() => {
      fn.apply(this, args);

      paused = false;
    }, waitTime);
  };
};

export default throttle;
