import {
  ClipboardEvent,
  forwardRef,
  FunctionComponent,
  MouseEvent,
  PropsWithoutRef,
  Ref,
  RefObject,
  useEffect,
  useImperativeHandle,
  useRef
} from 'react';
import styled, { css } from 'styled-components';

import { ForwardProps } from '../../../types';
import { FormControlProps, StyledFormControl } from '../../FormControl';
import FormField from '../../FormField';
import {
  useConfiguration,
  useConsolidatedRef,
  useFocusWithin,
  useI18n,
  useOuterEvent
} from '../../../hooks';
import Select, { Option } from '../../Select';
import Flex from '../../Flex';
import Button from '../../Button';
import Icon, { registerIcon } from '../../Icon';
import * as calendarIcon from '../../Icon/icons/calendar.icon';
import DatePicker from '../Picker/DatePicker';
import { MinuteInterval } from '../Picker/DatePicker.types';
import DateTimeProps, { DateTimeCallbackParameter } from '../DateTime.types';
import { hasProp, cap } from '../../../utils';
import Dialog from '../../Dialog/Dialog';

import StyledDateTime, {
  StyledClearNowButtonsContainer,
  StyledDialogContent,
  StyledInputContainer
} from './DateTime.styles';
import {
  ClockFormat,
  generatePickerButtonAriaLabel,
  getDateFormat,
  getTimeOptions,
  is12HClockFormat,
  parsePastedTextToDate,
  parseToDate,
  validateDate,
  validateTime
} from './utils';
import useAutoFocusNextInput from './useAutoFocusNextInput';
import PartInput from './PartInput';
import usePickerButton from './usePickerButton';
import { DateParts, DateTimeParts, TimeParts } from './DateTimeInput.types';

registerIcon(calendarIcon);

export interface DateTimeInputProps extends DateTimeProps {
  /**
   * Display the time with seconds.
   * @default false
   */
  withSeconds?: boolean;
  /**
   * Display additional column with week number in date picker popover.
   * @default false
   */
  showWeekNumber?: boolean;
  /**
   * Defines interval for time picker options (in minutes).
   * @default 30
   */
  pickerInterval?: MinuteInterval;
  /** Defines clock format that overrides locale based format. */
  clockFormat?: ClockFormat;
  /** Pass a heading and content to show additional information on the field. */
  additionalInfo?: FormControlProps['additionalInfo'];
}

export const StyledDateTimeInput = styled(StyledDateTime)<DateTimeInputProps>(() => {
  return css`
    padding-inline-end: 0;
    min-width: 0;
  `;
});

const StyledDateTimeFieldset = styled.fieldset`
  min-width: 0;
`;

function getMeridiem(hour: number) {
  return hour >= 12 ? 'PM' : 'AM';
}

const convertTo12h = (hour: number) => {
  let converted: number = hour;
  if (hour > 12) converted = hour - 12;
  else if (hour === 0) converted = 12;
  return converted;
};

const convertTo24h = (hour: number, meridiem: string) => {
  let converted: number = hour;
  if (meridiem === 'PM' && hour < 12) converted = hour + 12;
  else if (meridiem === 'AM' && hour === 12) converted = 0;
  return converted;
};

const validateDateTime = (
  year: number,
  month: number,
  day: number,
  hour: number,
  minute: number,
  second: number,
  is12h: boolean
) => {
  return validateDate(year, month, day) && validateTime(hour, minute, second, is12h);
};

const convertToCallbackParameter = (
  date: Date,
  parts: DateTimeParts<string | undefined>
): DateTimeCallbackParameter => {
  const withSeconds = hasProp(parts, 'second');
  const {
    year,
    month: monthIndex,
    day,
    hour,
    minute,
    second = date.getUTCSeconds().toString(),
    dayPeriod: meridiem
  } = parts;
  const toValidate = withSeconds
    ? [year, monthIndex, day, hour, minute, second]
    : [year, monthIndex, day, hour, minute];

  if (toValidate.every(Boolean)) {
    const is12h = meridiem !== undefined;

    if (
      !validateDateTime(
        Number(year),
        Number(monthIndex) + 1,
        Number(day),
        Number(hour),
        Number(minute),
        Number(second),
        is12h
      )
    ) {
      return {
        valueAsISOString: '',
        valueAsTimestamp: NaN,
        state: 'invalid'
      };
    }

    const resultDate = new Date(date);
    resultDate.setUTCFullYear(Number(year), Number(monthIndex), Number(day));
    resultDate.setUTCHours(
      is12h ? convertTo24h(Number(hour), meridiem!) : Number(hour),
      Number(minute),
      Number(second)
    );

    return {
      valueAsISOString: resultDate.toISOString(),
      valueAsTimestamp: resultDate.getTime()
    };
  }
  if (toValidate.every(p => !p)) {
    return {
      valueAsISOString: undefined,
      valueAsTimestamp: undefined
    };
  }
  return {
    valueAsISOString: '',
    valueAsTimestamp: NaN,
    state: 'incomplete'
  };
};

function dateOptions() {
  return {
    year: 'numeric',
    month: 'numeric',
    day: 'numeric'
  } as const;
}

const DateTimeInput: FunctionComponent<DateTimeInputProps & ForwardProps> = forwardRef(
  (props: PropsWithoutRef<DateTimeInputProps>, ref: Ref<HTMLFieldSetElement>) => {
    const { locale } = useConfiguration();
    const {
      value,
      min,
      max,
      id,
      label,
      labelHidden,
      withSeconds = false,
      clockFormat,
      info = `${getDateFormat(locale).join('')} ${getDateFormat(
        locale,
        getTimeOptions(withSeconds),
        true
      ).join('')}`,
      status,
      required,
      readOnly,
      disabled,
      showWeekNumber,
      pickerInterval = 30,
      onChange,
      onFocus,
      onBlur,
      onResolveSuggestion,
      additionalInfo,
      autoFocus,
      handle,
      ...restProps
    } = props;

    const t = useI18n();
    const containerRef = useConsolidatedRef(ref);

    const dateTime = value !== undefined ? parseToDate(value) : undefined;
    const is12h = clockFormat ? clockFormat === 12 : is12HClockFormat(locale);

    const [
      yearInputRef,
      monthInputRef,
      dayInputRef,
      hourInputRef,
      minuteInputRef,
      secondInputRef,
      meridiemSelectRef
    ] = [
      useRef<HTMLInputElement>(null),
      useRef<HTMLInputElement>(null),
      useRef<HTMLInputElement>(null),
      useRef<HTMLInputElement>(null),
      useRef<HTMLInputElement>(null),
      useRef<HTMLInputElement>(null),
      useRef<HTMLSelectElement>(null)
    ];

    useAutoFocusNextInput([
      yearInputRef,
      monthInputRef,
      dayInputRef,
      hourInputRef,
      minuteInputRef,
      secondInputRef
    ]);

    const setMeridiem = (meridiem: string) => {
      if (meridiemSelectRef.current) {
        meridiemSelectRef.current.value = meridiem;
      }
    };

    useEffect(() => {
      if (is12h && meridiemSelectRef.current && dateTime?.getUTCHours()) {
        meridiemSelectRef.current.value = getMeridiem(dateTime?.getUTCHours());
      }
    }, [is12h, dateTime?.getUTCHours()]);

    const pickDateParts = (): DateParts<string | undefined> => {
      return {
        day: dayInputRef.current?.value || undefined,
        month: monthInputRef.current?.value
          ? (Number(monthInputRef.current.value) - 1).toString()
          : undefined,
        year: yearInputRef.current?.value || undefined
      };
    };

    const pickTimeParts = (): TimeParts<string | undefined> => {
      const parts: TimeParts<string | undefined> = {
        hour: hourInputRef.current?.value || undefined,
        minute: minuteInputRef.current?.value || undefined
      };
      if (withSeconds) parts.second = secondInputRef.current?.value;
      if (is12h) parts.dayPeriod = meridiemSelectRef.current?.value;
      return parts;
    };

    const pickParts = (): DateTimeParts<string | undefined> => {
      return { ...pickDateParts(), ...pickTimeParts() };
    };

    const currentlySelectedDate = () => {
      const parts = pickDateParts();
      if (Object.values(parts).every(Boolean)) {
        const year = Number(parts.year);
        const month = Number(parts.month);
        const day = Number(parts.day);

        return validateDate(year, month + 1, day)
          ? new Date(Date.UTC(year, month, day))
          : undefined;
      }
    };

    const currentlySelectedTime = () => {
      const parts = pickParts();

      if (Object.values(parts).every(Boolean)) {
        const hour = Number(parts.hour);
        const minute = Number(parts.minute);
        const second = parts.second ? Number(parts.second) : 0;

        const current = dateTime ?? new Date();
        return validateTime(hour, minute, second, is12h)
          ? new Date(
              current.setUTCHours(
                is12h ? convertTo24h(hour, parts.dayPeriod!) : hour,
                minute,
                second
              )
            )
          : undefined;
      }
    };

    const currentlySelectedDateTime = () => {
      const selectedDate = currentlySelectedDate();
      const selectedTime = currentlySelectedTime();

      return selectedDate && selectedTime
        ? new Date(
            selectedDate.setUTCHours(
              selectedTime.getUTCHours(),
              selectedTime.getUTCMinutes(),
              selectedTime.getUTCSeconds()
            )
          )
        : undefined;
    };

    const setDefaultTimeIfEmpty = () => {
      // set value to midnight if time empty, skip if date not fully set
      if (
        Object.values(pickDateParts()).every(Boolean) &&
        Object.values(pickTimeParts()).every(p => !p)
      ) {
        if (hourInputRef.current) hourInputRef.current.value = is12h ? '12' : '00';

        if (minuteInputRef.current) minuteInputRef.current.value = '00';

        if (withSeconds && secondInputRef.current) secondInputRef.current.value = '00';

        if (is12h && meridiemSelectRef.current) {
          meridiemSelectRef.current.value = 'AM';
          setMeridiem('AM');
        }
      }
    };

    const [open, setOpen, , setPopoverRef, buttonEl, setButtonEl] =
      usePickerButton(setDefaultTimeIfEmpty);

    const onInputChange = () => {
      onChange?.(
        convertToCallbackParameter(dateTime ? new Date(dateTime) : new Date(), pickParts())
      );
      buttonEl?.setAttribute(
        'aria-label',
        generatePickerButtonAriaLabel(currentlySelectedDateTime(), 'datetime', locale, t, open)
      );
    };

    const setInputValue = (newValue: Date | undefined) => {
      dayInputRef.current!.value = newValue?.getUTCDate().toString().padStart(2, '0') ?? '';
      monthInputRef.current!.value =
        (newValue && (newValue.getUTCMonth() + 1).toString().padStart(2, '0')) || '';
      yearInputRef.current!.value = newValue?.getUTCFullYear().toString() ?? '';

      if (newValue) {
        const hours = newValue.getUTCHours();
        hourInputRef.current!.value = (is12h ? convertTo12h(hours) : hours)
          .toString()
          .padStart(2, '0');
      } else {
        hourInputRef.current!.value = '';
      }

      minuteInputRef.current!.value = newValue?.getUTCMinutes().toString().padStart(2, '0') ?? '';
      if (withSeconds) {
        secondInputRef.current!.value = newValue?.getUTCSeconds().toString().padStart(2, '0') ?? '';
      }
      if (meridiemSelectRef.current) {
        meridiemSelectRef.current!.value = newValue ? getMeridiem(newValue.getUTCHours()) : 'AM';
      }

      setMeridiem(newValue ? getMeridiem(newValue.getUTCHours()) : 'AM');
    };

    const onPaste = (e: ClipboardEvent) => {
      const text = e.clipboardData.getData('text');
      const date = parsePastedTextToDate(text, locale, dateTime || new Date());
      if (date) {
        e.preventDefault();
        setInputValue(date);
        onInputChange();
      }
    };

    const inputContainerRef = useRef<HTMLDivElement>(null);

    const onFocusChange = (focused: boolean) => {
      const callbackParam = convertToCallbackParameter(
        dateTime ? new Date(dateTime) : new Date(),
        pickParts()
      );
      if (onFocus && focused) onFocus(callbackParam);
      if (!focused) {
        if (inputContainerRef.current) inputContainerRef.current.scrollTop = 0;
        onBlur?.(callbackParam);
      }
    };

    // autoclose picker on focusing out
    useOuterEvent('focusin', [containerRef], e => {
      if (containerRef.current?.contains(e.relatedTarget as HTMLElement)) setOpen(false);
    });

    useImperativeHandle(
      handle,
      () => ({
        clear: () => {
          if (dayInputRef.current) dayInputRef.current.value = '';
          if (monthInputRef.current) monthInputRef.current.value = '';
          if (yearInputRef.current) yearInputRef.current.value = '';
          if (hourInputRef.current) hourInputRef.current.value = '';
          if (minuteInputRef.current) minuteInputRef.current.value = '';
          if (secondInputRef.current) secondInputRef.current.value = '';
          if (meridiemSelectRef.current) meridiemSelectRef.current.value = '';
          onInputChange();
        }
      }),
      [onInputChange]
    );

    const displayNames = new Intl.DisplayNames(locale, { style: 'long', type: 'dateTimeField' });

    const DayInput = (
      <PartInput
        key='day'
        placeholder={t('day_placeholder')}
        aria-label={displayNames.of('day')}
        value={dateTime?.getUTCDate()}
        onChange={onInputChange}
        size={2}
        maxLength={2}
        readOnly={readOnly}
        min={1}
        max={31}
        ref={dayInputRef}
      />
    );

    const MonthInput = (
      <PartInput
        key='month'
        placeholder={t('month_placeholder')}
        aria-label={displayNames.of('month')}
        value={dateTime ? dateTime.getUTCMonth() + 1 : undefined}
        onChange={onInputChange}
        size={2}
        maxLength={2}
        readOnly={readOnly}
        min={1}
        max={12}
        ref={monthInputRef}
      />
    );

    const YearInput = (
      <PartInput
        key='year'
        placeholder={t('year_placeholder')}
        aria-label={displayNames.of('year')}
        value={dateTime?.getUTCFullYear()}
        onChange={onInputChange}
        size={4}
        maxLength={4}
        padWithZeros={false}
        readOnly={readOnly}
        min={1}
        ref={yearInputRef}
      />
    );

    const hour = dateTime && is12h ? convertTo12h(dateTime.getUTCHours()) : dateTime?.getUTCHours();

    const HourInput = (
      <PartInput
        key='hour'
        placeholder={t('hour_placeholder')}
        aria-label={displayNames.of('hour')}
        value={hour}
        onChange={onInputChange}
        size={2}
        maxLength={2}
        readOnly={readOnly}
        min={is12h ? 1 : 0}
        max={is12h ? 12 : 23}
        ref={hourInputRef}
      />
    );

    const MinuteInput = (
      <PartInput
        key='minute'
        placeholder={t('minute_placeholder')}
        aria-label={displayNames.of('minute')}
        value={dateTime?.getUTCMinutes()}
        onChange={onInputChange}
        size={2}
        maxLength={2}
        readOnly={readOnly}
        max={59}
        ref={minuteInputRef}
      />
    );

    const SecondInput = (
      <PartInput
        key='second'
        placeholder={t('second_placeholder')}
        aria-label={displayNames.of('second')}
        value={dateTime?.getUTCSeconds()}
        onChange={onInputChange}
        size={2}
        maxLength={2}
        readOnly={readOnly}
        max={59}
        ref={secondInputRef}
      />
    );

    const Period = (
      <Select
        ref={meridiemSelectRef}
        aria-label={displayNames.of('dayPeriod')}
        key='period'
        required={required}
        disabled={disabled}
        onChange={() => {
          if (Object.values(pickParts()).every(Boolean)) onInputChange();
        }}
        readOnly={readOnly}
      >
        <Option value='AM'>{t('meridiem_value_am')}</Option>
        <Option value='PM'>{t('meridiem_value_pm')}</Option>
      </Select>
    );

    const orderedRefs: RefObject<HTMLInputElement>[] = [];

    const dateInputs = getDateFormat(locale, dateOptions()).map(part => {
      switch (part) {
        case 'DD':
          orderedRefs.push(dayInputRef);
          return DayInput;
        case 'MM':
          orderedRefs.push(monthInputRef);
          return MonthInput;
        case 'YYYY':
          orderedRefs.push(yearInputRef);
          return YearInput;
        default:
          return part;
      }
    });

    const timeInputs = getDateFormat(locale, getTimeOptions(withSeconds, is12h)).map(part => {
      switch (part) {
        case 'hh':
          orderedRefs.push(hourInputRef);
          return HourInput;
        case 'mm':
          orderedRefs.push(minuteInputRef);
          return MinuteInput;
        case 'ss':
          orderedRefs.push(secondInputRef);
          return SecondInput;
        case 'AM':
        case 'PM':
          return Period;
        default:
          return part;
      }
    });

    useEffect(() => {
      if (autoFocus) orderedRefs[0].current?.focus();
    }, [autoFocus]);

    useFocusWithin([containerRef], onFocusChange);

    const Picker = (
      <>
        <Button
          variant='simple'
          icon
          ref={setButtonEl}
          onClick={() => {
            setOpen(cur => !cur);
          }}
          aria-label={generatePickerButtonAriaLabel(
            currentlySelectedDateTime(),
            'datetime',
            locale,
            t,
            open
          )}
          readOnly={readOnly}
          disabled={disabled}
        >
          <Icon name='calendar' />
        </Button>
        {open && buttonEl && (
          <Dialog
            ref={setPopoverRef}
            target={buttonEl}
            placement='bottom-end'
            ariaLabel={t('choose_date_and_time')}
          >
            <Flex container={{ direction: 'column' }} as={StyledDialogContent}>
              <DatePicker
                mode='datetime'
                selected={currentlySelectedDateTime()}
                onChange={selected => {
                  setInputValue(selected);
                  onInputChange();
                }}
                min={min}
                max={max}
                showWeekNumber={showWeekNumber}
                pickerInterval={pickerInterval}
                withSeconds={withSeconds}
                clockFormat={clockFormat}
              />
              <Flex as={StyledClearNowButtonsContainer} container={{ justify: 'between' }}>
                <Button
                  aria-label={t('clear_current_date_and_time')}
                  onClick={() => {
                    setInputValue(undefined);
                    setOpen(false);
                    setTimeout(() => buttonEl?.focus());
                    onInputChange();
                  }}
                >
                  {t('clear')}
                </Button>
                <Button
                  aria-label={t('set_current_date_and_time_a11y')}
                  onClick={() => {
                    const now = new Date();
                    now.setUTCMinutes(now.getMinutes());
                    now.setUTCHours(now.getHours());
                    now.setUTCSeconds(now.getSeconds());

                    setInputValue(now);
                    setOpen(false);
                    setTimeout(() => buttonEl?.focus());
                    onInputChange();
                  }}
                >
                  {cap(
                    new Intl.RelativeTimeFormat(locale, { numeric: 'auto' }).format(0, 'second')
                  )}
                </Button>
              </Flex>
            </Flex>
          </Dialog>
        )}
      </>
    );

    const Comp = (
      <Flex
        as={StyledDateTimeInput}
        forwardedAs={StyledFormControl}
        hasSuggestion={status === 'pending' && !!onResolveSuggestion}
        container={{ alignItems: 'center', wrap: 'nowrap' }}
        status={status}
        readOnly={readOnly}
        disabled={disabled}
        ref={containerRef}
        onClick={(e: MouseEvent) => {
          if (e.target === containerRef.current) orderedRefs[0].current?.focus();
        }}
        {...restProps}
      >
        <Flex
          ref={inputContainerRef}
          as={StyledInputContainer}
          container={{ alignItems: 'center', wrap: 'wrap' }}
          onPaste={onPaste}
        >
          {dateInputs}
          {timeInputs}
        </Flex>
        {!readOnly && Picker}
      </Flex>
    );

    useAutoFocusNextInput(orderedRefs);

    return label ? (
      <FormField
        as={StyledDateTimeFieldset}
        labelAs='legend'
        aria-labelledby={id}
        ref={ref}
        {...{
          label,
          labelHidden,
          id,
          info,
          status,
          required,
          disabled,
          onResolveSuggestion,
          additionalInfo
        }}
      >
        {Comp}
      </FormField>
    ) : (
      Comp
    );
  }
);

export default DateTimeInput;
