import { FunctionComponent } from 'react';
import styled, { css } from 'styled-components';

import { defaultThemeProp } from '../../theme';
import useOS from '../../hooks/useOS';
import { ForwardProps, NoChildrenProp } from '../../types';

const osxKeyMapper: { [key: string]: string } = {
  Command: '⌘',
  Alt: '⌥',
  Option: '⌥',
  Control: '⌃',
  Return: '↵',
  Enter: '↵',
  Delete: 'Del',
  CommandOrControl: '⌘'
};

const windowsKeyMapper: { [key: string]: string } = {
  Control: 'Ctrl',
  Alt: 'Alt',
  Option: 'Alt',
  Meta: '⊞',
  Command: '⊞',
  Backspace: 'Backspace',
  CommandOrControl: 'Ctrl'
};

const generalKeyMapper: { [key: string]: string } = {
  'Page up': 'Pg up',
  'Page down': 'Pg dn',
  Ins: 'Insert',
  Shift: '⇧'
};

export const StyledKeyboard = styled.kbd(
  ({
    theme: {
      base,
      components: {
        text: { primary },
        badges: { keyboard }
      }
    }
  }) => {
    return css`
      background-color: ${keyboard['background-color']};
      color: ${base.palette['foreground-color']};
      display: inline-block;
      position: relative;
      font-family: ${base['font-family']};
      font-weight: ${primary['font-weight']};
      overflow: hidden;
      white-space: nowrap;
      padding: 0.125rem;
      border: 0.0125rem solid ${keyboard['border-color']};
      border-radius: calc(${base['border-radius']} / 4);
      text-transform: capitalize;
      text-align: center;
      min-width: 1.25rem;
      height: 1.25rem;
      text-overflow: ellipsis;
      line-height: 1rem;
      inset-block-start: calc(1.25rem / 4 - 0.125rem);
    `;
  }
);
StyledKeyboard.defaultProps = defaultThemeProp;

export interface KeyboardProps extends NoChildrenProp {
  keyName: string;
}

const Keyboard: FunctionComponent<KeyboardProps & ForwardProps> = ({ keyName, ...restProps }) => {
  const { windows: isWindows } = useOS();

  return (
    <StyledKeyboard {...restProps} aria-label={keyName}>
      {(isWindows && windowsKeyMapper[keyName]) ||
        osxKeyMapper[keyName] ||
        generalKeyMapper[keyName] ||
        keyName}
    </StyledKeyboard>
  );
};

export default Keyboard;
