import {
  forwardRef,
  FunctionComponent,
  Ref,
  PropsWithoutRef,
  ReactNode,
  MouseEventHandler
} from 'react';
import styled, { css } from 'styled-components';

import { defaultThemeProp } from '../../theme';
import Flex from '../Flex';
import { ForwardProps, BaseProps, AsProp } from '../../types';
import { StyledPopover } from '../Popover';

import { StyledCardMedia } from './CardMedia';

export interface CardProps extends BaseProps, AsProp {
  /** Components and content for the Card. */
  children?: ReactNode;
  /**
   * Enables the focus, hover, and active styles.
   * @default false
   * @deprecated
   */
  interactive?: boolean;
  /** Callback for onClick event. */
  onClick?: MouseEventHandler<HTMLElement>;
  /** Ref forwarded to the wrapping element. */
  ref?: Ref<HTMLElement>;
}

export const StyledCard = styled.article<Partial<CardProps>>(props => {
  const {
    'border-radius': borderRadius,
    'border-color': borderColor,
    background
  } = props.theme.components.card;
  const interactive = props.interactive
    ? css`
        &:hover {
          border: 0;
          box-shadow: 0 0 0 0.0625rem ${borderColor};
        }
        &:focus {
          border: 0;
          box-shadow: 0 0 0 0.125rem ${borderColor};
        }
      `
    : undefined;

  return css`
    ${StyledPopover} &, &:not(& &) {
      background-color: ${background};
      border-radius: ${borderRadius};

      &:focus {
        outline: none;
      }

      ${interactive}

      ${StyledCardMedia} {
        &:first-child img {
          border-radius: ${borderRadius} ${borderRadius} 0 0;
        }
      }
    }
  `;
});

StyledCard.defaultProps = defaultThemeProp;

const Card: FunctionComponent<CardProps & ForwardProps> = forwardRef(
  (
    { children, as, onClick, interactive = false, ...restProps }: PropsWithoutRef<CardProps>,
    ref: CardProps['ref']
  ) => {
    const interactiveProps = {
      ...(onClick && {
        onClick,
        tabIndex: 0,
        role: 'button'
      }),
      ...(interactive && {
        tabIndex: 0
      }),
      interactive
    };

    return (
      <Flex
        role='none'
        container={{ direction: 'column' }}
        as={StyledCard}
        forwardedAs={as}
        ref={ref}
        {...interactiveProps}
        {...restProps}
      >
        {children}
      </Flex>
    );
  }
);

export default Card;
