import { FunctionComponent, KeyboardEvent } from 'react';
import styled, { css } from 'styled-components';

import Button from '../Button';
import BareRoleButton from '../Button/BareRoleButton';
import Icon, { registerIcon } from '../Icon';
import * as timesIcon from '../Icon/icons/times.icon';
import * as minusIcon from '../Icon/icons/minus.icon';
import * as undockIcon from '../Icon/icons/undock.icon';
import { defaultThemeProp } from '../../theme';
import { ForwardProps } from '../../types';
import Flex from '../Flex';
import { useI18n, useModalContext } from '../../hooks';

import { ModalProps } from './Modal.types';

registerIcon(timesIcon, minusIcon, undockIcon);

const StyledMinimizedModal = styled(BareRoleButton)(({ theme: { base } }) => {
  return css`
    height: 3rem;
    width: 30ch;
    border-radius: calc(0.5 * ${base['border-radius']}) calc(0.5 * ${base['border-radius']}) 0 0;
    color: ${base.palette['foreground-color']};
    background-color: ${base.palette['primary-background']};
    box-shadow: ${base.shadow.low};
    cursor: pointer;
    user-select: none;
    pointer-events: auto;
    transition: box-shadow ${base.animation.speed} ${base.animation.timing.ease};

    &:hover {
      box-shadow: ${base.shadow.high};
    }

    &:focus {
      outline: none;
      box-shadow: ${base.shadow.focus};
    }

    > div:first-child {
      margin-inline-end: auto;
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
    }
  `;
});

StyledMinimizedModal.defaultProps = defaultThemeProp;

const MinimizedModal: FunctionComponent<ModalProps & ForwardProps> = ({
  heading,
  onBeforeClose,
  onAfterClose,
  onRequestDismiss,
  onRequestActivate,
  onRequestMinimize,
  onRequestMaximize,
  onRequestDock,
  ...restProps
}) => {
  const { dismissible, dockable, maximizable, dock, maximize, activate, unmount } =
    useModalContext();

  const t = useI18n();

  return (
    <Flex
      {...restProps}
      container={{ alignItems: 'center', justify: 'between', pad: [0, 2], gap: 2 }}
      item={{ shrink: 0 }}
      as={StyledMinimizedModal}
      aria-label={`${t('activate')} ${heading}`}
      onKeyDown={(e: KeyboardEvent<HTMLDivElement>) => {
        // Prevent issues when auto focusing on close button.
        if ((e.key === 'Space' || e.key === 'Enter') && e.target === e.currentTarget)
          e.preventDefault();
      }}
      onClick={() => {
        if (onRequestActivate?.() !== false && !dockable) activate();
        if (onRequestDock?.() !== false && dockable) dock();
      }}
      data-app-region
    >
      <div>{heading}</div>

      {(maximizable || dockable || dismissible) && (
        <Flex container>
          {maximizable && (
            <Button
              icon
              variant='simple'
              aria-label={t('modal_maximize')}
              onClick={() => {
                if (onRequestMaximize?.() !== false) {
                  maximize();
                }
              }}
            >
              <Icon name='undock' />
            </Button>
          )}

          {dockable && (
            <Button
              aria-label={t('modal_dock')}
              variant='simple'
              onClick={() => {
                if (onRequestDock?.() !== false) {
                  dock();
                }
              }}
              icon
            >
              <Icon name='minus' />
            </Button>
          )}

          {dismissible && (
            <Button
              aria-label={`${t('close')} ${heading}`}
              variant='simple'
              onClick={() => {
                if (onRequestDismiss?.() !== false) {
                  onBeforeClose?.();
                  unmount();
                  onAfterClose?.();
                }
              }}
              icon
            >
              <Icon name='times' />
            </Button>
          )}
        </Flex>
      )}
    </Flex>
  );
};

export default MinimizedModal;
