import { forwardRef, FunctionComponent, PropsWithoutRef, Ref } from 'react';

import type { BaseProps, ForwardProps, NoChildrenProp } from '../../types';
import { useConfiguration } from '../../hooks';

import { getFraction, getFormattedValue } from './utils';

export interface NumberDisplayProps extends BaseProps, NoChildrenProp {
  /** Number value */
  value?: number | bigint;
  /** Unit to be included in the text, for example '%' */
  unit?: string;

  formattingOptions?: {
    /**
     * Value display notation.
     * @default 'standard'
     */
    notation?: 'standard' | 'compact';
    /**
     * Determines the fixed number of fraction digits. The value is rounded to the given precision.
     * Maximum is 20, default is value precision.
     * @default variable
     */
    fractionDigits?: number;
    /**
     * Determines whether group separators should be shown.
     * @default true
     */
    groupSeparators?: boolean;
  };
  /** Ref for the wrapping element. */
  ref?: Ref<HTMLSpanElement>;
}

const NumberDisplay: FunctionComponent<NumberDisplayProps & ForwardProps> = forwardRef(
  (
    {
      value,
      unit = '',
      formattingOptions: {
        fractionDigits = getFraction(String(value)).substring(1).length,
        notation,
        groupSeparators = true
      } = {},
      ...restProps
    }: PropsWithoutRef<NumberDisplayProps>,
    ref: NumberDisplayProps['ref']
  ) => {
    const { locale } = useConfiguration();

    return (
      <span ref={ref} {...restProps}>
        {value || value === 0 ? (
          getFormattedValue(value.toString(), locale, unit, {
            notation,
            useGrouping: groupSeparators,
            maximumFractionDigits: fractionDigits,
            minimumFractionDigits: fractionDigits
          })
        ) : (
          <span aria-hidden='true'>&ndash;&ndash;</span>
        )}
      </span>
    );
  }
);

export default NumberDisplay;
