import { forwardRef, FunctionComponent, PropsWithoutRef, Ref, MouseEvent } from 'react';

import { useConsolidatedRef, useI18n } from '../../hooks';
import { Action, ForwardProps, NoChildrenProp } from '../../types';
import Button from '../Button';
import Icon from '../Icon';
import MenuButton, { MenuButtonProps } from '../MenuButton';

export interface ActionsProps extends NoChildrenProp {
  items: Action[];
  contextualLabel?: string;
  /** @default 2 */
  menuAt?: number;
  menuButtonProps?: {
    onClick?: MenuButtonProps['onClick'];
    onKeyDown?: MenuButtonProps['onKeyDown'];
  };
  /** @default true */
  iconOnly?: boolean;
  ref?: Ref<HTMLButtonElement>;
}

const Actions: FunctionComponent<ActionsProps & ForwardProps> = forwardRef(
  (
    {
      items,
      menuAt = 2,
      iconOnly = true,
      menuButtonProps,
      contextualLabel
    }: PropsWithoutRef<ActionsProps>,
    ref: ActionsProps['ref']
  ) => {
    const t = useI18n();
    const menuButtonRef = useConsolidatedRef(ref);

    if (!items || items.length === 0) {
      return null;
    }

    return items.length >= menuAt ? (
      <MenuButton
        ref={menuButtonRef}
        text={t('actions')}
        aria-label={contextualLabel && t('actions_for', [contextualLabel])}
        iconOnly
        icon='more'
        variant='simple'
        onClick={menuButtonProps?.onClick}
        onKeyDown={menuButtonProps?.onKeyDown}
        menu={{
          items: items.map(({ text, onClick, ...restProps }) => ({
            ...restProps,
            primary: text,
            onClick: onClick
              ? (id, e) => {
                  onClick(id, e, menuButtonRef.current ?? undefined);
                }
              : undefined
          }))
        }}
      />
    ) : (
      <>
        {items.map(({ id, icon, text, onClick, ...restProps }) => {
          return (
            <Button
              key={id}
              variant={icon && iconOnly ? 'simple' : undefined}
              onClick={(event: MouseEvent<HTMLButtonElement>) => onClick?.(id, event)}
              label={icon ? text : undefined}
              icon={!!icon && iconOnly}
              aria-label={contextualLabel ? `${text} - ${contextualLabel}` : text}
              {...restProps}
            >
              {icon && iconOnly ? <Icon name={icon} /> : text}
            </Button>
          );
        })}
      </>
    );
  }
);

export default Actions;
