import {
  FunctionComponent,
  useRef,
  forwardRef,
  PropsWithoutRef,
  Ref,
  useCallback,
  useReducer,
  useImperativeHandle
} from 'react';
import styled, { css } from 'styled-components';

import { useConsolidatedRef, useFocusWithin, useI18n } from '../../../hooks';
import { ForwardProps } from '../../../types';
import { defaultThemeProp } from '../../../theme';
import { FormControlProps, HandleValue } from '../../FormControl';
import Flex from '../../Flex';
import FormField, { StyledFormField } from '../../FormField/FormField';
import DateRangePicker from '../Picker/DateRangePicker';
import type {
  DateRangeCallbackParameter,
  DateTimeCallbackParameter,
  DateTimeProperties
} from '../DateTime.types';
import { StyledRangeInputContainer } from '../../Input/Input.styles';
import DateTimeProps from '../DateTime.types';

import DateInput, { DateInputRefObject } from './DateInput';
import { parseToDate } from './utils';
import StyledDateInput from './DateTime.styles';

export interface DateRangeInputProps extends DateTimeProperties {
  /** Object of range boundaries. */
  value?: { start?: DateTimeProps['value']; end?: DateTimeProps['value'] };
  /**
   * Display additional column with week number in date picker popover.
   * @default false
   */
  showWeekNumber?: boolean;
  /** Pass a string or a fragment with an Icon and string for first part of DataRangeInput. */
  fromLabel?: FormControlProps['label'];
  /** Pass a string or a fragment with an Icon and string for second part of DataRangeInput. */
  toLabel?: FormControlProps['label'];
  /** Pass an object with "heading" and "content" properties to show AdditionalInfo for second part of DataRangeInput. */
  toAdditionalInfo?: FormControlProps['additionalInfo'];
  /** Pass an object with "heading" and "content" properties to show AdditionalInfo for first part of DataRangeInput. */
  fromAdditionalInfo?: FormControlProps['additionalInfo'];
  /** Callback fired when input value changes. */
  onChange: (value: DateRangeCallbackParameter) => void;
  /** Callback fired when the component gets focused. */
  onFocus?: (value: DateRangeCallbackParameter) => void;
  /** Callback fired when the component loses focus. */
  onBlur?: (value: DateRangeCallbackParameter) => void;
  /** Automatically focuses the input on render. */
  autoFocus?: boolean;
  /** Imperative handle */
  handle?: Ref<HandleValue>;
}

const StyledDateRangeInput = styled.div<DateRangeInputProps>(props => {
  const {
    theme: {
      components: { input }
    }
  } = props;

  return css`
    border: 0;
    min-width: min-content;

    ${StyledFormField} {
      &:last-child > ${StyledDateInput} {
        margin-inline-start: -${input['border-width']};
      }
      & legend {
        align-self: center;
      }
      &:focus,
      &:hover,
      &:focus-within {
        z-index: 2;
      }
    }
  `;
});

StyledDateRangeInput.defaultProps = defaultThemeProp;

type DateRepresentable = string | number | Date;

type DateRangeAction = { type: 'start' | 'end'; value?: number };
type State = { start?: number; end?: number };

const reducer = (prevState: State, { type, value }: DateRangeAction): State => {
  switch (type) {
    case 'start': {
      return { ...prevState, start: value };
    }
    case 'end': {
      return { ...prevState, end: value };
    }
    default:
      return prevState;
  }
};

const convertToCallbackParameter = (v?: number): DateTimeCallbackParameter => {
  return {
    valueAsISOString: v === undefined || Number.isNaN(v) ? undefined : new Date(v).toISOString(),
    valueAsTimestamp: v
  };
};

const getSelected = (first: number | undefined, second: number | undefined) => {
  const selected = first ?? second;
  return selected ? parseToDate(selected) : undefined;
};

const parseValueToDate = (value: DateRepresentable | undefined) => {
  return value ? parseToDate(value) : undefined;
};

const DateRangeInput: FunctionComponent<DateRangeInputProps & ForwardProps> = forwardRef(
  (
    {
      value = {},
      min,
      max,
      label,
      fromLabel,
      toLabel,
      labelHidden,
      status,
      required,
      readOnly,
      disabled,
      onChange,
      onFocus,
      onBlur,
      showWeekNumber,
      autoFocus,
      fromAdditionalInfo,
      toAdditionalInfo,
      handle,
      ...restProps
    }: PropsWithoutRef<DateRangeInputProps>,
    ref: Ref<HTMLDivElement>
  ) => {
    const t = useI18n();

    const startDateInputRef = useRef<DateInputRefObject>(null);
    const endDateInputRef = useRef<DateInputRefObject>(null);

    const startDateClearHandle = useRef<HandleValue>(null);
    const endDateClearHandle = useRef<HandleValue>(null);

    const [state, dispatch] = useReducer(reducer, {
      start: value.start ? parseToDate(value.start).getTime() : undefined,
      end: value.end ? parseToDate(value.end).getTime() : undefined
    });

    // Need to keep the value in ref also, to be able to call onBlur with newest value
    const startDateRef = useRef<DateTimeCallbackParameter>(convertToCallbackParameter(state.start));
    const endDateRef = useRef<DateTimeCallbackParameter>(convertToCallbackParameter(state.end));

    useImperativeHandle(
      handle,
      () => ({
        clear: () => {
          startDateClearHandle.current?.clear();
          endDateClearHandle.current?.clear();
          const emptyCallbackParameter = convertToCallbackParameter(undefined);
          startDateRef.current = emptyCallbackParameter;
          endDateRef.current = emptyCallbackParameter;
          dispatch({ type: 'start', value: undefined });
          dispatch({ type: 'end', value: undefined });
          onChange({
            start: emptyCallbackParameter,
            end: emptyCallbackParameter
          });
        }
      }),
      [onChange]
    );

    const onFocusChange = useCallback(
      (focused: boolean) => {
        (focused ? onFocus : onBlur)?.({
          start: startDateRef.current,
          end: endDateRef.current
        });
      },
      [onFocus, onBlur]
    );

    const containerRef = useConsolidatedRef(ref);
    useFocusWithin([containerRef], onFocusChange);

    const onStartDateChange = (selectedDate?: Date) => {
      const selected = selectedDate?.getTime();
      let start;
      let end;
      if (state.end && selected && selected > state.end) {
        dispatch({ type: 'start', value: state.end });
        dispatch({ type: 'end', value: selected });
        onChange({
          start: convertToCallbackParameter(state.end),
          end: convertToCallbackParameter(selected)
        });
        start = state.end;
        end = selected;
      } else {
        dispatch({ type: 'start', value: selected });
        onChange({
          start: convertToCallbackParameter(selected),
          end: convertToCallbackParameter(state.end)
        });
        start = selected;
        end = state.end;
      }
      startDateRef.current = convertToCallbackParameter(start);
      endDateRef.current = convertToCallbackParameter(end);
      endDateInputRef.current?.setOpen(true);
    };

    const onEndDateChange = (selectedDate?: Date) => {
      const selected = selectedDate?.getTime();
      let start;
      let end;
      if (state.start && selected && selected < state.start) {
        dispatch({ type: 'end', value: state.start });
        dispatch({ type: 'start', value: selected });
        onChange({
          start: convertToCallbackParameter(selected),
          end: convertToCallbackParameter(state.start)
        });
        start = selected;
        end = state.start;
      } else {
        dispatch({ type: 'end', value: selected });
        onChange({
          start: convertToCallbackParameter(state.start),
          end: convertToCallbackParameter(selected)
        });
        start = state.start;
        end = selected;
      }
      startDateRef.current = convertToCallbackParameter(start);
      endDateRef.current = convertToCallbackParameter(end);
    };

    const getSelectedRange = () => {
      const startDate = parseValueToDate(state.start);
      const endDate = parseValueToDate(state.end);

      if (startDate && endDate && startDate > endDate) return undefined;

      return {
        startDate,
        endDate
      };
    };

    const commonProps = {
      required,
      readOnly,
      disabled,
      min,
      max,
      status
    };

    return (
      <FormField
        as={StyledDateRangeInput}
        {...restProps}
        ref={containerRef}
        container={{ wrap: 'nowrap' }}
        labelHidden={labelHidden}
        label={label}
        labelAs='legend'
        required={required}
        readOnly={readOnly}
        status={status}
        disabled={disabled}
      >
        <Flex container as={StyledRangeInputContainer}>
          <DateInput
            {...commonProps}
            value={state.start}
            ref={startDateInputRef}
            handle={startDateClearHandle}
            picker={{
              renderer: DateRangePicker,
              rendererProps: {
                selected: getSelected(state.start, state.end),
                selectedRange: getSelectedRange(),
                onChange: onStartDateChange
              }
            }}
            onBlur={changedValue => {
              // invalid value stored only in ref, state holds undefined
              dispatch({
                type: 'start',
                value: changedValue.state ? undefined : changedValue.valueAsTimestamp
              });

              startDateRef.current = changedValue;
              onChange({
                start: changedValue,
                end: convertToCallbackParameter(state.end)
              });
            }}
            additionalInfo={fromAdditionalInfo}
            label={fromLabel}
            aria-label={`${t('range_from_datetime') + (label ? ` - ${label}` : '')}`}
            autoFocus={autoFocus}
          />
          <DateInput
            {...commonProps}
            value={state.end}
            ref={endDateInputRef}
            handle={endDateClearHandle}
            picker={{
              renderer: DateRangePicker,
              rendererProps: {
                selected: getSelected(state.end, state.start),
                selectedRange: getSelectedRange(),
                onChange: onEndDateChange
              }
            }}
            onBlur={changedValue => {
              // invalid value stored only in ref, state holds undefined
              dispatch({
                type: 'end',
                value: changedValue.state ? undefined : changedValue.valueAsTimestamp
              });

              endDateRef.current = changedValue;
              onChange({
                start: convertToCallbackParameter(state.start),
                end: changedValue
              });
            }}
            additionalInfo={toAdditionalInfo}
            label={toLabel}
            aria-label={`${t('range_to_datetime') + (label ? ` - ${label}` : '')}`}
          />
        </Flex>
      </FormField>
    );
  }
);

export default DateRangeInput;
