import { Fragment, FunctionComponent } from 'react';

import { ForwardProps } from '../../types';
import Flex from '../Flex';
import Grid from '../Grid';
import Text from '../Text';

import { StepMarkerIcon, StyledBulletWrapper, StyledStepText } from './FormProgress.styles';
import type MultiStepFormProps from './MultiStepForm.types';

interface FormProgressProps {
  steps: MultiStepFormProps['steps'];
  currentStepId: MultiStepFormProps['currentStepId'];
}

const VerticalFormProgress: FunctionComponent<FormProgressProps & ForwardProps> = ({
  steps,
  currentStepId,
  ...restProps
}) => {
  const curIdx = steps.findIndex(step => step.id === currentStepId);
  const curStep = steps[curIdx];

  return (
    <Flex
      {...restProps}
      container={{ alignItems: 'start' }}
      role='progressbar'
      aria-valuenow={curIdx}
      aria-valuemin={0}
      aria-valuetext={curStep.name}
      aria-valuemax={steps.length}
    >
      <Grid
        container={{
          cols: '2rem minmax(0, 1fr)',
          colGap: 1
        }}
      >
        {steps.map((step, index) => {
          return (
            <Fragment key={step.id}>
              <StyledBulletWrapper hasNext={index !== steps.length - 1} prior={index < curIdx}>
                <StepMarkerIcon
                  aria-label={step.name}
                  aria-describedby={step.id}
                  current={index === curIdx}
                  prior={index < curIdx}
                  depth={step.depth}
                />
              </StyledBulletWrapper>
              <Text variant='h4' as={StyledStepText}>
                {step.name}
              </Text>
            </Fragment>
          );
        })}
      </Grid>
    </Flex>
  );
};

export default VerticalFormProgress;
