import { forwardRef, FunctionComponent, PropsWithoutRef, Ref } from 'react';

import type { BaseProps, ForwardProps, NoChildrenProp } from '../../types';
import Link from '../Link';

import { formatPhone } from './utils';

export interface PhoneDisplayProps extends BaseProps, NoChildrenProp {
  /** Phone number, no 'tel:' scheme, '+18887342669' */
  value?: string;
  /** The text to be displayed. */
  displayText?: string;
  /**
   * Controls styling of the field.
   * @default 'link'
   */
  variant?: 'link' | 'text';
  /** Options to define formatting. */
  formattingOptions?: {
    /**
     * Display phone number with country calling code
     * @default true
     */
    showCountryCode?: boolean;
  };
  /** Ref for the wrapping element. */
  ref?: Ref<HTMLAnchorElement | HTMLSpanElement>;
}

const PhoneDisplay: FunctionComponent<PhoneDisplayProps & ForwardProps> = forwardRef(
  (
    {
      value,
      displayText,
      variant = 'link',
      formattingOptions: { showCountryCode = true } = {},
      ...restProps
    }: PropsWithoutRef<PhoneDisplayProps>,
    ref: PhoneDisplayProps['ref']
  ) => {
    const displayValue = value ? (
      displayText || formatPhone(value || '', { nationalFormat: !showCountryCode }) || value
    ) : (
      <span aria-hidden='true'>&ndash;&ndash;</span>
    );

    return variant === 'text' || !value ? (
      <span ref={ref as Ref<HTMLSpanElement>} {...restProps}>
        {displayValue}
      </span>
    ) : (
      <Link ref={ref as Ref<HTMLAnchorElement>} {...restProps} href={`tel:${value}`}>
        {displayValue}
      </Link>
    );
  }
);

export default PhoneDisplay;
