import { forwardRef, FunctionComponent, PropsWithoutRef, Ref } from 'react';
import styled from 'styled-components';

import { BaseProps, ForwardProps } from '../../types';

export interface ParagraphDisplayProps extends BaseProps {
  /** Paragraph text */
  value?: string;
  /** Ref for the wrapping element. */
  ref?: Ref<HTMLParagraphElement>;
}

const StyledParagraphDisplay = styled.p`
  white-space: break-spaces;
`;

const ParagraphDisplay: FunctionComponent<ParagraphDisplayProps & ForwardProps> = forwardRef(
  (
    { value, ...restProps }: PropsWithoutRef<ParagraphDisplayProps>,
    ref: ParagraphDisplayProps['ref']
  ) => {
    return (
      <StyledParagraphDisplay ref={ref} {...restProps}>
        {value !== undefined ? value : <span aria-hidden='true'>&ndash;&ndash;</span>}
      </StyledParagraphDisplay>
    );
  }
);

export default ParagraphDisplay;
