import {
  useState,
  forwardRef,
  FunctionComponent,
  PropsWithoutRef,
  Ref,
  MouseEventHandler,
  useMemo,
  ReactNode,
  useImperativeHandle
} from 'react';
import styled, { css } from 'styled-components';
import { getContrast, readableColor } from 'polished';

import { defaultThemeProp } from '../../theme';
import { BaseProps, ForwardProps, NoChildrenProp, OmitStrict } from '../../types';
import Button, { StyledButton } from '../Button';
import { focusHeadingOrContainer, tryCatch } from '../../utils';
import { useConsolidatedRef, useDirection, useI18n } from '../../hooks';
import Flex from '../Flex';
import Text from '../Text';
import Grid from '../Grid';
import ExpandCollapse from '../ExpandCollapse';
import { Count } from '../Badges';
import Icon, { StyledIcon, registerIcon } from '../Icon';
import * as timesIcon from '../Icon/icons/times.icon';
import * as caretRightIcon from '../Icon/icons/caret-right.icon';
import * as warnSolidIcon from '../Icon/icons/warn-solid.icon';
import * as flagWaveSolidIcon from '../Icon/icons/flag-wave-solid.icon';
import * as informationSolidIcon from '../Icon/icons/information-solid.icon';
import * as checkIcon from '../Icon/icons/check.icon';
import VisuallyHiddenText from '../VisuallyHiddenText';
import { UnorderedList } from '../List';

registerIcon(
  timesIcon,
  caretRightIcon,
  warnSolidIcon,
  flagWaveSolidIcon,
  informationSolidIcon,
  checkIcon
);

export interface BannerHandleValue {
  /** Used to apply focus to the banner via an imperative handle. */
  focus: () => void;
}

export interface BannerMessage {
  label: string;
  description?: string;
  action?: {
    text: string;
    onClick?: MouseEventHandler<HTMLButtonElement | HTMLAnchorElement>;
    href?: string;
  };
  items?: (BannerMessage | string)[];
}

export interface BannerProps extends BaseProps, NoChildrenProp {
  /** Controls the styling of the Banner. */
  variant: 'urgent' | 'warning' | 'success' | 'info';
  /**
   * The heading tag to render the provided heading as. This is agnostic to the styling of the heading.
   * @default 'h2'
   */
  headingTag?: 'h1' | 'h2' | 'h3' | 'h4' | 'h5' | 'h6';
  /** Pass one or more descriptive messages to inform the user. */
  messages: (string | BannerMessage)[];
  /** Callback when user explicitly dismisses the Banner. */
  onDismiss?: MouseEventHandler;
  /** Ref that can be used to access the imperative handle. */
  handle?: Ref<BannerHandleValue>;
  /** Ref forwarded to the wrapping element. */
  ref?: Ref<HTMLDivElement>;
}

export const StyledBanner = styled.div<{ headingTag: BannerProps['headingTag'] }>(
  ({ headingTag, theme }) => {
    return css`
      border-radius: ${theme.components.card['border-radius']};

      &:has(${headingTag}:focus) {
        box-shadow: ${theme.base.shadow.focus};
      }
    `;
  }
);

StyledBanner.defaultProps = defaultThemeProp;

export const StyledBannerStatus = styled.div<Pick<BannerProps, 'variant'>>(({ variant, theme }) => {
  const { background } = theme.components.banner[variant];
  const color = tryCatch(() =>
    getContrast(background, theme.base.palette['primary-background']) >= 3
      ? theme.base.palette['primary-background']
      : readableColor(background)
  );

  return css`
    background-color: ${background};
    color: ${color};
    border-start-start-radius: inherit;
    border-end-start-radius: inherit;
    border: 0.0625rem solid ${theme.base.palette['border-line']};
    border-inline-end: none;
    font-size: 1.25rem;
  `;
});

StyledBannerStatus.defaultProps = defaultThemeProp;

export const StyledBannerHeader = styled.div(({ theme }) => {
  const { rtl } = useDirection();

  return css`
    min-height: 2rem;

    ${StyledButton}[aria-expanded] {
      ${StyledIcon} {
        transition: rotate ${theme.base.animation.speed} ${theme.base.animation.timing.ease};
      }

      &[aria-expanded='true'] ${StyledIcon} {
        rotate: 90deg;
      }

      &[aria-expanded='false'] ${StyledIcon} {
        rotate: ${rtl ? 180 : 0}deg;
      }
    }
  `;
});

StyledBannerHeader.defaultProps = defaultThemeProp;

export const StyledBannerContent = styled.div(({ theme }) => {
  return css`
    height: 100%;
    background: ${theme.components.card.background};
    padding-block: calc(0.5 * ${theme.base.spacing});
    padding-inline-start: calc(2 * ${theme.base.spacing});
    padding-inline-end: calc(0.5 * ${theme.base.spacing});
    border: 0.0625rem solid ${theme.base.palette['border-line']};
    border-inline-start: none;
    border-start-end-radius: inherit;
    border-end-end-radius: inherit;
  `;
});

StyledBannerContent.defaultProps = defaultThemeProp;

export const StyledBannerMessageList: typeof UnorderedList = styled(UnorderedList)(
  ({ theme }) => css`
    padding-block-end: calc(0.5 * ${theme.base.spacing});
    padding-inline-end: calc(2 * ${theme.base.spacing});
  `
);

StyledBannerMessageList.defaultProps = defaultThemeProp;

export const StyledBannerMessage = styled.span`
  overflow-wrap: break-word;
  list-style: none;
`;

StyledBannerMessage.defaultProps = defaultThemeProp;

const BannerMessageItem = forwardRef<
  HTMLLIElement,
  OmitStrict<BannerMessage, 'items'> & ForwardProps
>(({ label, description, action, ...restProps }, ref) => {
  return (
    <StyledBannerMessage {...restProps} ref={ref}>
      <span>{label}</span>
      {description && (
        <>
          {' '}
          <Text variant='secondary'>{description}</Text>
        </>
      )}
      {action && (
        <>
          {' '}
          <Text variant='secondary'>
            <Button variant='link' href={action.href} onClick={action.onClick} {...action}>
              {action.text}
            </Button>
          </Text>
        </>
      )}
    </StyledBannerMessage>
  );
});

const prepareMessages = (
  messages: (string | BannerMessage)[]
): [messages: ReactNode[], count: number] => {
  let count = 0;

  return [
    messages.flatMap(msg => {
      count += 1;

      if (typeof msg === 'string') {
        return [<BannerMessageItem label={msg} />];
      }

      if (msg.items) {
        const { items, ...restMsg } = msg;
        const [subMessages, subCount] = prepareMessages(items);
        count += subCount;
        return [<BannerMessageItem {...restMsg} />, subMessages];
      }

      return [<BannerMessageItem {...msg} />];
    }),
    count
  ];
};

const Banner: FunctionComponent<BannerProps & ForwardProps> = forwardRef(
  (
    {
      variant,
      headingTag = 'h2',
      messages,
      onDismiss,
      handle,
      ...restProps
    }: PropsWithoutRef<BannerProps>,
    ref: BannerProps['ref']
  ) => {
    const [messagesToRender, count] = useMemo(() => prepareMessages(messages), [messages]);

    const t = useI18n();
    const [collapsed, setCollapsed] = useState(count > 2);

    const bannerRef = useConsolidatedRef(ref);

    let role: string;
    let icon: string;
    let heading: string;
    let ariaLive: string;

    switch (variant) {
      case 'urgent':
        heading = t('error');
        icon = 'warn-solid';
        role = 'alert';
        ariaLive = 'assertive';
        break;
      case 'warning':
        heading = t('warning');
        icon = 'flag-wave-solid';
        role = 'alert';
        ariaLive = 'assertive';
        break;
      case 'success':
        heading = t('success');
        icon = 'check';
        role = 'none';
        ariaLive = 'polite';
        break;
      case 'info':
      default:
        heading = t('information');
        icon = 'information-solid';
        role = 'none';
        ariaLive = 'polite';
        break;
    }

    useImperativeHandle(
      handle,
      () => ({
        focus: () => {
          if (bannerRef.current) focusHeadingOrContainer(bannerRef.current, heading);
        }
      }),
      []
    );

    return (
      <Grid
        {...restProps}
        as={StyledBanner}
        container={{ cols: '2rem minmax(0, 1fr)' }}
        role={role}
        aria-live={ariaLive}
        headingTag={headingTag}
        ref={bannerRef}
      >
        <Flex
          as={StyledBannerStatus}
          container={{ justify: 'center', alignItems: 'start', pad: [1.5, 0] }}
          variant={variant}
        >
          <Icon name={icon} />
        </Flex>
        {count === 1 ? (
          <Flex container={{ alignItems: 'start' }} as={StyledBannerContent}>
            <VisuallyHiddenText as={headingTag}>{`${heading} ${count}`}</VisuallyHiddenText>
            <Flex item={{ alignSelf: 'center', grow: 1 }} as='ul'>
              {typeof messages[0] === 'string' ? (
                <BannerMessageItem label={messages[0]} as='li' />
              ) : (
                <BannerMessageItem {...messages[0]} as='li' />
              )}
            </Flex>
            {onDismiss && (
              <Button
                onClick={onDismiss}
                label={t('dismiss')}
                aria-label={t('banner_dismiss_button_label_a11y')}
                variant='simple'
                icon
              >
                <Icon name='times' />
              </Button>
            )}
          </Flex>
        ) : (
          <StyledBannerContent>
            <Grid
              as={StyledBannerHeader}
              container={{ cols: 'auto 1fr auto', areas: '"toggle heading dismiss"', gap: 1 }}
            >
              <Grid
                container={{ cols: 'min-content min-content', gap: 0.5, alignItems: 'center' }}
                item={{ area: 'heading', alignSelf: 'center' }}
                as={Text}
                forwardedAs={headingTag}
                variant='h2'
              >
                {heading} <Count>{count}</Count>
              </Grid>
              <Grid item={{ area: 'toggle', alignSelf: 'center' }}>
                <Button
                  onClick={() => {
                    setCollapsed(cur => !cur);
                  }}
                  label={t(collapsed ? 'expand' : 'collapse')}
                  aria-expanded={collapsed ? 'false' : 'true'}
                  variant='simple'
                  icon
                >
                  <Icon name='caret-right' />
                </Button>
              </Grid>
              <Grid item={{ area: 'dismiss', alignSelf: 'center' }}>
                {onDismiss && (
                  <Button
                    onClick={onDismiss}
                    label={t('dismiss')}
                    aria-label={t('banner_dismiss_button_label_a11y')}
                    variant='simple'
                    icon
                  >
                    <Icon name='times' />
                  </Button>
                )}
              </Grid>
            </Grid>
            <ExpandCollapse collapsed={collapsed}>
              <StyledBannerMessageList items={messagesToRender} />
            </ExpandCollapse>
          </StyledBannerContent>
        )}
      </Grid>
    );
  }
);

export default Banner;
