import { ReactNode, FunctionComponent, Ref, forwardRef } from 'react';
import styled, { css } from 'styled-components';

import { BaseProps, ForwardProps, NoChildrenProp, TestIdProp } from '../../types';
import Grid from '../Grid';
import { StyledLabel } from '../Label';
import { useBreakpoint, useConsolidatedRef, useTestIds } from '../../hooks';
import { defaultThemeProp } from '../../theme';
import ShowMoreLess from '../ShowMoreLess';

import { getFieldValueListTestIds, getFieldValueItemTestIds } from './FieldValueList.test-ids';

export type FieldValueVariant = 'inline' | 'stacked';

export interface FieldValueListItemProps extends TestIdProp {
  name: string;
  value?: ReactNode;
  variant?: FieldValueVariant;
}

export interface FieldValueListItem extends FieldValueListItemProps {
  id?: string;
}

export interface FieldValueListProps extends BaseProps, NoChildrenProp, TestIdProp {
  /** The set of field values to render in the list. */
  fields: FieldValueListItem[];
  /**
   * Determines if the field and values should be displayed on the same line, or stacked with the field above the value.
   * @default 'inline'
   */
  variant?: FieldValueVariant | 'value-comparison';
}

export const StyledFieldName = styled.dt`
  word-break: break-word;
`;

export const StyledFieldValue = styled.dd`
  word-break: break-word;
`;

export type StyledFieldValueListProps = Pick<FieldValueListProps, 'variant'>;

export const StyledFieldValueList = styled.dl<StyledFieldValueListProps>(({ variant, theme }) => {
  return css`
    width: 100%;

    ${variant === 'value-comparison' &&
    css`
      dt {
        width: 100%;
      }
      dd {
        text-align: end;
        padding-inline-start: calc(2 * ${theme.base.spacing});
      }
      ${StyledLabel} {
        max-width: unset;
      }
      dd:not(:last-of-type),
      dt:not(:last-of-type) {
        border-bottom: 0.0625rem solid ${theme.base.palette['border-line']};
        padding-bottom: calc(0.5 * ${theme.base.spacing});
      }
    `}
  `;
});

StyledFieldValueList.defaultProps = defaultThemeProp;

export const StyledStackedFieldValue = styled.div``;

export const FieldValueItem = ({
  testId,
  name,
  value,
  variant = 'inline'
}: FieldValueListItemProps) => {
  const testIds = useTestIds(testId, getFieldValueItemTestIds);

  const displayValue =
    typeof value === 'string' ? <ShowMoreLess lines={3}>{value}</ShowMoreLess> : value;

  const labelAndValue = (
    <>
      <StyledLabel data-testid={testIds.name} as={StyledFieldName}>
        {name}
      </StyledLabel>
      <StyledFieldValue data-testid={testIds.value}>
        {value ? displayValue : <span aria-hidden='true'>&ndash;&ndash;</span>}
      </StyledFieldValue>
    </>
  );

  return variant === 'stacked' ? (
    <StyledStackedFieldValue data-testid={testIds.root}>{labelAndValue}</StyledStackedFieldValue>
  ) : (
    <>{labelAndValue}</>
  );
};

const FieldValueList: FunctionComponent<FieldValueListProps & ForwardProps> = forwardRef(
  (
    { testId, fields, variant = 'inline', ...restProps }: FieldValueListProps,
    ref: Ref<HTMLDivElement>
  ) => {
    const testIds = useTestIds(testId, getFieldValueListTestIds);

    const fieldValueListRef = useConsolidatedRef<HTMLDivElement>(ref);

    const isXSContentWidthOrAbove = useBreakpoint('xs', {
      breakpointRef: fieldValueListRef,
      themeProp: 'content-width'
    });

    return (
      <Grid
        data-testid={testIds.root}
        {...restProps}
        ref={fieldValueListRef}
        container={{
          cols:
            ['inline', 'value-comparison'].includes(variant) && isXSContentWidthOrAbove
              ? '16ch minmax(0, 1fr)'
              : 'minmax(0, 1fr)',
          colGap: variant !== 'value-comparison' ? 2 : 0,
          rowGap: variant !== 'value-comparison' ? 1 : 0.5
        }}
        as={StyledFieldValueList}
        variant={variant}
      >
        {fields.map(({ id, name, value, variant: fieldVariant }) => {
          return (
            <FieldValueItem
              name={name}
              value={value}
              variant={
                (fieldVariant ?? variant) === 'stacked' || !isXSContentWidthOrAbove
                  ? 'stacked'
                  : 'inline'
              }
              key={id ?? name}
            />
          );
        })}
      </Grid>
    );
  }
);

export default FieldValueList;
