import {
  forwardRef,
  useMemo,
  FunctionComponent,
  PropsWithoutRef,
  Ref,
  ReactNode,
  useEffect
} from 'react';
import styled, { css } from 'styled-components';

import { defaultThemeProp } from '../../theme';
import { ForwardProps } from '../../types';
import Flex from '../Flex';
import Grid from '../Grid';
import Text from '../Text';
import SummaryItem from '../SummaryItem';
import Icon from '../Icon';
import Breadcrumbs from '../Breadcrumbs';
import { useConfiguration, useConsolidatedRef } from '../../hooks';
import { focusHeadingOrContainer } from '../../utils';

import {
  PageTemplateProps,
  PageLayoutProps,
  StyledPageHeader,
  StyledPageLayout,
  StyledPageIcon,
  StyledRegion
} from './PageTemplates';

export interface DashboardPageProps extends PageTemplateProps {
  a: ReactNode;
  b: ReactNode;
  c: ReactNode;
  filters?: ReactNode[];
  fitToViewport?: boolean;
}

export interface DashboardPageLayoutProps extends PageLayoutProps {
  filters?: ReactNode[];
  filterPosition?: 'block-start' | 'inline-start' | 'inline-end';
  fitToViewport?: boolean;
  regionFullWidth: ReactNode[];
}

export const StyledFilterRegion = styled(StyledRegion)``;

StyledFilterRegion.defaultProps = defaultThemeProp;

export const StyledDashboardRegion = styled(StyledRegion)`
  overflow: auto;
`;

StyledDashboardRegion.defaultProps = defaultThemeProp;

export const StyledDashboardPageLayout = styled(StyledPageLayout)<
  Pick<DashboardPageProps, 'fitToViewport'>
>(({ fitToViewport }) => {
  const height = 'calc(100vh - var(--appshell-offset, 0))';

  return css`
    min-height: ${height};
    ${fitToViewport &&
    css`
      height: ${height};
    `}
  `;
});

StyledDashboardPageLayout.defaultProps = defaultThemeProp;

const DashboardPageLayout: FunctionComponent<DashboardPageLayoutProps & ForwardProps> = forwardRef(
  (
    {
      regions,
      regionFullWidth,
      cols = `repeat(${regions.length}, minmax(0, 1fr))`,
      filters,
      filterPosition = 'block-start',
      fitToViewport = false,
      title,
      icon,
      actions,
      banners,
      path,
      as,
      ...restProps
    }: PropsWithoutRef<DashboardPageLayoutProps>,
    ref: PageLayoutProps['ref']
  ) => {
    const pageRef = useConsolidatedRef(ref);
    const { loadedRef } = useConfiguration();

    useEffect(() => {
      if (!pageRef.current) return;

      if (loadedRef.current) {
        focusHeadingOrContainer(pageRef.current, title);
      } else {
        loadedRef.current = true;
      }
    }, [title]);

    const headerArea = useMemo(() => {
      if (filterPosition === 'block-start' || !filters || Object.keys(filters).length === 0) {
        return `'${Array.from({ length: regions.length }, () => 'header').join(' ')}'`;
      }
      return `'${Array.from({ length: regions.length + 1 }, () => 'header').join(' ')}'`;
    }, [filterPosition, filters, regions.length]);

    const bannerArea = `'${Array.from({ length: regions.length }, () => 'banner').join(' ')}'`;
    const filterArea = `'${Array.from({ length: regions.length }, () => 'filter').join(' ')}'`;

    const regionArea = useMemo(() => {
      if (filters && Object.keys(filters).length !== 0) {
        if (filterPosition === 'inline-start') {
          return `'filter ${Array.from({ length: regions.length }, (_, i) => `col-${i + 1}`).join(
            ' '
          )}'`;
        }
        if (filterPosition === 'inline-end') {
          return `'${Array.from({ length: regions.length }, (_, i) => `col-${i + 1}`).join(
            ' '
          )} filter'`;
        }
      }
      return `'${Array.from({ length: regions.length }, (_, i) => `col-${i + 1}`).join(' ')}'`;
    }, [filterPosition, filters, regions.length]);

    const regionFullWidthArea = useMemo(() => {
      if (filters && Object.keys(filters).length !== 0) {
        if (filterPosition === 'inline-start') {
          return `'filter ${Array.from({ length: regions.length }, () => 'col-full-width').join(
            ' '
          )}'`;
        }
        if (filterPosition === 'inline-end') {
          return `'${Array.from({ length: regions.length }, () => 'col-full-width').join(
            ' '
          )} filter'`;
        }
      }
      return `'${Array.from({ length: regions.length }, () => 'col-full-width').join(' ')}'`;
    }, [filterPosition, filters, regions.length]);

    const gridAreas = useMemo(() => {
      if (filterPosition === 'block-start' && filters && Object.keys(filters).length !== 0) {
        return banners
          ? `${headerArea}\n${bannerArea}\n${filterArea}\n${regionArea}\n${regionFullWidthArea}`
          : `${headerArea}\n${filterArea}\n${regionArea}\n${regionFullWidthArea}`;
      }
      return banners
        ? `${headerArea}\n${bannerArea}\n${regionArea}\n${regionFullWidthArea}`
        : `${headerArea}\n${regionArea}\n${regionFullWidthArea}`;
    }, [filterPosition, filters, banners]);

    const pageRows = useMemo(() => {
      if (fitToViewport) {
        if (filterPosition === 'block-start' && filters && Object.keys(filters).length !== 0) {
          return banners ? 'auto auto auto 1fr 1fr' : 'auto auto 1fr 1fr';
        }
        return banners ? 'auto auto 1fr 1fr' : 'auto 1fr 1fr';
      }
      return 'fit-content(100%)';
    }, [filterPosition, banners]);

    if (filterPosition === 'inline-start' && filters && Object.keys(filters).length !== 0) {
      cols = '1fr 2fr 2fr';
    }

    if (filterPosition === 'inline-end' && filters && Object.keys(filters).length !== 0) {
      cols = '2fr 2fr 1fr';
    }

    return (
      <Grid
        {...restProps}
        as={StyledDashboardPageLayout}
        forwardedAs={as}
        fitToViewport={fitToViewport}
        container={{
          autoRows: 'max-content',
          cols: '1fr',
          gap: 2,
          pad: 2
        }}
        md={{
          container: {
            areas: gridAreas,
            rows: pageRows,
            cols
          }
        }}
        ref={pageRef}
      >
        <Grid as={StyledPageHeader} md={{ item: { area: 'header' } }}>
          {path && path.length > 0 && <Breadcrumbs path={...path} />}
          <SummaryItem
            visual={
              icon && (
                <StyledPageIcon container={{ alignItems: 'center', justify: 'center' }}>
                  <Icon name={icon} />
                </StyledPageIcon>
              )
            }
            primary={<Text variant='h1'>{title}</Text>}
            actions={actions}
          />
        </Grid>
        {banners && <Grid md={{ item: { area: 'banner' } }}>{banners}</Grid>}
        {filters && Object.keys(filters).length !== 0 && (
          <Grid
            as={StyledFilterRegion}
            container={{
              autoRows: 'max-content',
              gap: 2
            }}
            md={{
              container: {
                cols:
                  filterPosition === 'block-start'
                    ? 'repeat(4, minmax(min(20ch, 100%), 1fr))'
                    : 'repeat(auto-fit, minmax(min(20ch, 100%), 1fr))'
              },
              item: { area: 'filter' }
            }}
          >
            {filters}
          </Grid>
        )}
        {regions.map((colItems: ReactNode, i: number) => {
          const key = `${i}`;
          return (
            <Flex
              container={{
                direction: 'column',
                justify: 'start',
                gap: 2
              }}
              as={StyledDashboardRegion}
              key={key}
            >
              {colItems}
            </Flex>
          );
        })}
        {regionFullWidth.map((colItems: ReactNode, i: number) => {
          const key = `${i}`;
          return (
            <Grid
              container={{
                cols: 'minmax(0, 1fr)',
                alignItems: 'start',
                gap: 2
              }}
              md={{ item: { area: 'col-full-width' } }}
              as={StyledDashboardRegion}
              key={key}
            >
              {colItems}
            </Grid>
          );
        })}
      </Grid>
    );
  }
);

const DashboardPage: FunctionComponent<DashboardPageProps & ForwardProps> = forwardRef(
  (
    { a, b, c, filters, fitToViewport, ...restProps }: PropsWithoutRef<DashboardPageProps>,
    ref: Ref<HTMLDivElement>
  ) => {
    return (
      <DashboardPageLayout
        {...restProps}
        cols='1fr 1fr'
        filters={filters}
        fitToViewport={fitToViewport}
        regions={[a, b]}
        regionFullWidth={[c]}
        ref={ref}
      />
    );
  }
);

export default DashboardPage;
