import {
  ChangeEvent,
  forwardRef,
  FunctionComponent,
  KeyboardEvent,
  PropsWithoutRef,
  useCallback,
  useMemo,
  useRef,
  FocusEvent
} from 'react';

import Button from '../Button';
import Icon, { registerIcon } from '../Icon';
import * as plusIcon from '../Icon/icons/plus.icon';
import * as minusIcon from '../Icon/icons/minus.icon';
import Flex from '../Flex';
import FormField from '../FormField';
import { ForwardProps } from '../../types';
import {
  useConfiguration,
  useConsolidatedRef,
  useI18n,
  useInputFormatter,
  useTestIds,
  useUID
} from '../../hooks';
import StyledInput from '../Input/Input.styles';

import StyledNumberInput, { StyledStepperInput } from './NumberInput.styles';
import { NumberInputProps, NUMBER_MAX_DECIMAL_PLACES } from './NumberInput.types';
import {
  getCleanedValue,
  getDecimalSign,
  getDecrementedValue,
  getFormattedValue,
  getIncrementedValue
} from './utils';
import { getNumberInputTestIds } from './Number.test-ids';

registerIcon(minusIcon, plusIcon);

const NumberInput: FunctionComponent<NumberInputProps & ForwardProps> = forwardRef(
  (props: PropsWithoutRef<NumberInputProps>, ref: NumberInputProps['ref']) => {
    const uid = useUID();
    const {
      testId,
      id = uid,
      status,
      label,
      labelHidden,
      info,
      required,
      disabled,
      readOnly,
      additionalInfo,
      value = '',
      onChange,
      onFocus,
      onBlur,
      onResolveSuggestion,
      unit,
      showDecimal = true,
      numberOfDecimals: numberOfDecimalsProp = NUMBER_MAX_DECIMAL_PLACES,
      showGroupSeparators = true,
      min = Number.MIN_SAFE_INTEGER,
      max = Number.MAX_SAFE_INTEGER,
      step = 1,
      variant,
      'aria-label': ariaLabel,
      autoFocus,
      ...restProps
    } = props;

    const testIds = useTestIds(testId, getNumberInputTestIds);

    const { locale } = useConfiguration();
    const inputRef = useConsolidatedRef(ref);
    const containerRef = useRef<HTMLElement>(null);
    const maximumFractionDigits = showDecimal ? numberOfDecimalsProp : 0;
    const decimalSign = useMemo(() => getDecimalSign(locale), [locale]);

    const formatValue = useCallback(
      number => {
        return getFormattedValue(number, locale, unit, {
          useGrouping: showGroupSeparators,
          maximumFractionDigits
        });
      },
      [locale, unit, showGroupSeparators, maximumFractionDigits]
    );

    const onInputChange = (e: ChangeEvent<HTMLInputElement>) => {
      const inputValue = e.target.value;
      const cleanedInputValue = getCleanedValue(inputValue, decimalSign, maximumFractionDigits);
      if (cleanedInputValue === value) return;
      onChange(cleanedInputValue);
    };

    const onKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
      if (readOnly) return;
      const input = e.currentTarget;
      switch (e.key) {
        case 'ArrowUp':
          e.preventDefault();
          onChange(getIncrementedValue(value, min, max, step));
          break;
        case 'ArrowDown':
          e.preventDefault();
          onChange(getDecrementedValue(value, min, max, step));
          break;
        case decimalSign:
          if (input.value.includes(decimalSign)) {
            if (input.selectionStart && input.value.charAt(input.selectionStart) === decimalSign) {
              input.setSelectionRange(input.selectionStart + 1, input.selectionStart + 1);
            }
            e.preventDefault();
          } else if (
            input.selectionStart !== null &&
            !input.value.charAt(input.selectionStart - 1).match(/\d/)
          ) {
            onChange(
              getCleanedValue(`${input.value}0${decimalSign}`, decimalSign, maximumFractionDigits)
            );
            e.preventDefault();
          }
          break;
        case '-':
          if (value.startsWith('-')) e.preventDefault();
          break;
        default:
      }
    };

    const onInputFocusEvent = (e: FocusEvent<HTMLInputElement>) => {
      let parsableValue = value;

      if (Number.isNaN(parseFloat(value))) {
        parsableValue = '';
      } else if (value?.endsWith('.')) {
        parsableValue = value.slice(0, -1);
      }

      if (e.type === 'focus') onFocus?.(parsableValue);
      else {
        if (parsableValue !== value) {
          onChange(parsableValue);
        }

        onBlur?.(parsableValue);
      }
    };

    const formattedValue = useInputFormatter(inputRef, value, formatValue, `\\p{N}${decimalSign}-`);

    const t = useI18n();

    const numberInput = (
      <Flex
        data-testid={testIds.root}
        as={StyledNumberInput}
        hasSuggestion={status === 'pending' && !!onResolveSuggestion}
        container={{ alignItems: 'center', wrap: 'nowrap' }}
        status={status}
        readOnly={readOnly}
        disabled={disabled}
      >
        <StyledInput
          data-testid={testIds.control}
          {...{ onChange: onInputChange, id, readOnly, required, disabled, status, ...restProps }}
          inputMode='numeric'
          ref={inputRef}
          value={formattedValue}
          onKeyDown={onKeyDown}
          onFocus={onInputFocusEvent}
          onBlur={onInputFocusEvent}
          aria-label={`${ariaLabel || label}. ${unit ? t('measured_in', [unit]) : ''}`}
          maxLength={16}
          autoFocus={autoFocus}
          aria-describedby={info ? `${id}-info` : undefined}
        />
      </Flex>
    );

    const onMinusClick = () => {
      onChange?.(getDecrementedValue(value, min, max, step));
    };

    const onPlusClick = () => {
      onChange?.(getIncrementedValue(value, min, max, step));
    };

    const stepper = (
      <Flex
        as={StyledStepperInput}
        hasSuggestion={status === 'pending' && !!onResolveSuggestion}
        container={{ alignItems: 'center', wrap: 'nowrap' }}
        status={status}
        readOnly={readOnly}
        disabled={disabled}
        size={formattedValue?.length}
      >
        <Button
          data-testid={testIds.minus}
          variant='simple'
          icon
          onClick={onMinusClick}
          readOnly={readOnly}
          disabled={disabled}
          tabIndex={-1}
          aria-hidden='true'
        >
          <Icon name='minus' />
        </Button>
        <Flex
          container={{ alignItems: 'center', wrap: 'nowrap', justify: 'center' }}
          item={{ grow: 1 }}
          ref={containerRef}
          onClick={(e: MouseEvent) => {
            if (e.target === containerRef.current) inputRef?.current?.focus();
          }}
        >
          <StyledInput
            data-testid={testIds.control}
            {...{ onChange: onInputChange, id, readOnly, required, disabled, status, ...restProps }}
            inputMode='numeric'
            ref={inputRef}
            value={formattedValue}
            onKeyDown={onKeyDown}
            onFocus={onInputFocusEvent}
            onBlur={onInputFocusEvent}
            aria-label={`${ariaLabel || label}. ${unit ? t('measured_in', [unit]) : ''}`}
            maxLength={16}
            autoFocus={autoFocus}
            aria-describedby={`${id}-info`}
          />
        </Flex>
        <Button
          data-testid={testIds.plus}
          variant='simple'
          icon
          onClick={onPlusClick}
          readOnly={readOnly}
          disabled={disabled}
          tabIndex={-1}
          aria-hidden='true'
        >
          <Icon name='plus' />
        </Button>
      </Flex>
    );

    const Comp = variant === 'stepper' && !readOnly ? stepper : numberInput;

    return label ? (
      <FormField
        {...{
          testId: testIds,
          label,
          labelHidden,
          id,
          info,
          status,
          required,
          readOnly,
          disabled,
          additionalInfo,
          onResolveSuggestion
        }}
      >
        {Comp}
      </FormField>
    ) : (
      Comp
    );
  }
);

export default NumberInput;
