import { forwardRef, FunctionComponent, PropsWithoutRef, Ref } from 'react';

import { useConfiguration } from '../../hooks';
import { BaseProps, ForwardProps } from '../../types';
import Text from '../Text';

import { formatDuration } from './utils';

export interface DurationDisplayProps extends BaseProps {
  /** Number of milliseconds value for controlled input. */
  value?: number;
  /** Determines number of units to be displayed.
   * @default 4
   */
  significantUnits?: 4 | 3 | 2 | 1;
  /** Ref for the wrapping element. */
  ref?: Ref<HTMLSpanElement>;
}

const DurationDisplay: FunctionComponent<DurationDisplayProps & ForwardProps> = forwardRef(
  (
    { value, significantUnits = 4, ...restProps }: PropsWithoutRef<DurationDisplayProps>,
    ref: DurationDisplayProps['ref']
  ) => {
    const { locale } = useConfiguration();

    return (
      <Text ref={ref} {...restProps}>
        {typeof value === 'number' ? (
          formatDuration(value, { locale, significantUnits })
        ) : (
          <span aria-hidden='true'>&ndash;&ndash;</span>
        )}
      </Text>
    );
  }
);

export default DurationDisplay;
